void clicked()
{
    Args    args;
    FormRun formRun;

    args = new Args(formStr(InventTransRegister));
    args.caller(element);
    args.record(purchParmLine);

    formRun = classfactory.formRunClass(args);
    formRun.init();
    formRun.run();
    formRun.wait(true);

    // Skip to update receive now qty for purchParmLine when processing packing slip correction
    if (purchFormLetter.parmVersioningUpdateType() != VersioningUpdateType::Correction)
    {
        element.updateRegisteredLineQuantities();
        purchParmLine.modifiedReceiveNow();
    }
}

