Method.Source:
    [Form]
    public class PurchFormLetterParameters extends FormRun
    {
        boolean inventDimParmAllowCreatePurchaseOrder;
        boolean inventDimParmAllowCreatePurchaseOrderConfirmationRequest;
        boolean inventDimParmAllowCreatePackingSlip;
        boolean inventDimParmAllowCreateReceiptsList;
        boolean inventDimParmAllowCreateRFQ;
        boolean inventDimParmAllowCreatePurchReq;
        boolean inventDimParmAllowCreateAgreementConfirm;

}

public void init()
{
    List            inventDimAgreementFieldList = InventDim::dimAgreementFieldList();
    ListEnumerator  fieldListEnumerator = inventDimAgreementFieldList.getEnumerator();
    fieldId         fieldId;

    inventDimParmAllowCreatePurchaseOrder    = true;
    inventDimParmAllowCreatePurchaseOrderConfirmationRequest = true;
    inventDimParmAllowCreatePackingSlip      = true;
    inventDimParmAllowCreateReceiptsList     = true;
    inventDimParmAllowCreateRFQ              = true;
    inventDimParmAllowCreatePurchReq         = true;
    inventDimParmAllowCreateAgreementConfirm = true;

    super();

    VendFormletterParameters::find();
    VendFormletterDocument::find();

    while (fieldListEnumerator.moveNext())
    {
        fieldId = fieldListEnumerator.current();
        inventDimParmAgreementConfirm_DS.object(InventDim::dim2dimParm(fieldId)).enabled(true);
    }
}

public void run()
{
    super();

    inventDimParmPurchaseOrder_DS.create();
    InventDimFixedClass::inventDimFixed2InventDimParm(vendFormletterParameters.InventDimPurchaseOrder,inventDimParmPurchaseOrder);
    inventDimParmPurchaseOrder.write();

    InventDimParmPurchOrderCR_DS.create();
    InventDimFixedClass::inventDimFixed2InventDimParm(vendFormletterParameters.InventDimPurchOrderConfirmationRequest,InventDimParmPurchOrderCR);
    InventDimParmPurchOrderCR.write();

    inventDimParmPackingslip_DS.create();
    InventDimFixedClass::inventDimFixed2InventDimParm(vendFormletterParameters.InventDimPackingSlip,inventDimParmPackingslip);
    inventDimParmPackingslip.write();

    inventDimParmReceiptslist_DS.create();
    InventDimFixedClass::inventDimFixed2InventDimParm(vendFormletterParameters.InventDimReceiptsList,inventDimParmReceiptslist);
    inventDimParmReceiptslist.write();

    inventDimParmRFQ_DS.create();
    InventDimFixedClass::inventDimFixed2InventDimParm(vendFormletterParameters.InventDimRFQ,inventDimParmRFQ);
    inventDimParmRFQ.write();

    inventDimParmPurchReq_DS.create();
    InventDimFixedClass::inventDimFixed2InventDimParm(vendFormletterParameters.InventDimPurchReq,inventDimParmPurchReq);
    inventDimParmPurchReq.write();

    inventDimParmAgreementConfirm_DS.create();
    InventDimFixedClass::inventDimFixed2InventDimParm(vendFormletterParameters.InventDimPurchaseAgreement,inventDimParmAgreementConfirm);
    inventDimParmAgreementConfirm.write();
}

