void init()
{
    if (element.args() && !TradeFormHelper::isCalledFromForm(element.args(), formStr(PurchLineBackOrderListPage)))
    {
        element.args().copyCallerQuery(CopyCallerQuery::No);
    }

    super();

    backorderDateInventTrans = inventTrans_ds.query().dataSourceTable(tableNum(InventTrans)).addRange(fieldNum(InventTrans, DateExpected));

    inventTrans_ds.query().dataSourceTable(tableNum(InventTrans)).addSortField(fieldNum(InventTrans, DateExpected));
    inventTrans_ds.query().dataSourceTable(tableNum(InventTrans)).addRange(fieldNum(InventTrans, StatusReceipt)).value(strFmt('%1,%2',enum2str(StatusReceipt::Ordered),enum2str(StatusReceipt::QuotationReceipt)));
    inventTrans_ds.query().dataSourceTable(tableNum(InventTrans)).addRange(fieldNum(InventTrans, StatusIssue)).value('0');

    inventTransOrigin_ds.query().dataSourceTable(tableNum(InventTransOrigin)).addRange(fieldNum(InventTransOrigin, ReferenceCategory)).value(queryValue(InventTransType::Purch));

    element.project();

    backorderDatePurchLine = purchLine1_ds.query().dataSourceTable(tableNum(PurchLineBackOrder)).addRange(fieldNum(PurchLineBackOrder, ConfirmedDlv));

    backOrderDate.dateValue(DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()));

    element.projLinkActive();

    element.updateDesign(InventDimFormDesignUpdate::Init);

    xSysLastValue::getLast(this);

    const int viewPurchaseOrderLine = 1;

    TransactionViewRadioButton.selection(viewPurchaseOrderLine);
    pdsCWShowZeroCWQty.value(false);

    if (transactionView == 0)
    {
        viewInventTrans = true;
        tabPurchLine.visible(false);
        tabInventTrans.visible(true);
    }
    else
    {
        viewInventTrans = false;
        tabPurchLine.visible(true);
        tabInventTrans.visible(false);
    }
}

