void project()
{
    if (element.mustSkipProjectFormControlUpdate())
    {
        return;
    }

    if (! element.args().record()) // called from menu
    {
        inventTrans_ds.query().dataSourceTable(tableNum(InventTrans)).addRange(fieldNum(InventTrans, ProjId)).value(SysQuery::valueNotEmptyString());

        QueryBuildRange rangeProjId;
        rangeProjId = SysQuery::findOrCreateRange(purchLine1_ds.query().dataSourceTable(tableNum(PurchLineBackOrder)), fieldNum(PurchLineBackOrder, ProjId));
        rangeProjId.value(SysQuery::valueNotEmptyString());
    }

    purchLine1_ProjId.visible(true);
    transactionViewRadioButton.selection(1);
}

/// <summary>
/// Determines whether to update the form controls.
/// </summary>
/// <returns>
/// true if the update to form controls must be skipped; otherwise, false.
/// </returns>
protected boolean mustSkipProjectFormControlUpdate()
{
    return !element.args().parmEnum();
}

void projLinkActive()
{
    ProjTable           projTable;
    ProjInvoiceTable    projInvoiceTable;
    ProjWIPTable        projWIPTable;
    QueryBuildRange     rangeInventTransProjId, rangePurchLineProjId;

    if (element.args().dataset() == tableNum(ProjTable))
    {
        projTable   = element.args().record();

        inventTrans_ds.query().dataSourceTable(tableNum(InventTrans)).clearDynalinks();
        rangeInventTransProjId = SysQuery::findOrCreateRange(inventTrans_ds.query().dataSourceTable(tableNum(InventTrans)), fieldNum(InventTrans, ProjId));

        purchLine1_ds.query().dataSourceTable(tableNum(PurchLineBackOrder)).clearDynalinks();
        rangePurchLineProjId = SysQuery::findOrCreateRange(purchLine1_ds.query().dataSourceTable(tableNum(PurchLineBackOrder)), fieldNum(PurchLineBackOrder, ProjId));

        rangeInventTransProjId.value(projTable.ProjId+'*');
        rangePurchLineProjId.value(projTable.ProjId+'*');
    }
    else if (element.args().dataset() == tableNum(ProjInvoiceTable))
    {
        projInvoiceTable   = element.args().record();

        purchLine1_ds.query().dataSourceTable(tableNum(PurchLineBackOrder)).clearRange(fieldNum(PurchLineBackOrder,ProjId));
        inventTrans_ds.query().dataSourceTable(tableNum(InventTrans)).clearRange(fieldNum(InventTrans,ProjId));

        rangePurchLineProjId     = purchLine1_ds.query().dataSourceTable(tableNum(PurchLineBackOrder)).findRange(fieldNum(PurchLineBackOrder,ProjId));
        rangeInventTransProjId   = inventTrans_ds.query().dataSourceTable(tableNum(InventTrans)).findRange(fieldNum(InventTrans,ProjId));

        if (!rangePurchLineProjId || !rangeInventTransProjId)
        {
            while select ProjId from projTable
                where projTable.ProjInvoiceProjId   == projInvoiceTable.ProjInvoiceProjId
            {
                if (!rangePurchLineProjId)
                {
                    purchLine1_ds.query().dataSourceTable(tableNum(PurchLineBackOrder)).addRange(fieldNum(PurchLineBackOrder,ProjId)).value(projTable.ProjId);
                }
                if (!rangeInventTransProjId)
                {
                    inventTrans_ds.query().dataSourceTable(tableNum(InventTrans)).addRange(fieldNum(InventTrans,ProjId)).value(projTable.ProjId);
                }
            }
        }
    }
    else if (element.args().dataset() == tableNum(ProjWIPTable))
    {
        projWIPTable   = element.args().record();

        purchLine1_ds.query().dataSourceTable(tableNum(PurchLineBackOrder)).clearRange(fieldNum(PurchLineBackOrder,ProjId));
        inventTrans_ds.query().dataSourceTable(tableNum(InventTrans)).clearRange(fieldNum(InventTrans,ProjId));

        rangePurchLineProjId     = purchLine1_ds.query().dataSourceTable(tableNum(PurchLineBackOrder)).findRange(fieldNum(PurchLineBackOrder,ProjId));
        rangeInventTransProjId   = inventTrans_ds.query().dataSourceTable(tableNum(InventTrans)).findRange(fieldNum(InventTrans,ProjId));

        if (!rangePurchLineProjId || !rangeInventTransProjId)
        {
            while select ProjId from projTable
                where projTable.wipProject   == projWIPTable.ProjId
            {
                if (!rangePurchLineProjId)
                {
                    purchLine1_ds.query().dataSourceTable(tableNum(PurchLineBackOrder)).addRange(fieldNum(PurchLineBackOrder,ProjId)).value(projTable.ProjId);
                }
                if (!rangeInventTransProjId)
                {
                    inventTrans_ds.query().dataSourceTable(tableNum(InventTrans)).addRange(fieldNum(InventTrans,ProjId)).value(projTable.ProjId);
                }
            }
        }
    }
}

public void run()
{
    super();

    if (transactionViewRadioButton.selection() == 0)
    {
        inventTransOrigin_ds.executeQuery();
    }
    else
    {
        purchLine1_ds.executeQuery();
    }
}

public boolean  unpack(container  packedClass)
{
    boolean         ret;
    Integer         version    = conPeek(packedClass,1);

    switch (version)
    {
        case CurrentVersion:
            [version, #CurrentList] = packedClass;
            transactionViewRadioButton.selection(transactionView);
            linkViewCheckBox.value(linkView);
            ret = true;
            break;
        default:
            ret = false;
    }
    return ret;
}

void updateDesign(InventDimFormDesignUpdate mode)
{
    InventMovement movement = InventMovement::construct(purchLine);

    switch (mode)
    {
        case InventDimFormDesignUpdate::Init :
            if (!inventDimFormSetup)
            {
                inventDimFormSetup  = InventDimCtrl_Frm_Mov::newFromFormMovement(element, movement);
            }

        case InventDimFormDesignUpdate::Active :
            inventDimFormSetup.formActiveSetup(movement.inventDimGroupSetup(),movement);
            inventDimFormSetup.formSetControls(true);
            break;

        case InventDimFormDesignUpdate::FieldChange :
            inventDimFormSetup.formActiveSetup(movement.inventDimGroupSetup(),movement);
            inventDimFormSetup.formSetControls(false);
            break;

        default : throw error(strFmt("@SYS54195",funcName()));
    }
}

display Week week()
{
    return wkOfYr(backOrderDate.dateValue());
}

/// <summary>
/// This method either shows or hides line with CWQty == 0
/// </summary>
/// <param name = "_pdsCWQtyRange">QueryBuildRange</param>
/// <remarks>
/// If the Exclude zero CW quantities check box is checked, lines with CWQty = 0 are not included.
/// </remarks>
public void setPdsCWQtyRange(QueryBuildRange _pdsCWQtyRange)
{
    const str PdsNotZero = '!0';

    if (pdsCWShowZeroCWQty.value())
    {
        _pdsCWQtyRange.value(PdsNotZero);
    }
    else
    {
        _pdsCWQtyRange.value("");
    }
}

public void init()
{
    QueryBuildRange rangeIsDeleted;
    VendTable vendTable;

    super();

    if (TradeFormHelper::isCalledFromForm(element.args(), formStr(VendTable))
    ||  TradeFormHelper::isCalledFromForm(element.args(), formStr(VendTableListPage)))
    {
        vendTable = element.args().record() as VendTable;
        purchline_ds.query().dataSourceTable(tableNum(PurchLine)).addRange(fieldNum(PurchLine, VendAccount)).value(vendTable.AccountNum);
    }

    rangeIsDeleted = this.query().dataSourceTable(tableNum(PurchLine)).addRange(fieldNum(PurchLine,IsDeleted));
    rangeIsDeleted.value(SysQuery::value(NoYes::No));
    rangeIsDeleted.status(RangeStatus::Hidden);
}

public void linkActive()
{
    element.projLinkActive();

    super();
}

void executeQuery()
{
    QueryBuildRange pdsCWQtyRange;

    backorderDateInventTrans.value(strfmt('..%1',backOrderDate.dateValue()));

    if (#PdsCatchWeightEnabled)
    {
        pdsCWQtyRange = this.query().
            dataSourceTable(
                tablenum(InventTrans)).
                    findRange(fieldnum(InventTrans,PdsCWQty));

        if (! pdsCWQtyRange)
        {
            pdsCWQtyRange = this.query().
                dataSourceTable(
                    tablenum(InventTrans)).
                        addRange(fieldnum(InventTrans,PdsCWQty));
        }

        element.setPdsCWQtyRange(pdsCWQtyRange);
    }

    super();
}

