{
    QueryBuildDataSource qbds;
    super();
    qbds = this.queryBuildDataSource();
    qbds.addLink(fieldNum(PurchLineBackOrder,InventDimId),fieldNum(InventDim,InventDimId));
}

void executeQuery()
{
    backorderDateInventTrans.value(strFmt('..%1',backOrderDate.dateValue()));
    super();

    inventTransOriginQueryExecuted = true;
}

public void init()
{
    VendTable   vendTable;

    super();

    if (TradeFormHelper::isCalledFromForm(element.args(), formStr(VendTable))
    ||  TradeFormHelper::isCalledFromForm(element.args(), formStr(VendTableListPage)))
    {
        vendTable = element.args().record() as VendTable;
        purchline1_ds.query().dataSourceName('PurchLine1').addRange(fieldNum(PurchLineBackOrder, VendAccount)).value(vendTable.AccountNum);
    }

    this.cacheAddMethod(tablemethodStr(PurchLineBackOrder,pdsCWUnitId));
}

void executeQuery()
{
    QueryBuildRange pdsCWQtyRange;

    backorderDatePurchLine.value(strFmt('..%1',backOrderDate.dateValue()));
    if (#PdsCatchWeightEnabled)
    {
        pdsCWQtyRange = SysQuery::findOrCreateRange(PurchLine1_DS.queryBuildDataSource(), fieldnum(PurchLineBackOrder,PdsCWQty));

        element.setPdsCWQtyRange(pdsCWQtyRange);
    }

    super();

    purchLine1QueryExecuted = true;
}

