boolean modified()
{
    boolean ret = super();

    if (transactionViewRadioButton.selection() == 0)
    {
        inventTransOrigin_ds.executeQuery();
        purchLine1QueryExecuted = false;
    }
    else
    {
        purchLine1_ds.executeQuery();
        inventTransOriginQueryExecuted = false;
    }

    return ret;
}

public int selectionChange()
{
    int ret = super();

    TradeInventTransId  inventTransId;

    pdsCWShowZeroCWQty.value(false);

    if (this.selection() == 1)
    {
        pdsCWShowZeroCWQty.enabled(true);

        if (!purchLine1QueryExecuted)
        {
            purchLine1_ds.executeQuery();
        }
        viewInventTrans = false;
        tabPurchLine.visible(true);
        tabInventTrans.visible(false);
        if (linkViewCheckBox.value())
        {
            inventTransId = inventTransOrigin.InventTransId;
            PurchLine p = PurchLine::findInventTransId(inventTransId);
            purchLine1_ds.findRecord(p);
        }
    }
    else
    {
        pdsCWShowZeroCWQty.enabled(false);

        if (!inventTransOriginQueryExecuted)
        {
            inventTransOrigin_ds.executeQuery();
        }
        viewInventTrans = true;
        tabPurchLine.visible(false);
        tabInventTrans.visible(true);
        if (linkViewCheckBox.value())
        {
            inventTransId = purchLine1.InventTransId;
            InventTrans i = InventTrans::findByInventTransOrigin(InventTransOriginPurchLine::findInventTransOriginId(purchLine1.DataAreaId,purchLine1.InventTransId));
            inventTrans_ds.positionToRecord(i);
        }
    }

    return ret;
}

