/// <summary>
/// Creates and initializes the purchase copying contract object.
/// </summary>
/// <param name = "_purchTable">A <c>PurchTable</c> record to initialize from.</param>
/// <returns>An instance of <c>PurchCopyingContract</c>.</returns>
protected PurchCopyingContract createPurchCopyingContract(PurchTable _purchTable)
{
    PurchCopyingContract purchCopyingContract = PurchCopyingContract::newContract(_purchTable);

    this.initializePurchCopyingContract(purchCopyingContract, _purchTable);

    return purchCopyingContract;
}

/// <summary>
/// Initializes the <c>PurchCopyingContract</c>.
/// </summary>
/// <param name = "_purchCopyingContract">A <c>PurchCopyingContract</c> instance to initialize.</param>
/// <param name = "_purchTable">A <c>PurchTable</c> record to initialize from.</param>
protected void initializePurchCopyingContract(PurchCopyingContract _purchCopyingContract, PurchTable _purchTable)
{
    NoYes   recalculateAmount;
    Qty     qtyFactor;

    switch (copyItemQtyPrice.selection())
    {
        case ItemQtyPrice::Item :
            recalculateAmount   = NoYes::Yes;
            qtyFactor           = 0;
            break;

        case ItemQtyPrice::ItemQty :
            recalculateAmount   = NoYes::Yes;
            qtyFactor           = 1;
            break;

        case ItemQtyPrice::ItemQtyPrice :
            recalculateAmount   = NoYes::No;
            qtyFactor           = 1;
            break;
    }

    List    lineList = new List(Types::Record);
    while select tmpFrmVirtualLines
    {
        lineList.addEnd(tmpFrmVirtualLines);
    }

    _purchCopyingContract.parmCallingTable(_purchTable);
    _purchCopyingContract.parmPackedTmpFrmVirtualHeader(new List(Types::Record).pack());
    _purchCopyingContract.parmPackedTmpFrmVirtualLines(lineList.pack());
    _purchCopyingContract.parmQtyFactor(qtyFactor);
    _purchCopyingContract.parmReverseSign(NoYes::No);
    _purchCopyingContract.parmRecalculateAmount(recalculateAmount);
    _purchCopyingContract.parmHeaderCurrencyCode(_purchTable.CurrencyCode);
    _purchCopyingContract.parmCopyPrecisely(NoYes::Yes);
}

boolean canClose()
{
    boolean canClose = super();

    try
    {
        if (element.closedOk() && canClose)
        {
            PurchTable purchTable = element.args().caller().purchTable();

            if (callerDataSource.allowEdit() && purchTable.checkUpdate())
            {
                PurchCopyingContract purchCopyingContract = this.createPurchCopyingContract(purchTable);

                PurchCopying::newCopy(SalesPurchCopy::CopyAllLines, purchCopyingContract);

                this.updateCaller();
            }
        }
    }
    catch
    {
        canClose = false;
    }

    return canClose;
}

NoYes  editCopy(boolean             _set,
                RefTableId          _tableId,
                RefRecId            _recId,
                Num                 _id,
                LineNum             _lineNum,
                TransDate           _transDate,
                Qty                 _qty,
                NoYes               _copy
                , PdsCWInventQty    _pdsCWQty = 0.00
                )
{
    NoYes  copy;

    select tmpFrmVirtualLines
        where tmpFrmVirtualLines.TableNum    == _tableId &&
              tmpFrmVirtualLines.RecordNo    == _recId;

    if (tmpFrmVirtualLines)
    {
        copy   = NoYes::Yes;
    }

    if (_set)
    {
        if (_copy)
        {
            tmpFrmVirtualLines.TableNum      = _tableId;
            tmpFrmVirtualLines.RecordNo      = _recId;
            tmpFrmVirtualLines.LineNum       = _lineNum;
            tmpFrmVirtualLines.Id            = _id;
            tmpFrmVirtualLines.TransDate     = _transDate;
            tmpFrmVirtualLines.Qty           = _qty;

            tmpFrmVirtualLines.PdsCWQty      = _pdsCWQty;

            tmpFrmVirtualLines.write();
            copy   = NoYes::Yes;
        }
        else
        {
            tmpFrmVirtualLines.delete();
            copy   = NoYes::No;
        }
    }

    return copy;
}

void  init()
{
    //<GEERU>
    #ISOCountryRegionCodes
    //</GEERU>

    // <GEERU>
    if (! element.args().record() && ! SysCountryRegionCode::isLegalEntityInCountryRegion([ #isoCZ, #isoEE, #isoHU, #isoLT, #isoLV, #isoPL, #isoRU ]))
    // </GEERU>
    {
        throw error("@SYS22539");
    }

    super();

    element.updateDesign(InventDimFormDesignUpdate::Init);

    copyItemQtyPrice.selection(ItemQtyPrice::ItemQty);

    callerPurchLine     = element.args().record();
    callerDataSource    = FormDataUtil::getFormDataSource(callerPurchLine);
}

Object inventDimSetupObject()
{
    return inventDimFormSetup;
}

protected void updateCaller()
{
    PurchTable  purchTable = element.args().caller().purchTable();
    Object callerPurchTableDataSource = FormDataUtil::getFormDataSource(purchTable);

    if (callerPurchTableDataSource)
    {
        callerPurchTableDataSource.reread();
        callerPurchTableDataSource.refresh();

        if (formDataSourceHasMethod(callerPurchTableDataSource, identifierStr(reReadLines)))
        {
            callerPurchTableDataSource.reReadLines();
        }
    }
}

void updateDesign(InventDimFormDesignUpdate mode)
{
    InventMovement movement = InventMovement::construct(purchLine);

    switch (mode)
    {
        case InventDimFormDesignUpdate::Init :
            if (!inventDimFormSetup)
            {
                inventDimFormSetup  = InventDimCtrl_Frm_Mov::newFromFormMovement(element, movement);
            }

        case InventDimFormDesignUpdate::Active :
            inventDimFormSetup.formActiveSetup(movement.inventDimGroupSetup(),movement);
            inventDimFormSetup.formSetControls(true);
            break;

        case InventDimFormDesignUpdate::FieldChange :
            inventDimFormSetup.formActiveSetup(movement.inventDimGroupSetup(),movement);
            inventDimFormSetup.formSetControls(false);
            break;

        default : throw error(strFmt("@SYS54195",funcName()));
    }
}

