    // Do not call super()
}

void init()
{
    QueryBuildDataSource queryBuildDataSource;

    super();

    queryBuildDataSource = this.query().dataSourceTable(this.table());
    queryBuildDataSource.clearDynalinks();
    rangeVendInvoiceTrans_ItemId              = queryBuildDataSource.addRange(fieldNum(VendInvoiceTrans, ItemId));
    rangeVendInvoiceTrans_ProcurementCategory = queryBuildDataSource.addRange(fieldNum(VendInvoiceTrans, ProcurementCategory));
    rangeVendInvoiceTrans_InvoiceId           = queryBuildDataSource.addRange(fieldNum(VendInvoiceTrans, InvoiceId));

    this.cacheAddMethod(tableMethodStr(VendInvoiceTrans,pdsCWUnitId));
}

void executeQuery()
{
    PurchTable  purchTable = element.args().caller().purchTable();

    if (callerPurchLine.isCategoryBased())
    {
        rangeVendInvoiceTrans_ItemId.value(SysQuery::valueEmptyString());
        rangeVendInvoiceTrans_ProcurementCategory.enabled(true);
        rangeVendInvoiceTrans_ProcurementCategory.value(queryValue(callerPurchLine.ProcurementCategory));
    }
    else
    {
        rangeVendInvoiceTrans_ProcurementCategory.enabled(false);
        rangeVendInvoiceTrans_ItemId.value(queryValue(callerPurchLine.ItemId));
    }

    rangeVendInvoiceJour_OrderAccount.value(queryValue(purchTable.OrderAccount));
    rangeVendInvoiceTrans_InvoiceId.value(SysQuery::valueNotEmptyString());

    this.query().dataSourceTable(tableNum(VendInvoiceTrans)).addSortField(fieldNum(VendInvoiceTrans, InvoiceId));
    this.query().dataSourceTable(tableNum(VendInvoiceTrans)).addSortField(fieldNum(VendInvoiceTrans, LineNum));
    this.query().dataSourceTable(tableNum(VendInvoiceTrans)).addSortField(fieldNum(VendInvoiceTrans, ItemId));

    super();
}

int active()
{
    int             ret;

    ret = super();

    vendInvoiceTrans_ds.allowEdit    (callerDataSource.allowEdit());

    return ret;
}

