void init()
{
    QueryBuildRange      rangeIsDeleted;
    QueryBuildDataSource queryBuildDataSource;

    super();

    queryBuildDataSource = this.query().dataSourceTable(this.table());
    queryBuildDataSource.clearDynalinks();
    rangePurchLine_VendAccount  = queryBuildDataSource.addRange(fieldNum(PurchLine, VendAccount));
    rangePurchLine_PurchStatus  = queryBuildDataSource.addRange(fieldNum(PurchLine, PurchStatus));
    rangePurchLine_ItemId       = queryBuildDataSource.addRange(fieldNum(PurchLine, ItemId));
    rangePurchLine_ProcurementCategory = queryBuildDataSource.addRange(fieldNum(PurchLine, ProcurementCategory));

    rangeIsDeleted = queryBuildDataSource.addRange(fieldNum(PurchLine,IsDeleted));
    rangeIsDeleted.value(SysQuery::value(NoYes::No));
    rangeIsDeleted.status(RangeStatus::Hidden);

    this.cacheAddMethod(tableMethodStr(PurchLine,pdsCWUnitId));
}

void executeQuery()
{
    PurchTable      purchTable = element.args().caller().purchTable();
    InventDim       inventDim;
    InventDimParm   inventDimParm;

    rangePurchLine_PurchStatus.value(strFmt('%1,%2',enum2Value(PurchStatus::Backorder),enum2Value(PurchStatus::Received)));

    if (callerPurchLine.isCategoryBased())
    {
        rangePurchLine_ItemId.value(SysQuery::valueEmptyString());
        rangePurchLine_ProcurementCategory.enabled(true);
        rangePurchLine_ProcurementCategory.value(queryValue(callerPurchLine.ProcurementCategory));
    }
    else
    {
        rangePurchLine_ProcurementCategory.enabled(false);
        rangePurchLine_ItemId.value(queryValue(callerPurchLine.ItemId));

        inventDim = callerPurchLine.inventDim();
        inventDimParm = EcoResProductDimGroupSetup::newItemId(callerPurchLine.ItemId).inventDimParmActiveDimensions();
        inventDimParm.queryAddRangeValue(this.query().dataSourceName(identifierStr(InventDimPurchLine)),inventDim);
    }

    rangePurchLine_VendAccount.value(queryValue(purchTable.OrderAccount));

    this.query().dataSourceTable(tableNum(PurchLine)).addSortField(fieldNum(PurchLine, PurchId));
    this.query().dataSourceTable(tableNum(PurchLine)).addSortField(fieldNum(PurchLine, LineNumber));
    this.query().dataSourceTable(tableNum(PurchLine)).addSortField(fieldNum(PurchLine, ItemId));

    super();
}

int active()
{
    int             ret;

    ret = super();

    purchLine_ds.allowEdit    (callerDataSource.allowEdit());

    return ret;
}

