Method.Source:
[Form]
public class PurchLineMatchingPolicy extends FormRun
{
    Common originator;

    int vendItemIndex;
    int itemIndex;
    int vendIndex;

    boolean vendItemGridActive;
    boolean itemGridActive;
    boolean vendGridActive;

    boolean vendParametersExecuteQueryPerformed;

}

public void init()
{
    VendParameters parameters;
    Query query;
    QueryBuildDataSource qbdsVendParameters;

    parameters = VendParameters::find();
    if (parameters.editUseInvoiceMatching() == NoYes::No)
    {
        // Error when invoice matching is not set
        error("@SYS114452");
        this.close();
    }

    super();

    if (element.args().dataset() == tableNum(VendTable))
    {
        matchingPolicyLevel.clear();
        matchingPolicyLevel.add("@SYS8079"); // vendor
        matchingPolicyLevel.add("@SYS130459"); // item and vendor
        vendIndex = 0;
        vendItemIndex = 1;
        itemIndex = 2;

        vendItemGridActive = false;
        itemGridActive = false;
        vendGridActive = true;
        vendItemGrid_VendId.allowEdit(false);
        vendItemGrid_ItemId.allowEdit(true);
        // there is a dynalink from VendParameters to VendTable that not needed.
        query = vendParameters_ds.query();
        qbdsVendParameters = query.dataSourceTable(tableNum(VendParameters));
        qbdsVendParameters.clearDynalinks();
    }
    else if (element.args().dataset() == tableNum(InventTable))
    {
        matchingPolicyLevel.clear();
        matchingPolicyLevel.add("@SYS7407"); // item
        matchingPolicyLevel.add("@SYS130459"); // item and vendor
        itemIndex = 0;
        vendItemIndex = 1;
        vendIndex = 2;

        vendItemGridActive = false;
        itemGridActive = true;
        vendGridActive = false;
        vendItemGrid_VendId.allowEdit(true);
        vendItemGrid_ItemId.allowEdit(false);
    }
    else
    {
        matchingPolicyLevel.clear();
        matchingPolicyLevel.add("@SYS130459"); // item and vendor
        matchingPolicyLevel.add("@SYS7407"); // item
        matchingPolicyLevel.add("@SYS8079"); // vendor
        vendItemIndex = 0;
        itemIndex = 1;
        vendIndex = 2;

        vendItemGridActive = true;
        itemGridActive = false;
        vendGridActive = false;
        vendItemGrid_VendId.allowEdit(true);
        vendItemGrid_ItemId.allowEdit(true);
    }
}

public int task(int _taskId)
{
    #task
    int ret;

    switch (_taskId)
    {
        case #taskPrint :
        case #taskPrintPreview :
            //Allows the user to print if the matching policy level is vendItem.
            if (matchingPolicyLevel.selection() != vendItemIndex)
            {
                ret = 0;
                break;
            }

        default :
            ret = super(_taskId);
    }

    return ret;
}

public void run()
{
    super();

    if (element.args().dataset() == tableNum(VendTable))
    {
        tabPageVendItem.visible(false);
        tabPageVend.visible(true);
        tabPageItem.visible(false);
    }
    else if (element.args().dataset() == tableNum(InventTable))
    {
        tabPageVendItem.visible(false);
        tabPageVend.visible(false);
        tabPageItem.visible(true);
    }
    else
    {
        // By default show the vend/item grid
        tabPageVendItem.visible(true);
        tabPageVend.visible(false);
        tabPageItem.visible(false);
    }
}

public void executeQuery()
{
    // Only execute query if grid is active
    if (itemGridActive == false)
    {
        return;
    }

    super();
}

public void executeQuery()
{
    // Only execute query if grid is active
    if (vendGridActive == false)
    {
        return;
    }

    super();
}

public void executeQuery()
{
    // Only execute query if grid is active
    if (vendItemGridActive == false)
    {
        return;
    }

    super();
}

public void executeQuery()
{
    if (vendParametersExecuteQueryPerformed == true)
    {
        return;
    }

    super();

    vendParametersExecuteQueryPerformed = true;
}

public boolean modified()
{
    boolean ret = super();
    int matchingPolicyLevelSelection = matchingPolicyLevel.selection();

    // handle case where user manually typed in incorrect value in this case set to index value 0
    if (matchingPolicyLevelSelection != vendItemIndex
        && matchingPolicyLevelSelection != itemIndex
        && matchingPolicyLevelSelection != vendIndex)
    {
        matchingPolicyLevelSelection = matchingPolicyLevel.selection(0);
    }

    switch (matchingPolicyLevelSelection)
    {
        case vendItemIndex:
            if (vendItemGridActive == true)
            {
                break;
            }
            tabPageVendItem.visible(true);
            tabPageItem.visible(false);
            tabPageVend.visible(false);

            vendItemGridActive = true;
            itemGridActive = false;
            vendGridActive = false;
            vendItemMatchingPolicy_ds.executeQuery();
            break;

        case itemIndex:
            if (itemGridActive == true)
            {
                break;
            }
            tabPageVendItem.visible(false);
            tabPageItem.visible(true);
            tabPageVend.visible(false);

            itemGridActive = true;
            vendGridActive = false;
            vendItemGridActive = false;
            inventTable_ds.executeQuery();
            break;

        case vendIndex:
            if (vendGridActive == true)
            {
                break;
            }
            tabPageVendItem.visible(false);
            tabPageItem.visible(false);
            tabPageVend.visible(true);

            vendGridActive = true;
            itemGridActive = false;
            vendItemGridActive = false;
            vendTable_ds.executeQuery();
            break;
        default:
            Debug::assert(false);
            break;
    }
    return ret;
}
