public int active()
{
    int ret;

    ret = super();

    if (ret)
    {
        // Need to change the form view depending on location on the tree if it is a root or not and
        // on the checkbox checked or not.
        if ((newCategoryItem) && (newCategoryItem.idx() == rootCategoryItem))
        {
            purchPolicyRuleCatThreshold_ds.object(fieldNum(PurchPolicyRuleCatThreshold_PSN, Active)).allowEdit(false);
            element.setFormElementState(false);
        }
        else  //not a root node
        {
            if (purchPolicyRuleCatThreshold_Active.checked())
            {
                purchPolicyRuleCatThreshold_ds.object(fieldNum(PurchPolicyRuleCatThreshold_PSN, Active)).allowEdit(true);
                element.setFormElementState(true);
            }
            else
            {
                purchPolicyRuleCatThreshold_ds.object(fieldNum(PurchPolicyRuleCatThreshold_PSN, Active)).allowEdit(true);
                element.setFormElementState(false);
            }
        }
    }

    return ret;
}

public boolean validate()
{
    boolean             ret;
    EcoResCategory      ecoResCategorySelected;
    EcoResCategoryName  categoryName;

    // Get the current selected category Info
    int                         currentItemid = categoryTree.getFirstSelected();
    FormTreeItem                currentCategoryItem = categoryTree.getItem(currentItemid);
    RefRecId                    ecoResSelectedCategoryId = currentCategoryItem.data();
    ecoResCategorySelected   =  EcoResCategory::find(ecoResSelectedCategoryId);

    ret = super();

    if (ret && purchPolicyRuleCatThreshold_Active.checked())
    {
        [ret, categoryName] = PurchPolicyRuleCatThresholdForm_PSN::noParentOrChildCategoryIsActive(ecoResCategorySelected, sysPolicyRule);

        // Error if an active category found
        if (!ret)
        {
            Box::stop(strFmt("@SPS1803", categoryName));
        }
    }

    return ret;
}

public void initValue()
{
    CategoryThreshold_PSN       categoryThreshold = 0;
    EcoResCategory              ecoResCategorySelected;

    int                     currentItemid = categoryTree.getFirstSelected();
    FormTreeItem            currentCategoryItem = categoryTree.getItem(currentItemid);
    EcoResCategoryId        ecoResSelectedCategoryId = currentCategoryItem.data();

    ecoResCategorySelected = EcoResCategory::find(ecoResSelectedCategoryId);

    // Traverse up the tree to root to find a default threshold
    if (ecoResCategorySelected)
    {
        while (ecoResCategorySelected.DefaultThreshold_PSN <= 0
            && ecoResCategorySelected.ParentCategory)
        {
            ecoResCategorySelected = EcoResCategory::find(ecoResCategorySelected.ParentCategory);
        }

        purchPolicyRuleCatThresholdDetail.ThresholdAmount = ecoResCategorySelected.DefaultThreshold_PSN;
    }

      super();
}

public void executeQuery()
{
    QueryBuildDataSource    queryBuildDataSource;

    purchPolicyRuleCatThresholdDetail_ds.query().validTimeStateDateTimeRange(DateTimeUtil::minValue(),DateTimeUtil::maxValue());
    queryBuildDataSource = purchPolicyRuleCatThresholdDetail_ds.query().dataSourceTable(tableNum(PurchPolicyRuleCatThresholdDetail_PSN));
    queryBuildDataSource.addRange(fieldNum(PurchPolicyRuleCatThresholdDetail_PSN, PurchPolicyRuleCatThreshold_PSN)).value(int642str(purchPolicyRuleCatThreshold.RecId));

    super();
}

