private void pdsCWEnableControls(
        boolean         _isCWItem,
        boolean         _isProductMaster,
        FormRealControl _inventQtyControl,
        FormRealControl _cwQtyControl,
        FormRealControl _variantQtyControl,
        FormRealControl _variantCwQtyControl
    )
{
    // variants
    _variantQtyControl.allowEdit(_isProductMaster   && !_isCWItem);
    _variantCwQtyControl.allowEdit(_isProductMaster &&  _isCWItem);

    // distinct
    _inventQtyControl.allowEdit(!_isProductMaster   && !_isCWItem);
    _cwQtyControl.allowEdit(!_isProductMaster       &&  _isCWItem);
}

