/// <summary>
/// Determines the inventory dimension ID to be used with the <c>tmpFrmVirtual</c> record.
/// </summary>
/// <param name="_inventDimIdLine">The <c>InventDimId</c> that is used to determine the inventory dimension.</param>
/// <param name="_itemId">The ID of the Item.</param>
/// <param name="_copyProductDimensions">A Boolean value which determines if product dimensions would be copied from passed InventDim; optional.</param>
/// <param name="_existingItemsTab">A Boolean value which determines if request is coming from Existing Items tab on form; optional.</param>
/// <returns>
/// A inventory dimension ID.
/// </returns>
public InventDimId tmpFrmVirtualInventDimId(InventDimId _inventDimIdLine, ItemId _itemId, boolean _copyProductDimensions = false, boolean _existingItemsTab = true)
{
    InventDimId             inventDimIdLocal;
    InventDim               invDim;
    InventDim               tmpInventDim;
    PurchTable              purchTable;
    InventItemPurchSetup    inventItemPurchSetupLocal;
    InventDimParm           inventDimParmLocal;
    InventDimGroupSetup     inventDimGroupSetup;
    // <GEERU>
    PurchTable_RU           purchTable_RU;
    // </GEERU>

    if (_existingItemsTab && inventorydimensions_InventLocationId.visible()) // If warehouse is visible on the form
    {
        inventDimIdLocal = _inventDimIdLine;
        if (   !inventoryDimensions_inventProfileId_RU.visible()
            && InventDimGroupSetup::newItemId(inventSum.ItemId).isDimensionActive(fieldNum(InventDim, InventProfileId_RU))
            && element.args().record().TableId == tableNum(PurchTable))
        {
            purchTable = element.args().record() as PurchTable;
            invDim     = InventDim::find(inventDimIdLocal);

            purchTable_RU = purchTable.purchTable_RU();
            if (purchTable_RU.InventProfileId_RU)
            {
                invDim.InventProfileId_RU = purchTable_RU.InventProfileId_RU;
            }

            invDim           = InventDim::findOrCreate(invDim);
            inventDimIdLocal = invDim.InventDimId;
        }
    }
    else
    {
        tmpInventDim        = InventDim::find(_inventDimIdLine);

        if (_copyProductDimensions)
        {
            inventDimGroupSetup = InventDimGroupSetup::newItemId(_itemId);
            inventDimParmLocal.initProductDimensionsAllGroups();
            invDim.copyDimParmDim(inventDimGroupSetup, tmpInventDim, inventDimParmLocal);
        }

        invDim.InventSiteId = tmpInventDim.InventSiteId;

        if (inventoryDimensions_InventProfileId_RU.visible())
        {
            invDim.InventProfileId_RU = tmpInventDim.InventProfileId_RU;
        }

        inventDimIdLocal = InventDim::findDim(invDim).InventDimId;

        if (element.args().record().TableId == tableNum(PurchTable))
        {
            purchTable = element.args().record() as PurchTable;
            //If site on header and on line are equal and warehouse is defined on header, use location from header
            if (purchTable.InventSiteId == invDim.InventSiteId && purchTable.InventLocationId)
            {
                invDim.InventLocationId = purchTable.InventLocationId;
            }
            else // Otherwise set default warehouse for this site
            {
                select firstonly InventDimId, InventDimIdDefault from inventItemPurchSetupLocal
                    where inventItemPurchSetupLocal.ItemId      == _itemId
                       && inventItemPurchSetupLocal.InventDimId == inventDimIdLocal;

                if (inventItemPurchSetupLocal.RecId)
                {
                    tmpInventDim            = InventDim::find(inventItemPurchSetupLocal.InventDimIdDefault);
                    invDim.InventLocationId = tmpInventDim.InventLocationId;
                }
            }

            if (   !invDim.InventProfileId_RU
                && InventDimGroupSetup::newItemId(inventSum.ItemId).isDimensionActive(fieldNum(InventDim, InventProfileId_RU)))
            {
                purchTable_RU = purchTable.purchTable_RU();
                if (purchTable_RU.InventProfileId_RU)
                {
                    invDim.InventProfileId_RU = purchTable_RU.InventProfileId_RU;
                }
            }
        }

        inventDimIdLocal = InventDim::findOrCreate(invDim).InventDimId;
    }

    return inventDimIdLocal;
}

public boolean unpack(container  packedClass)
{
    boolean         ret;
    Integer         version;

    if (typeOf(conPeek(packedClass,1)) != Types::Integer)
    {
        queryFilter = new QueryRun(packedClass);
        ret = true;
    }
    else
    {
        version = conPeek(packedClass,1);

        switch (version)
        {
            case CurrentVersion:
                [version, #CurrentList] = packedClass;
                queryFilter = new QueryRun(queryFilterValue);
                ret = true;
                break;
            default:
                ret = false;
        }
    }
    return ret;
}

void updateDesign(InventDimFormDesignUpdate mode)
{
    switch (mode)
    {
        case InventDimFormDesignUpdate::Init:
        case InventDimFormDesignUpdate::LinkActive:
            element.inventDimSetupObject().initFromCaller();
            inventDimParm = inventDimFormSetup.parmDimParmVisibleGrid().data();

        case InventDimFormDesignUpdate::Active:
            element.inventDimSetupObject().formActiveSetup();
            element.inventDimSetupObject().formSetControls(true,true);
            break;

        default:
            throw error(strFmt("@SYS54195",funcName()));
    }
}

