edit PurchQty  purchQty(
    boolean    set,
    InventSum _inventSum,
    PurchQty  _qty)
{
    InventDimId             inventDimIdLocal;

    inventDimParm     = inventDimFormSetup.parmDimParmVisibleGrid().data();
    inventDimIdLocal  = element.tmpFrmVirtualInventDimId(_inventSum.InventDimId, _inventSum.ItemId);
    inventDimCriteria = InventDim::find(inventDimIdLocal);

    select tmpFrmVirtual
        where   tmpFrmVirtual.TableNum      == _inventSum.TableId &&
                tmpFrmVirtual.ItemId        == _inventSum.ItemId &&
                tmpFrmVirtual.InventDimId   == inventDimIdLocal;

    if (set)
    {
        if (_qty)
        {
            tmpFrmVirtual.Qty = _qty;
        }
        else
        {
            tmpFrmVirtual.Qty = 0;
        }

        tmpFrmVirtual.TableNum      = _inventSum.TableId;
        tmpFrmVirtual.ItemId        = _inventSum.ItemId;
        tmpFrmVirtual.InventDimId   = inventDimIdLocal;
        tmpFrmVirtual.write();

        element.createInventDimLocal(inventDimCriteria);
    }

    select sum(Qty) from tmpFrmVirtual
        where   tmpFrmVirtual.TableNum      == _inventSum.TableId &&
                tmpFrmVirtual.ItemId        == _inventSum.ItemId
    #InventDimJoin(tmpFrmVirtual.inventDimId,inventDimLocal,inventDimCriteria,inventDimParm);

    return tmpFrmVirtual.Qty;
}

/// <summary>
/// Set and or returns the value of the PdsCWQty field
/// </summary>
/// <param name="_set">set value to field</param>
/// <param name="_inventSum">Current InventSum record</param>
/// <param name="_pdsCWQty">Current field value</param>
/// <returns>Current catch weight qty</returns>
edit PdsCWInventQty  pdsCWQty(
    boolean        _set,
    InventSum      _inventSum,
    PdsCWInventQty _pdsCWQty)
{
    InventDimId             inventDimIdLocal;
    PdsCWInventQty          pdsCWInventQty = 0;

    //Check if user has security access is not required as the user already has
    //access to form and therefore access to fields
    if (PdsGlobal::pdsIsCWItem(_inventSum.ItemId))
    {
        inventDimParm       = inventDimFormSetup.parmDimParmVisibleGrid().data();
        inventDimIdLocal    = element.tmpFrmVirtualInventDimId(_inventSum.InventDimId, _inventSum.ItemId);
        inventDimCriteria   = InventDim::find(inventDimIdLocal);

        select tmpFrmVirtual
            where  tmpFrmVirtual.TableNum      == _inventSum.TableId
                && tmpFrmVirtual.ItemId        == _inventSum.ItemId
                && tmpFrmVirtual.InventDimId   == inventDimIdLocal;

        if (_set)
        {
            if (_pdsCWQty)
            {
                tmpFrmVirtual.PdsCWQty = _pdsCWQty;
            }
            else
            {
                tmpFrmVirtual.PdsCWQty = 0;
            }

            tmpFrmVirtual.TableNum      = _inventSum.TableId;
            tmpFrmVirtual.ItemId        = _inventSum.ItemId;
            tmpFrmVirtual.InventDimId   = inventDimIdLocal;
            tmpFrmVirtual.write();

            element.createInventDimLocal(inventDimCriteria);
        }

        select sum(PdsCWQty) from tmpFrmVirtual
            where   tmpFrmVirtual.TableNum      == _inventSum.TableId &&
                    tmpFrmVirtual.ItemId        == _inventSum.ItemId
        #InventDimJoin(tmpFrmVirtual.inventDimId,inventDimLocal,inventDimCriteria,inventDimParm);

        pdsCWInventQty = tmpFrmVirtual.PdsCWQty;
    }

    return pdsCWInventQty;
}

