display InventQtyAvailPhysical availPhysical(InventDimCombination _inventDimCombination)
{
    InventQty       qtyAvailPhysical;
    InventDim       inventDimFind;

    inventDimFind.initFromInventDim(inventDim);
    EcoResProductDimGroupSetup::copyProductDimensionsForItem(_inventDimCombination.ItemId, _inventDimCombination.inventDim(), inventDimFind);

    inventDimParm       = inventDimFormSetup.parmDimParmVisibleGrid().data();
    inventDimParm.setActiveProductDim(EcoResProductDimGroupSetup::newItemId(_inventDimCombination.ItemId));

    qtyAvailPhysical = InventSum::findSumQty(inventDimCombination.ItemId, inventDimFind, inventDimParm).AvailPhysical;

    return qtyAvailPhysical;
}

edit PurchQty  purchQty(boolean               set,
                        InventDimCombination  _inventDimCombination,
                        PurchQty              _qty)
{
    InventDimId           inventDimIdLocal;
    InventDim             inventDimJoin;

    inventDimId         = element.findDimFromCombination(_inventDimCombination);
    inventDimParm       = inventDimFormSetup.parmDimParmVisibleGrid().data();
    inventDimIdLocal    = element.tmpFrmVirtualInventDimId(inventDimId, _inventDimCombination.ItemId, true);
    inventDimParm.setActiveProductDim(EcoResProductDimGroupSetup::newItemId(_inventDimCombination.ItemId));

    inventDimCriteria   = InventDim::find(inventDimId);

    select tmpFrmVirtual
        where   tmpFrmVirtual.TableNum      == tableNum(InventDimCombination)   &&
                tmpFrmVirtual.ItemId        == _inventDimCombination.ItemId     &&
                tmpFrmVirtual.InventDimId   == inventDimIdLocal;

    if (set)
    {
        if (_qty)
        {
            tmpFrmVirtual.Qty = _qty;
        }
        else
        {
            tmpFrmVirtual.Qty = 0;
        }

        tmpFrmVirtual.TableNum      = tableNum(InventDimCombination);
        tmpFrmVirtual.ItemId        = _inventDimCombination.ItemId;
        tmpFrmVirtual.InventDimId   = inventDimIdLocal;
        tmpFrmVirtual.write();
    }

    select sum(Qty) from tmpFrmVirtual
        where   tmpFrmVirtual.TableNum      == _inventDimCombination.TableId &&
                tmpFrmVirtual.ItemId        == _inventDimCombination.ItemId
    #InventDimJoin(tmpFrmVirtual.inventDimId, inventDimJoin, inventDimCriteria, inventDimParm);

    return tmpFrmVirtual.Qty;
}

/// <summary>
/// Return and or set catch weight quantity field
/// </summary>
/// <param name="_set">Set value to field</param>
/// <param name="_inventDimCombination">Current InventDimCombination record</param>
/// <param name="_pdsCWQty">Current Catch weight quantity</param>
/// <returns>Returns current value of PdsCWQty field</returns>
edit PdsCWInventQty  pdsCWQty(boolean               _set,
                              InventDimCombination  _inventDimCombination,
                              PdsCWInventQty        _pdsCWQty)
{
    InventDimId           inventDimIdLocal;
    PdsCWInventQty        pdsCWInventQty = 0;

    //Check if user has security access is not required as the user already has
    //access to form and therefore access to fields
    if (PdsGlobal::pdsIsCWItem(_inventDimCombination.ItemId))
    {
        inventDimId         = element.findDimFromCombination(_inventDimCombination);
        inventDimParm       = inventDimFormSetup.parmDimParmVisibleGrid().data();
        inventDimIdLocal    = element.tmpFrmVirtualInventDimId(inventDimId, _inventDimCombination.ItemId, true);
        inventDimParm.setActiveProductDim(EcoResProductDimGroupSetup::newItemId(_inventDimCombination.ItemId));

        inventDimCriteria   = InventDim::find(inventDimId);

        select tmpFrmVirtual
            where   tmpFrmVirtual.TableNum      == tableNum(InventDimCombination)   &&
                    tmpFrmVirtual.ItemId        == _inventDimCombination.ItemId     &&
                    tmpFrmVirtual.InventDimId   == inventDimIdLocal;

        if (_set)
        {
            if (_pdsCWQty)
            {
                tmpFrmVirtual.PdsCWQty = _pdsCWQty;
            }
            else
            {
                tmpFrmVirtual.PdsCWQty = 0;
            }

            tmpFrmVirtual.TableNum      = tableNum(InventDimCombination);
            tmpFrmVirtual.ItemId        = _inventDimCombination.ItemId;
            tmpFrmVirtual.InventDimId   = inventDimIdLocal;
            tmpFrmVirtual.write();
        }

        select sum(PdsCWQty) from tmpFrmVirtual
            where   tmpFrmVirtual.TableNum      == _inventDimCombination.TableId &&
                    tmpFrmVirtual.ItemId        == _inventDimCombination.ItemId
        #InventDimJoin(tmpFrmVirtual.inventDimId,inventDimLocal,inventDimCriteria,inventDimParm);

        pdsCWInventQty = tmpFrmVirtual.PdsCWQty;
    }

    return pdsCWInventQty;
}

/// <summary>
/// Return the available physical catch weight inventory for specific inventdim
/// </summary>
/// <param name="_inventDimCombination">Current InventDimCombination record</param>
/// <returns>Available Physical Catch Weight inventory</returns>
display PdsCWAvailPhysical pdsCWAvailPhysical(InventDimCombination _inventDimCombination)
{
    PdsCWInventQty  pdsCWQtyAvailPhysical = 0;
    InventDim       inventDimFind;

    //Check if user has security access
    if (hasFieldAccess(tableNum(InventSum),
                       fieldNum(InventSum, PdsCWAvailPhysical),
                       AccessType::View))
    {
        if (PdsGlobal::pdsIsCWItem(_inventDimCombination.ItemId))
        {
            inventDimFind.initFromInventDim(inventDim);
            EcoResProductDimGroupSetup::copyProductDimensionsForItem(_inventDimCombination.ItemId, _inventDimCombination.inventDim(), inventDimFind);

            inventDimParm       = inventDimFormSetup.parmDimParmVisibleGrid().data();
            inventDimParm.setActiveProductDim(EcoResProductDimGroupSetup::newItemId(_inventDimCombination.ItemId));

            pdsCWQtyAvailPhysical = InventSum::findSumQty(inventDimCombination.ItemId, inventDimFind, inventDimParm).PdsCWAvailPhysical;
        }
    }

    return pdsCWQtyAvailPhysical;
}

