void gotFocus()
{
    super();

    inventDimParmFixed.enabled(true);
    menuButton.enabled(true);
}

        boolean modified()
        {
            boolean     ret;
            PurchQty    tmpPurchQty;

            ret = super();

            tmpPurchQty = InventTable::calculateOrderQuantity(InventItemPurchSetup::find(inventSum.ItemId, inventSum.InventDimId),
															  inventSum.InventDimId,
															  this.realValue(),
															  InventTable::find(inventSum.ItemId).purchUnitId());

            if (tmpPurchQty != this.realValue())
            {
                inventSum_ds.purchQty(true, inventSum, tmpPurchQty);
            }

            return ret;
        }

/// <summary>
/// Complete standard field update and update purchQty in InventSum datasource
/// </summary>
/// <returns>true if change is completed; otherwise false</returns>
boolean modified()
{
    boolean     ret;

    ret = super();

    if (ret)
    {
        inventSum_ds.purchQty(true, inventSum, PdsCatchWeight::inventQty(inventTable.ItemId, pdsCWQty.realValue(), inventTable.inventTableModulePurch().UnitId));
    }

    return ret;
}

        boolean modified()
        {
            boolean             ret;
            PurchQty            tmpSalesQty;

            ret = super();

            tmpSalesQty = InventTable::calculateOrderQuantity(InventItemPurchSetup::findDefault(inventTable.ItemId),
															  inventDimId,
															  this.realValue(),
														      inventTable.salesUnitId());

            if (tmpSalesQty != this.realValue())
            {
                inventDimCombination_ds.purchQty(true, inventDimCombination, tmpSalesQty);
                inventDimCombination_ds.research(true);
            }

            return ret;
        }

