/// If _actionPaneHeaderVisible should be visible.
/// </param>
public void addVendorsFromVendorSearch(List _selectedVendorList, boolean _checkForExistingVendors = true)
{
    if (PurchRFQAddVendors::addVendorsFromList(_selectedVendorList, purchRFQCaseTable, _checkForExistingVendors))
    {
        purchRFQVendLink_ds.research();
    }
}

void allignDatasourceTablesLayout()
{
    FormDataSource          fds = this.dataSource();
    QueryBuildDataSource    qbds;
    PurchRFQVendLink        vendLink;
    PurchRFQCaseId          rfqCaseId;
    Args                    callerObject;
    Common                  common;

    PurchRFQCaseId getCallerObjectRFQCaseId(Common _common)
    {
        PurchRFQCaseTable       callerPurchRFQCaseTable;
        PurchReqLine            purchReqLine;

        switch (common.TableId)
        {
            case tableNum(PurchReqLine) :
                purchReqLine = common;
                rfqCaseId = purchReqLine.PurchRFQCaseId;
                break;

            case tableNum(PurchRFQCaseTable) :
                callerPurchRFQCaseTable = common;
                rfqCaseId = callerPurchRFQCaseTable.RFQCaseId;
                break;
        }
        return  rfqCaseId;
    }

    // Try to locate PurchRFQVendLinkAlternative datasource...
    qbds = fds.query().dataSourceName(identifierStr(PurchRFQVendLinkAlternative));

    qbds.joinMode(JoinMode::ExistsJoin);
    qbds.enabled(true);

    // Setup (re-assign) name...
    qbds.name(identifierStr(PurchRFQVendLinkAlternative));

    // Setting up proper link configuration...
    if (listPagePurchRFQCaseId)
    {
        rfqCaseId = listPagePurchRFQCaseId;
    }
    else
    {
        callerObject = element.args();
        common = callerObject.record();

        rfqCaseId = (!common && callerObject.lookupValue()) ? callerObject.lookupValue() : getCallerObjectRFQCaseId(common);
    }

    select count(RecId) from vendLink where vendLink.rfqCaseId == rfqCaseId;

    if (!vendLink.RecId)
    {
        qbds.joinMode(JoinMode::NoExistsJoin);
    }
    else
    {
        qbds.clearLinks();
    }

    // Re-assign vendAccount range:
    vendAccountRange = SysQuery::findOrCreateRange(qbds, fieldNum(PurchRFQVendLink, VendAccount));

    vendAccountRange.status(RangeStatus::Locked);
}

public boolean canChangeDeliveryPostalAddress_BR()
{
    if (BrazilParameters::isEnabled()
        && !purchRFQCaseTable.InventSiteId)
    {
        return checkFailed("@SYS4000725");
    }
    return true;
}

