/// <summary>
/// Determines if alternate lines exist for the replies to the current RFQ case.
/// </summary>
/// <param name="_purchRFQCaseTable">
/// The <c>PurchRFQCaseTable</c> record.
/// </param>
/// <returns>
/// true if alternate lines exist; otherwise, false.
/// </returns>
public boolean doAlternatesExist(PurchRFQCaseTable _purchRFQCaseTable)
{
    PurchRFQCaseId      key = _purchRFQCaseTable.RFQCaseId;
    boolean             alternateExist;

    if (key)
    {
        if (!mapAlternateExist)
        {
            mapAlternateExist = new Map(Types::String, Types::Enum);
        }

        if (mapAlternateExist.exists(key))
        {
            alternateExist = mapAlternateExist.lookup(key);
        }
        else
        {
            alternateExist = _purchRFQCaseTable.doAlternatesExist();
            mapAlternateExist.insert(key, alternateExist);
        }
    }

    return alternateExist;
}

