public void enableProjSalesPrice()
{
    boolean mustDefaultCostPriceAsSalesPrice;

    if (purchRFQCaseLine.ProjId)
    {
        if (!purchRFQCaseLine.ItemId && ProjParameters::find().DefaultCostPriceAsSalesPrice)
        {
            mustDefaultCostPriceAsSalesPrice = true;
        }

        purchRFQCaseLine_ds.object(fieldNum(PurchRFQCaseLine, ProjSalesCurrencyId)).allowEdit(purchRFQCaseTableForm.editProjSalesCurrencyId(purchRFQCaseLine.ProjId) && !mustDefaultCostPriceAsSalesPrice);
        purchRFQCaseLine_ds.object(fieldNum(PurchRFQCaseLine, ProjSalesCurrencyId)).skip(!(purchRFQCaseTableForm.editProjSalesCurrencyId(purchRFQCaseLine.ProjId) && !mustDefaultCostPriceAsSalesPrice));

        purchRFQCaseLine_ds.object(fieldNum(PurchRFQCaseLine, ProjSalesPrice)).allowEdit(!mustDefaultCostPriceAsSalesPrice);
        purchRFQCaseLine_ds.object(fieldNum(PurchRFQCaseLine, ProjSalesPrice)).skip(mustDefaultCostPriceAsSalesPrice);
    }
    else
    {
        purchRFQCaseLine_ds.object(fieldNum(PurchRFQCaseLine, ProjSalesCurrencyId)).allowEdit(false);
        purchRFQCaseLine_ds.object(fieldNum(PurchRFQCaseLine, ProjSalesCurrencyId)).skip(true);

        purchRFQCaseLine_ds.object(fieldNum(PurchRFQCaseLine, ProjSalesPrice)).allowEdit(false);
        purchRFQCaseLine_ds.object(fieldNum(PurchRFQCaseLine, ProjSalesPrice)).skip(true);
    }
}

public boolean existQuestionnaire()
{
    return PurchRFQCaseTableKMCollection::find(purchRFQCaseTable.RFQCaseId, purchRFQCaseTableKMCollection.KMCollectionId).RecId != 0;
}

public List getSelectedVendorAccountNumbersList ()
{
    Object      callerObject;
    List        listVendAccNum;

    listVendAccNum =  new List(Types::String);
    callerObject    = element.args().caller();
    if (callerObject 
        && callerObject is FormRun 
        && (callerObject.name() == formStr(VendSearchResults)
            || callerObject.name() == formStr(VendVendorSearchResults)))
    {
        listVendAccNum = callerObject.getSelectedVendorAccountNumberList();
    }
    return listVendAccNum;
}

public void  init()
{
    PurchRFQLine                rfqLine;
    Common                      rec         = element.args().record();
    PurchRFQCaseTable           purchRFQCaseTableRec;
    VendTable                   localVendTable;
    VendTmpCurrentVendorResult  vendTmpCurrentVendorResult;
    VendCurrentVendorResultTmp  vendCurrentVendorResultTmp;

    #ISOCountryRegionCodes

    if (#PdsCatchWeightEnabled)
    {
        formCtrl = PdsCWFormCtrl_PurchRFQCaseTable::newFromForm(element);
        formCtrl.initPre();
    }

    super();

    isRFQAmendmentEnabled = PurchRFQParameters::isRFQAmendmentEnabled();
    isProductSearchLookupEnabled = EcoResProductSearchLookup::isProductSearchLookupEnabled();

    if (formCtrl)
    {
        formCtrl.initPost();
    }

    if (rec.TableId == tableNum(PurchRFQCaseTable))
    {
        purchRFQCaseTableRec = rec;
    }

    if (this.isCalledFromListPage())
    {
        purchRFQCaseTableForm = PurchRFQCaseTableForm::newFromCommon(rec, element.args().parm());

        callerListPage = element.args().caller();
        // if called from the VendTableListPage
        if (callerListPage.name() == formStr(VendTableListPage))
        {
            localVendTable = rec;
            vendAccountSelection = localVendTable.AccountNum;
            itemBuyerGroupSelection = localVendTable.ItemBuyerGroupId;
        }
        else if (callerListPage.name() == formStr(VendSearchResults))
        {
            vendTmpCurrentVendorResult = rec;
            vendAccountSelection = vendTmpCurrentVendorResult.VendorAccountNum;
        }
        else if (callerListPage.name() == formStr(VendVendorSearchResults))
        {
            vendCurrentVendorResultTmp = rec;
            vendAccountSelection = vendCurrentVendorResultTmp.VendorAccountNumber;
        }
        else
        {
            // Transfer filter setting from a caller list page...
            vendAccountSelection = callerListPage.design().controlName(formControlStr(PurchRFQCaseTableListPage,VendorFilter)).valueStr();
            itemBuyerGroupSelection = callerListPage.design().controlName(formControlStr(PurchRFQCaseTableListPage,BuyerGroupFilter)).valueStr();
        }
        skipSecondExecuteQueryForLPCall = true;

        listPagePurchRFQCaseId = purchRFQCaseTableRec.rfqCaseId;
    }
    else
    {
        boolean isCalledFromProjectListPage = this.isCalledFromProjectListPage();
        purchRFQCaseTableForm = PurchRFQCaseTableForm::newFromCommon(rec, this.determineCallerModule(isCalledFromProjectListPage));

        if (isCalledFromProjectListPage)
        {
            element.args().parmEnumType(0);
        }

        if (rec && rec.TableId == tableNum(PurchRFQCaseTable))
        {
            purchRFQCaseTable_ds.query()
                .dataSourceTable(tableNum(PurchRFQCaseTable))
                .addRange(fieldNum(PurchRFQCaseTable, RFQCaseId))
                .value(queryValue(purchRFQCaseTableRec.RFQCaseId));
        }

        skipSecondExecuteQueryForLPCall = false;
    }

    this.allignDatasourceTablesLayout();
    element.updateDesign(InventDimFormDesignUpdate::Init);

    purchRFQCaseLineGrid.visibleColsValue(14+conLen(inventDimFormSetup.dimFieldsActive(inventDimFormSetup.parmDimParmVisibleGrid())));

    purchRFQCaseTable_ds.query(purchRFQCaseTableForm.queryPurchRFQCaseTable(purchRFQCaseTable_ds.query()));
    purchRFQCaseLine_ds.query(purchRFQCaseTableForm.queryPurchRFQCaseLine(purchRFQCaseLine_ds.query()));

    if (rec && rec.TableId == tableNum(PurchRFQLine))
    {
        rfqLine = rec;
        purchRFQCaseTable_ds.query().dataSourceTable(tableNum(PurchRFQCaseTable)).addRange(fieldNum(PurchRFQCaseTable, rfqCaseId)).value(queryValue(PurchRFQTable::find(rfqLine.rfqId).rfqCaseId));
    }

    if (element.args().lookupValue())
    {
        str rfqCaseId = element.args().lookupValue();
        purchRFQCaseTable_ds.query().dataSourceTable(tableNum(PurchRFQCaseTable)).addRange(fieldNum(PurchRFQCaseTable, rfqCaseId)).value(queryValue(rfqCaseId));
    }

    // For Projects: Show only purchase requisitions associated to projects if form is called from the project main menu
    if (purchRFQCaseTableForm.project())
    {
        if (!rec) // called from menu
        {
            purchRFQCaseTable_ds.query().dataSourceTable(tableNum(PurchRFQCaseTable)).addRange(fieldNum(PurchRFQCaseTable, ProjId)).value(SysQuery::valueNotEmptyString());
        }
    }

    purchRFQCaseTable_ds.initRange();

    if (isRFQAmendmentEnabled)
    {
        purchRFQCaseLine_ds.initRange();
    }

    purchRFQVendLinkAlternative_ds.initRange();

    if (callerListPage)
    {
        // For calls from secondary list pages it is required to set up status filter properly:
        statusSelection = PurchRFQCaseTableListPageInteraction::getStatusFilterSettingsForTaskPage(callerListPage.args());
    }
    else
    {
        element.updateLinks();
    }

    // Needed to check validity of address
    logisticsPostalAddressHeader_ds.addFieldToSelectionList(fieldNum(LogisticsPostalAddress, ValidFrom));
    logisticsPostalAddressHeader_ds.addFieldToSelectionList(fieldNum(LogisticsPostalAddress, ValidTo));
    logisticsPostalAddressHeader_ds.addFieldToSelectionList(fieldNum(LogisticsPostalAddress, CountryRegionId));
    logisticsPostalAddressLine_ds.addFieldToSelectionList(fieldNum(LogisticsPostalAddress, ValidFrom));
    logisticsPostalAddressLine_ds.addFieldToSelectionList(fieldNum(LogisticsPostalAddress, ValidTo));
    logisticsPostalAddressLine_ds.addFieldToSelectionList(fieldNum(LogisticsPostalAddress, CountryRegionId));
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]))
    {
        element.setVisible_IN();
    }

    if (BrazilParameters::isEnabled())
    {
        salesPurchOperationTypeController_BR = new SalesPurchOperationTypeController_BR(this.name());
    }

    // <GTE>
    if (TaxSolutionScopeIntegrationUtil::isCompanyEnabled())
    {
        ProjectSalesTax.visible(false);
        ProjectSalesTax_ProjTaxGroupId.visible(false);
        ProjectSalesTax_ProjTaxItemGroupId.visible(false);
    }

    listVendorsFromVendorSearch = element.getSelectedVendorAccountNumbersList();

    AmendmentStatus.text("@SCM:UnderAmendment");

    VendorQuestion_IsVendorQuestionEnabled.checked(PurchRFQParameters::find().IsVendorQuestionEnabled);

    if (PurchIsSupplierShipmentDatesControlsVisibleFlight::instance().isEnabled())
    {
        PurchRFQVendLink_ShipCalendarId.visible(PurchParameters::find().IsSupplierShipmentDatesEnabled);
    }
}

/// <summary>
/// Determines the caller module.
/// </summary>
/// <param name = "_isCalledFromPurchRFQCTListPageProject">A Boolean indicating whether the form is called from project related forms.</param>
/// <returns>The module if found; otherwise, empty string.</returns>
protected str determineCallerModule(boolean _isCalledFromPurchRFQCTListPageProject)
{
    str integratingModule;

    if (_isCalledFromPurchRFQCTListPageProject)
    {
        const str ProjectModule = 'Project';
        integratingModule = ProjectModule;
    }
    else
    {
        if (element.args())
        {
            integratingModule = element.args().parm();
        }
    }

    return integratingModule;
}

