/// <summary>
/// Decides whether or not address changes are allowed
/// </summary>
/// <returns>
/// True if address changes should be available, otherwise - False.
/// </returns>
private boolean isAddressEditEnabled()
{
    return ((purchRFQCaseTable.RecId) && (purchRFQCaseTable.StatusLow == PurchRFQStatus::Created));
}

/// <summary>
/// Determines if the form is called from a listPage.
/// </summary>
/// <returns>
/// true if the form is called from a list page; otherwise, false.
/// </returns>
protected boolean isCalledFromListPage()
{
    if (element.args())
    {
        FormRun callerForm = element.args().caller() as FormRun;
        if (callerForm)
        {
            switch (callerForm.name())
            {
                case formStr(PurchRFQCaseTableListPage):
                case formStr(VendTableListPage):
                case formStr(VendSearchResults):
                case formStr(VendVendorSearchResults):
                    return true;
            }
        }
    }
    return false;
}

/// <summary>
/// Determines if the form is called from a project listPage.
/// </summary>
/// <returns>
/// true if the form is called from a project list page; otherwise, false.
/// </returns>
protected boolean isCalledFromProjectListPage()
{
    if (element.args())
    {
        FormRun callerForm = element.args().caller() as FormRun;
        if (callerForm)
        {
            switch (callerForm.name())
            {
                case formStr(ProjGrant):
                case formStr(ProjGrantTransListPage):
                case formStr(ProjTable):
                case formStr(ProjProjectsListPage):
                case formStr(ProjInvoiceTable):
                case formStr(ProjProjectContractsListPage):
                    return true;
            }
        }
    }
    return false;
}

/// <summary>
/// Decides whether or not copy from action should be available on the list page.
/// </summary>
/// <returns>
/// True if copy from action should be available, otherwise - False.
/// </returns>
private boolean isCopyFromActionEnabled()
{
    return element.createLineAllowed();
}

/// <summary>
/// Decides whether or not reply action should be available on the list page.
/// </summary>
/// <returns>
/// True if reply action should be available, otherwise - False.
/// </returns>
private boolean isReplyActionEnabled()
{
    return ((purchRFQCaseTable.RecId) && (purchRFQCaseTable.StatusHigh > PurchRFQStatus::Created));
}

/// <summary>
/// Calculates whether or not send action should be available on the list page.
/// </summary>
/// <returns>
/// True if send action should be available, otherwise returns false.
/// </returns>
private boolean isSendActionEnabled()
{
    PurchRFQCaseTable       localPurchRFQCaseTable;
    boolean                 sendButtonIsEnabled = false;

    localPurchRFQCaseTable =  purchRFQCaseTable_ds.getFirst(true) ? purchRFQCaseTable_ds.getFirst(true) : purchRFQCaseTable_ds.cursor();

    while (localPurchRFQCaseTable && !sendButtonIsEnabled)
    {
        purchRFQCaseTableType = localPurchRFQCaseTable.type();
        sendButtonIsEnabled   = purchRFQCaseTableType.mayRFQBeSent();
        localPurchRFQCaseTable = purchRFQCaseTable_ds.getNext();
    }

    return sendButtonIsEnabled;
}

public LogisticsLocationRecId parmPostalAddressLocation(LogisticsLocationRecId _postalAddressLocation = 0)
{
    if (!_postalAddressLocation)
    {
        // Set location by active datasource
        if (element.dataSource().name() == purchRFQCaseLine_ds.name())
        {
            _postalAddressLocation = purchRFQCaseLine.deliveryLocation();
        }
        else
        {
            _postalAddressLocation = purchRFQCaseTable.deliveryLocation();
        }
    }

    postalAddressLocation = _postalAddressLocation;
    return postalAddressLocation;
}

public ProjInventJournalTransMapForm projInventJournalTransMapForm()
{
    if (!projInventJournalTransMapForm)
    {
        projInventJournalTransMapForm = ProjInventJournalTransMapForm::construct(purchRFQCaseLine);
    }
    else
    {
        projInventJournalTransMapForm.parmProjInventJournalTransMap(purchRFQCaseLine);
    }

    return projInventJournalTransMapForm;
}

