}

/// <summary>
/// Updates product dimensions assuming item has already been set on the line.
/// </summary>
/// <param name="_inventDim">
/// An instance of <c>InventDim</c> that provides the new product dimensions.
/// </param>
public void updateProductDimensions(InventDim _inventDim)
{
    Set productDimensionFields = inventDim.copyProductDimensionsAndGetModifiedDimensionFields(_inventDim, purchRFQCaseLine.ItemId);
    if (productDimensionFields)
    {
        this.modifyInventDimSet(_inventDim, productDimensionFields);
    }
}

/// <summary>
/// Updates the inventory dimension field.
/// </summary>
/// <param name = "_fieldId">Field Id that is being updated.</param>
public void modifyInventDimField(FieldId _fieldId)
{
    if (purchRFQCaseLine.PurchRFQLineMap::modifyInventDimField(inventDim, _fieldId))
    {
        InventDim.data(purchRFQCaseLine.inventDim());
        InventDim_DS.setCurrent();
    }

    element.findProductNumber();
}

/// <summary>
/// Updates <c>InventDim</c> data based on the given set of modified dimension fields.
/// </summary>
/// <param name = "_inventDim">The new inventory dimension record.</param>
/// <param name = "_dimFields">A set containing the ID's of the modified <c>InventDim</c> fields.</param>
public void modifyInventDimSet(InventDim _inventDim, Set _dimFields)
{
    if (purchRFQCaseLine.PurchRFQLineMap::modifyInventDimSet(_inventDim, _dimFields))
    {
        InventDim.data(purchRFQCaseLine.inventDim());
    }
}

    /// <summary>
    /// Sets action pane button access for the details page.
    /// </summary>
    /// <param name="_gridIsPopulated">
    /// Boolean identifying if the grid is populated.
    /// </param>
    public void setActionPaneButtonAccess(boolean _gridIsPopulated)
    {
        boolean allowPurchRFQAmendment;
        boolean allowPurchRFQQuestionaire;
        boolean allowPurchRFQButtonEdit;
        boolean allowAmendmentWithNoLock;
        boolean isRFQPublished = purchRFQPublishTable_PSN.RecId;

        allowPurchRFQButtonEdit = !((purchRFQCaseTableForm.isRFQSent(purchRFQCaseTable) || isRFQPublished)
                                    && PurchRFQCaseTable.isAmended == NoYes::No);

        // Business logic dependent buttons availability is calculated by dedicated methods.
        boolean canSend = this.isSendActionEnabled()
                    && !(isRFQAmendmentEnabled && purchRFQCaseTable.isAmended);

        // Set process button enable/disable, visible/invisible properly.
        sendMenuItemButton.visible(PurchRFQCaseTableForm::isSendVisible());
        sendMenuItemButton.enabled(PurchRFQCaseTableForm::isSendEnabled(canSend));
        PurchRFQCaseSendPublish_PSN.enabled(PurchRFQCaseTableForm::isSendAndPublishEnabled(purchRFQCaseTable));

	    boolean canBulkSend = PurchRFQCaseTableForm::isSendVisible() && PurchRFQCaseAutoCreateUpdateHeaderStatusPerLineFlight::instance().isEnabled();
        bulkSendMenuItemButton.visible(canBulkSend);
        bulkSendMenuItemButton.enabled(canBulkSend && PurchRFQCaseTableForm::isSendEnabled(canSend));

		enterReplyMenuItemButton.enabled((_gridIsPopulated) ? this.isReplyActionEnabled() : false);
        copyFromMenuItemButton.enabled((_gridIsPopulated) ? this.isCopyFromActionEnabled() : false);

		// The rest of the buttons shouldn't be available for empty form.
        totalsMenuItemButton.enabled(_gridIsPopulated);
        manageMiscChargesMenuItemButton.enabled(_gridIsPopulated);
        compareRepliesMenuItemButton.enabled(_gridIsPopulated && purchRFQCaseTable.StatusHigh > PurchRFQStatus::Sent);
        rfqJournalMenuItemButton.enabled(_gridIsPopulated);
        printMgmtButton.enabled(_gridIsPopulated && purchRFQCaseTable.rfqVendNum != PurchRFQVendNum::None);

        plannedPOMenuItemButton.enabled(_gridIsPopulated);

        // The amendment should not be added/removed when one or more RFQ reply is received
        allowAmendmentWithNoLock = purchRFQCaseTable.StatusHigh > PurchRFQStatus::Created
            && purchRFQCaseTable.StatusHigh < PurchRFQStatus::Accepted;
        AddAmendmentButton.visible(isRFQAmendmentEnabled ? false : allowAmendmentWithNoLock);
        RemoveAmendmentButton.visible(isRFQAmendmentEnabled ? false : allowAmendmentWithNoLock);
        PurchRFQAmendment_ds.allowCreate(isRFQAmendmentEnabled ? false : allowAmendmentWithNoLock);
        PurchRFQAmendment_ds.allowDelete(isRFQAmendmentEnabled ? false : allowAmendmentWithNoLock);

        PurchRFQAmendmentCreate.enabled(purchRFQCaseTable.editAmendmentAllowed());
        PurchRFQAmendmentFinalize.enabled(purchRFQCaseTable.isAmended);
        PurchRFQAmendmentCreate.visible(isRFQAmendmentEnabled && !purchRFQCaseTable.isAmended);
        PurchRFQAmendmentCancel.visible(isRFQAmendmentEnabled && purchRFQCaseTable.isAmended);
        PurchRFQAmendmentFinalize.visible(isRFQAmendmentEnabled);

        allowPurchRFQQuestionaire = purchRFQCaseTable.StatusHigh < PurchRFQStatus::Received && allowPurchRFQButtonEdit;
        AddQuestionnaire.enabled(isRFQAmendmentEnabled ? allowPurchRFQButtonEdit : allowPurchRFQQuestionaire);
        PurchRFQCaseTableKMCollection_ds.allowCreate(allowPurchRFQQuestionaire);
        PurchRFQCaseTableKMCollection_ds.allowDelete(allowPurchRFQQuestionaire);
        ViewQuestions.enabled(this.existQuestionnaire());

        UnsealBids.enabled(purchRFQCaseTable.canBidBeUnsealed());

        // The unseal bid button should be hidding if the bid type is not sealed.
        UnsealBids.visible(PurchRFQCaseTable.BidType == PurchRFQBidType::Sealed);

        CancelMenuItemButton.enabled(purchRFQCaseTable.canCancelRFQ());

        PurchRFQVendViewQuestion.visible(purchRFQCaseTable.IsVendorQuestionEnabled);
    }

void setAmendmentFieldsAccess()
{
    purchRFQAmendment_ds.object(fieldNum(PurchRFQAmendment, Name)).allowEdit(purchRFQCaseTable.isAmended && PurchRFQAmendment.isLatestAmendment());
    purchRFQAmendment_ds.object(fieldNum(PurchRFQAmendment, Description)).allowEdit(purchRFQCaseTable.isAmended && PurchRFQAmendment.isLatestAmendment());
}

void setButtonAccessHeader()
{
    boolean gridIsPopulated = purchRFQCaseTable.RecId;

    // Set access settings for the action pane
    this.setActionPaneButtonAccess(gridIsPopulated);

    // Address action pane
    actionPaneAddressHeader.enabled(this.isAddressEditEnabled());

    PurchRFQSealedBidFlightingManager::initSealedBiddingSpecificControl(element);
}

void setButtonAccessLine()
{
    boolean allowNewLines = element.createLineAllowed();

    AddButton.enabled(allowNewLines);

    buttonLineSetup.enabled(purchRFQCaseLine.RecId ? true : false);
    VendorReplyMenuItemButton.enabled(this.isReplyActionEnabled());

    // Line Functions buttons
    boolean isFunctionsButtonEnabled = purchRFQCaseLine.LineItemType == PurchRFQLineItemType::Item && purchRFQCaseLine.RecId;
    buttonLineFunctions.enabled(isFunctionsButtonEnabled);

    purchRFQBOMExpand.enabled(isFunctionsButtonEnabled
                                && purchRFQCaseLine.LineItemType == PurchRFQLineItemType::Item
                                && purchRFQCaseLine.InventTransId !=''
                                && purchRFQCaseLine.StatusHigh == PurchRFQStatus::Created
                                && allowNewLines
                                && PurchRFQCaseLine.inventTable().isBOMProducedByDefault());

    ButtonPurchRFQCaseLineCopy.enabled(isFunctionsButtonEnabled
                                        && purchRFQCaseLine.LineItemType == PurchRFQLineItemType::Item
                                        && purchRFQCaseLine.ItemId
                                        && purchRFQCaseLine.StatusLow == PurchRFQStatus::Created
                                        && allowNewLines);

    boolean allowRenumberLineNumber = (purchRFQCaseTable.RecId && purchRFQCaseTable.StatusHigh > PurchRFQStatus::Created) ? false : true;
    buttonRenumberLineNumber.enabled(allowRenumberLineNumber);

    // Line address buttons
    DirPartyPostalAddressNew.enabled(this.isAddressEditEnabled());
    OtherAddress.enabled(this.isAddressEditEnabled());
}

void setEditItemTypeAllowed(boolean  allowEdit)
{
    purchRFQCaseLine_ds.object(fieldNum(PurchRFQCaseLine, LineItemType)).allowEdit(allowEdit);
}

void setEditRFQTypeAllowed(boolean _allowEdit)
{
    purchRFQCaseTable_ds.object(fieldNum(PurchRFQCaseTable, rfqType)).allowEdit(_allowEdit);
    purchRFQCaseLine_ds.object(fieldNum(PurchRFQCaseLine, rfqType)).allowEdit(_allowEdit);
}

/// <summary>
/// Set access levels to datasources and fields.
/// </summary>
void setFieldsAccess()
{
    boolean allowHeaderEdit   = element.editHeaderAllowed();
    boolean allowHeaderDelete = purchRFQCaseTable.checkDelete(false);

    if (isConfigurationkeyEnabled(configurationKeyNum(VendorCollaboration_PSN)))
    {
        element.setRFQCaseTableInternalFieldsAccess_PSN(allowHeaderEdit);
    }

    purchRFQCaseTable_ds.allowEdit(allowHeaderEdit);
    purchRFQCaseTable_ds.allowDelete(allowHeaderDelete);
    element.setEditRFQTypeAllowed(allowHeaderEdit 
        && (purchRFQCaseTable.rfqType == PurchRFQType::Purch || purchRFQCaseTable.rfqType == PurchRFQType::PurchAgreement));

    boolean inAmendmentProcess = purchRFQCaseTable.isAmended == NoYes::Yes;
    boolean isRFQSent = purchRFQCaseTableForm.isRFQSent(purchRFQCaseTable);
    boolean isRFQPublished = purchRFQPublishTable_PSN.RecId;
    boolean allowRFQFieldEdit = !((isRFQSent || isRFQPublished) && !inAmendmentProcess);

    purchRFQCaseTable_ds.object(fieldNum(PurchRFQCaseTable, SolicitationType)).allowEdit(!isRFQSent);
    purchRFQCaseTable_ds.object(fieldNum(PurchRFQCaseTable, BidType)).allowEdit(!isRFQSent);
    purchRFQCaseTable_ds.object(fieldNum(PurchRFQCaseTable, SolicitationType)).allowEdit(!isRFQSent);
    purchRFQCaseTable_ds.object(fieldNum(PurchRFQCaseTable, AllowBidByInvitationOnly)).allowEdit(isRFQAmendmentEnabled ? allowRFQFieldEdit : !isRFQSent);
    IsSealedHeader.visible(purchRFQCaseTable.BidType == PurchRFQBidType::Sealed);

    element.setItemType(purchRFQCaseLine.LineItemType);

    element.enableProjSalesPrice();

    boolean allowLineEdit   = element.editLineAllowed();
    boolean allowLineDelete = element.deleteLineAllowed();

    purchRFQCaseLine_ds.allowCreate(element.createLineAllowed());
    purchRFQCaseLine_ds.allowEdit(allowLineEdit);
    purchRFQCaseLine_ds.allowDelete(allowLineDelete);

    //Amendment tab field access
    purchRFQAmendment_ds.object(fieldNum(PurchRFQAmendment, AmendmentNumber)).visible(isRFQAmendmentEnabled);
    purchRFQAmendment_ds.object(fieldNum(PurchRFQAmendment, createdDateTime)).visible(isRFQAmendmentEnabled);
    purchRFQAmendment_ds.object(fieldNum(PurchRFQAmendment, ProcessedBy)).visible(isRFQAmendmentEnabled);

    purchRFQCaseTable_ds.object(fieldNum(PurchRFQCaseTable, Name)).allowEdit(allowLineEdit);
    RFQInfo_AllowAlternates.enabled(purchRFQCaseTable.StatusHigh < PurchRFQStatus::Received || !element.doAlternatesExist(purchRFQCaseTable));
    RFQInfo_AllowAlternatesHeader.enabled(purchRFQCaseTable.StatusHigh < PurchRFQStatus::Received || !element.doAlternatesExist(purchRFQCaseTable));
    purchRFQCaseLine_ds.object(fieldNum(PurchRFQCaseLine, ExpiryDateTime)).allowEdit(purchRFQCaseTable.BidType != PurchRFQBidType::Sealed);
    purchRFQCaseLine_ds.object(fieldNum(purchRFQCaseLine, ItemId)).allowEdit(purchRFQCaseLine.RecId == 0);

    boolean isEditItemTypeAllowed = BrazilParameters::isEnabled() ? false : purchRFQCaseLine.RecId == 0;
    element.setEditItemTypeAllowed(isEditItemTypeAllowed);

    element.updateDesign(InventDimFormDesignUpdate::Active);
    element.setButtonAccessHeader();
    element.setButtonAccessLine();

    PurchRFQCaseTable_UnsealedDateTimeHeader.visible(purchRFQCaseTable.doShowUnsealedDateTime());

    boolean isRFQCanceled = this.purchRFQCaseTable().isRFQCanceled();
    if (isRFQAmendmentEnabled)
    {
        this.setFieldAccessForAmendedRFQ(allowRFQFieldEdit);

        if ( purchRFQCaseLine.purchReqLine().RecId != 0 )
        {
            PurchRFQCaseTable_DS.object(fieldNum(PurchRFQCaseTable, InclTax)).allowEdit(false);
        }

        if (isRFQCanceled)
        {
            this.updatedFieldAccessForCanceledRFQ();
        }
    }

    purchRFQCaseTable_ds.object(fieldNum(PurchRFQCaseTable, InclTax)).enabled(!(PurchRFQCaseTableFromPurchReqInclTaxDisabledFlight::instance().isEnabled()
        && purchRFQCaseTable.rfqType == PurchRFQType::PurchReq));

    purchRFQVendLink_ds.allowCreate(!isRFQCanceled);
    purchRFQVendLink_ds.allowEdit(!isRFQCanceled);

    boolean canDeletePurchRFQVendLink = PurchRFQVendLink.statusHigh() == PurchRFQStatus::Created;

    purchRFQVendLink_ds.allowDelete(canDeletePurchRFQVendLink);

    ProcessButtonGroup.enabled(!isRFQCanceled);
    AmendmentButtonGroup.enabled(!isRFQCanceled);
    VendorSearch.enabled(!isRFQCanceled);

    PurchRFQConfigReply1.enabled(!isRFQCanceled 
        && PurchRFQCaseTableListPageInteraction::isRFQCaseFieldsTransferToReplyOrShowLegacyFieldsFeatureEnabled());

    AutoAddVendors.enabled(!isRFQCanceled);

    VendorQuestionOptions.visible(PurchRFQParameters::find().IsVendorQuestionEnabled);

    VendorQuestion_IsVendorQuestionEnabled.allowEdit(allowRFQFieldEdit);
    VendorQuestion_VendorQuestionCutoffDateTime.allowEdit(VendorQuestion_IsVendorQuestionEnabled.checked() && allowRFQFieldEdit);

    if (ProjProjectOperationsIntegrationFeatureHelper::isProjOpsProjectPurchaseOrderFeatureEnabledForCompany())
    {
        ProjectSalesPrice.visible(false);
        ProjectSalesPrice.enabled(false);
    }
}

private void updatedFieldAccessForCanceledRFQ()
{
    purchRFQCaseTable_ds.allowEdit(false);
    purchRFQCaseTable_ds.allowDelete(false);

    purchRFQCaseLine_ds.allowCreate(false);
    purchRFQCaseLine_ds.allowEdit(false);
    purchRFQCaseLine_ds.allowDelete(false);
}

private void setFieldAccessForAmendedRFQ(boolean _allowRFQFieldEdit)
{
    // RFQ Header tab fields
    PurchRFQCaseTable_DS.object(fieldNum(PurchRFQCaseTable, ExpiryDateTime)).allowEdit(_allowRFQFieldEdit);
    PurchRFQCaseTable_DS.object(fieldNum(PurchRFQCaseTable, InclTax)).allowEdit(_allowRFQFieldEdit);
    PurchRFQCaseTable_DS.object(fieldNum(PurchRFQCaseTable, DeliveryDate)).allowEdit(_allowRFQFieldEdit);
    PurchRFQCaseTable_DS.object(fieldNum(PurchRFQCaseTable, AllowAlternates)).allowEdit(_allowRFQFieldEdit);

    //Purchase agreement order fields
    PurchRFQCaseTable_DS.object(fieldNum(PurchRFQCaseTable, ResponsibleWorkerId)).allowEdit(_allowRFQFieldEdit);
    PurchRFQCaseTable_DS.object(fieldNum(PurchRFQCaseTable, ValidityDateStart)).allowEdit(_allowRFQFieldEdit);
    PurchRFQCaseTable_DS.object(fieldNum(PurchRFQCaseTable, ValidityDateEnd)).allowEdit(_allowRFQFieldEdit);
    PurchRFQCaseTable_DS.object(fieldNum(PurchRFQCaseTable, AgreementClassification)).allowEdit(_allowRFQFieldEdit);

    if (purchRFQCaseTable.BidType != PurchRFQBidType::Sealed)
    {
        purchRFQCaseLine_ds.object(fieldNum(PurchRFQCaseLine, ExpiryDateTime)).allowEdit(_allowRFQFieldEdit);
    }

    // RFQ Address fasttab fields
    PurchRFQCaseTable_DS.object(fieldNum(PurchRFQCaseTable, DeliveryName)).allowEdit(_allowRFQFieldEdit);
    PurchRFQCaseTable_DS.object(fieldNum(PurchRFQCaseTable, DeliveryPostalAddress)).allowEdit(_allowRFQFieldEdit);
    LogisticsPostalAddressLine_DS.object(fieldNum(LogisticsPostalAddress, Address)).allowEdit(_allowRFQFieldEdit);

    // RFQ Price discount fasttab fields
    PurchRFQCaseTable_DS.object(fieldNum(PurchRFQCaseTable, CurrencyCode)).allowEdit(_allowRFQFieldEdit);
    PurchRFQCaseTable_DS.object(fieldNum(PurchRFQCaseTable, Payment)).allowEdit(_allowRFQFieldEdit);
    PurchRFQCaseTable_DS.object(fieldNum(PurchRFQCaseTable, PaymMode)).allowEdit(_allowRFQFieldEdit);

    // RFQ Item fasttab fields
    PurchRFQCaseLine_DS.object(fieldNum(PurchRFQCaseLine, PurchUnit)).allowEdit(_allowRFQFieldEdit);
    PurchRFQCaseLine_DS.object(fieldNum(PurchRFQCaseLine, LineNumber)).allowEdit(_allowRFQFieldEdit);
    PurchRFQCaseLine_DS.object(fieldNum(PurchRFQCaseLine, ExternalItemId)).allowEdit(_allowRFQFieldEdit);
    PurchRFQCaseLine_DS.object(fieldNum(PurchRFQCaseLine, ItemId)).allowEdit(_allowRFQFieldEdit);
    PurchRFQCaseLine_DS.object(fieldNum(PurchRFQCaseLine, CategoryId)).allowEdit(_allowRFQFieldEdit);
    PurchRFQCaseLine_DS.object(fieldNum(PurchRFQCaseLine, ItemName)).allowEdit(_allowRFQFieldEdit);
    PurchRFQCaseLine_DS.object(fieldNum(PurchRFQCaseLine, PurchQty)).allowEdit(_allowRFQFieldEdit);
    PurchRFQCaseLine_DS.object(fieldNum(PurchRFQCaseLine, PdsCWQty)).allowEdit(_allowRFQFieldEdit);

    // RFQ general fasttab fields
    PurchRFQCaseLine_DS.object(fieldNum(PurchRFQCaseLine, Name)).allowEdit(_allowRFQFieldEdit);
    PurchRFQCaseLine_DS.object(fieldNum(PurchRFQCaseLine, ExternalItemId)).allowEdit(_allowRFQFieldEdit);
    PurchRFQCaseLine_DS.object(fieldNum(PurchRFQCaseLine, DeliveryDate)).allowEdit(_allowRFQFieldEdit);

    // RFQ Address fasttab fields
    PurchRFQCaseLine_DS.object(fieldNum(PurchRFQCaseLine, DeliveryName)).allowEdit(_allowRFQFieldEdit);
    PurchRFQCaseLine_DS.object(fieldNum(PurchRFQCaseLine, DeliveryPostalAddress)).allowEdit(_allowRFQFieldEdit);

    //RFQ Project fasttab fields
    PurchRFQCaseLine_DS.object(fieldNum(PurchRFQCaseLine, PurchPrice)).allowEdit(_allowRFQFieldEdit);
    PurchRFQCaseLine_DS.object(fieldNum(PurchRFQCaseLine, LineAmount)).allowEdit(_allowRFQFieldEdit);

    // RFQ Line Price/Discount fasttab
    PurchRFQCaseLine_DS.object(fieldNum(PurchRFQCaseLine, LinePercent)).allowEdit(_allowRFQFieldEdit);
    PurchRFQCaseLine_DS.object(fieldNum(PurchRFQCaseLine, LineDisc)).allowEdit(_allowRFQFieldEdit);
    PurchRFQCaseLine_DS.object(fieldNum(PurchRFQCaseLine, MultiLnDisc)).allowEdit(_allowRFQFieldEdit);
    PurchRFQCaseLine_DS.object(fieldNum(PurchRFQCaseLine, MultiLnPercent)).allowEdit(_allowRFQFieldEdit);
    PurchRFQCaseLine_DS.object(fieldNum(PurchRFQCaseLine, PriceUnit)).allowEdit(_allowRFQFieldEdit);
    PurchRFQCaseLine_DS.object(fieldNum(PurchRFQCaseLine, PurchMarkup)).allowEdit(_allowRFQFieldEdit);

    // RFQ Inventory dimension fasttab fields: Product dimensions
    Enumerator enum = InventDim::dimProductDimFieldList().getEnumerator();
    while (enum.moveNext())
    {
        FieldId inventDimFieldId = enum.current();

        InventDim_DS.object(inventDimFieldId).allowEdit(_allowRFQFieldEdit);
    }

    // RFQ Inventory dimension fasttab fields: Site/wh (affects delivery address)
    InventDim_DS.object(fieldNum(InventDim, InventSiteId)).allowEdit(_allowRFQFieldEdit);
    InventDim_DS.object(fieldNum(InventDim, InventLocationId)).allowEdit(_allowRFQFieldEdit);

    // Setting RFQ button access for amendments functionality
    DirPartyPostalAddressNewHeader.enabled(_allowRFQFieldEdit);
    LogisticsLocationSelectHeader.enabled(_allowRFQFieldEdit);
    DirPartyPostalAddressNew.enabled(_allowRFQFieldEdit);
    PurchRFQBOMExpand.enabled(_allowRFQFieldEdit && element.createLineAllowed());

    if (_allowRFQFieldEdit)
    {
        element.setItemType(purchRFQCaseLine.LineItemType);
    }
}

private void setRFQCaseTableInternalFieldsAccess_PSN(boolean _allowEdit)
{
    purchRFQCaseTable_ds.object(fieldNum(PurchRFQCaseTable, ItemBuyerGroup)).allowEdit(_allowEdit);
    purchRFQCaseTable_ds.object(fieldNum(PurchRFQCaseTable, Requester)).allowEdit(_allowEdit);
    purchRFQCaseTable_ds.object(fieldNum(PurchRFQCaseTable, LanguageId)).allowEdit(_allowEdit);
    purchRFQCaseTable_ds.object(fieldNum(PurchRFQCaseTable, RequestingDepartment)).allowEdit(_allowEdit);
    purchRFQCaseTable_ds.object(fieldNum(PurchRFQCaseTable, SitesSvcPage)).allowEdit(_allowEdit);
    purchRFQCaseTable_ds.object(fieldNum(PurchRFQCaseTable, SitesEmailId)).allowEdit(_allowEdit);
    purchRFQCaseTable_ds.object(fieldNum(PurchRFQCaseTable, DlvMode)).allowEdit(_allowEdit);
    purchRFQCaseTable_ds.object(fieldNum(PurchRFQCaseTable, DlvTerm)).allowEdit(_allowEdit);
    purchRFQCaseTable_ds.object(fieldNum(PurchRFQCaseTable, InventLocationId)).allowEdit(_allowEdit);
    purchRFQCaseTable_ds.object(fieldNum(PurchRFQCaseTable, InventSiteId)).allowEdit(_allowEdit);
    purchRFQCaseTable_ds.object(fieldNum(PurchRFQCaseTable, DefaultDimension)).allowEdit(_allowEdit);
    DistributionTemplateHeader.allowEdit(_allowEdit);
}

void setItemType(PurchRFQLineItemType _itemType)
{
    InventDim           invDim;

    if (_itemType == PurchRFQLineItemType::Item)
    {
        purchRFQCaseLine_ds.object(fieldNum(PurchRFQCaseLine, ItemId)).allowEdit(true);
        purchRFQCaseLine_ds.object(fieldNum(PurchRFQCaseLine, CategoryId)).allowEdit(false);
        purchRFQCaseLine_ds.object(fieldNum(PurchRFQCaseLine, ItemName)).allowEdit(false);
        purchRFQCaseLine_ds.object(fieldNum(PurchRFQCaseLine, ItemId)).mandatory(true);
        purchRFQCaseLine_ds.object(fieldNum(PurchRFQCaseLine, CategoryId)).mandatory(false);
        purchRFQCaseLine_ds.object(fieldNum(PurchRFQCaseLine, ItemName)).mandatory(false);
    }
    else // Category
    {
        purchRFQCaseLine_ds.object(fieldNum(PurchRFQCaseLine, CategoryId)).allowEdit(true);
        purchRFQCaseLine_ds.object(fieldNum(PurchRFQCaseLine, ItemId)).allowEdit(false);
        purchRFQCaseLine_ds.object(fieldNum(PurchRFQCaseLine, ItemName)).allowEdit(true);
        purchRFQCaseLine_ds.object(fieldNum(PurchRFQCaseLine, CategoryId)).mandatory(true);
        purchRFQCaseLine_ds.object(fieldNum(PurchRFQCaseLine, ItemId)).mandatory(false);
        purchRFQCaseLine_ds.object(fieldNum(PurchRFQCaseLine, ItemName)).mandatory(true);
        purchRFQCaseLine.ItemId = '';
    }

    invDim = purchRFQCaseLine.inventDim();
    inventDim.InventSiteId      = invDim.InventSiteId;
    inventDim.InventLocationId  = invDim.InventLocationId;

    purchRFQCaseLine_ds.refresh();
}

void  setMandatoryAgreementOrderFields(boolean _mandatory)
{
    PurchAgreementOrderHeader.visible(_mandatory);
    purchRFQCaseTable_ds.object(fieldNum(PurchRFQCaseTable, ResponsibleWorkerId)).visible(_mandatory);
    purchRFQCaseTable_ds.object(fieldNum(PurchRFQCaseTable, ValidityDateStart)).mandatory(_mandatory);
    purchRFQCaseTable_ds.object(fieldNum(PurchRFQCaseTable, ValidityDateEnd)).mandatory(_mandatory);
    purchRFQCaseTable_ds.object(fieldNum(PurchRFQCaseTable, AgreementClassification)).mandatory(_mandatory);
}

