{
    boolean marked = TaxParameters::checkTaxParameters_IN();

    priceBasis_IN.visible(marked);
    purchRFQVendLink_SalesTaxFormTypes_IN.visible(TaxParameters::find().SalesTax_IN);
}

[Hookable(false), Wrappable(false)]
internal void showHeaderLogisticsPostalEffectiveAddressMessage()
{
    // Keep the message appearing on the order details pages (not on the list) only with lowest status Created
    if (element.viewOptionHelper().getViewOption() != FormViewOption::Grid
        && purchRFQCaseTable.StatusLow == PurchRFQStatus::Created
        && (logisticsPostalAddressHeader.RecId && !logisticsPostalAddressHeader.isEffective()))
    {
        warning("@ProcurementAndSourcing:HeaderEffectiveAddressMessage");
    }
}

[Hookable(false), Wrappable(false)]
internal void showLineLogisticsPostalEffectiveAddressMessage()
{
    // Keep the message appearing on the order details pages (not on the list) only with status Created
    if (element.viewOptionHelper().getViewOption() == FormViewOption::Details
        && (purchRFQCaseLine.RecId && purchRFQCaseLine.StatusLow == PurchRFQStatus::Created)
        && (logisticsPostalAddressLine.RecId && !logisticsPostalAddressLine.isEffective()))
    {
        warning("@ProcurementAndSourcing:LineEffectiveAddressMessage");
    }
}

void updateDesign(InventDimFormDesignUpdate mode)
{
    InventMovement movement = InventMovement::construct(purchRFQCaseLine);

    switch (mode)
    {
        case InventDimFormDesignUpdate::Init        :
            if (!inventDimFormSetup)
                inventDimFormSetup  = InventDimCtrl_Frm_Mov_PurchRFQ::newFromFormMovement(element, movement);

        case InventDimFormDesignUpdate::Active      :
            inventDimFormSetup.formActiveSetup(movement.inventDimGroupSetup(),movement);
            inventDimFormSetup.formSetControls(true);
            break;

        case InventDimFormDesignUpdate::FieldChange :
            inventDimFormSetup.formActiveSetup(movement.inventDimGroupSetup(),movement);
            inventDimFormSetup.formSetControls(false);
            break;

        default :
            // Incorrect parameter for the function %1
            throw error(strFmt("@SYS54195",funcName()));
    }
}

void updateLinks()
{
    QueryBuildDataSource    qbds;
    PurchRFQVendLink        vendLink;
    PurchRFQCaseId          rfqCaseId;

    if (purchRFQVendLinkAlternative_qr != null)
    {
        qbds = purchRFQVendLinkAlternative_qr.query().dataSourceName(identifierStr(PurchRFQVendLinkAlternative));
    }
    else
    {
        qbds = purchRFQVendLinkAlternative_ds.query().dataSourceName(identifierStr(PurchRFQVendLinkAlternative));
    }

    qbds.enabled(true);
    qbds.joinMode(JoinMode::ExistsJoin);

    if (strLTrim(vendAccountSelection))
    {
        if (!qbds.linkCount())
        {
            qbds.addLink(fieldNum(PurchRFQCaseTable, rfqCaseId), fieldNum(PurchRFQVendLink, rfqCaseId));
        }
        vendAccountRange.value(vendAccountSelection);
    }
    else
    {
        qbds.enabled(false);

        vendAccountRange.value(SysQuery::valueUnlimited());
    }
}

private void setRFQCancelledVisibility()
{
    CanceledRFQGroup.visible(this.purchRFQCaseTable().isRFQCanceled());
    Cancel_CanceledPurchReqId.visible(this.purchRFQCaseTable().CanceledPurchReqId != "");
}

void initRange()
{
    QueryBuildDataSource    qbds;
    Common                  rec;

    // Accessing query build data source...
    qbds = this.query().dataSourceTable(tableNum(PurchRFQCaseTable));

    // Find or create needed ranges...
    itemBuyerGroupRange = SysQuery::findOrCreateRange(qbds,fieldNum(PurchRFQCaseTable, ItemBuyerGroup));
    statusRange = SysQuery::findOrCreateRange(qbds,fieldNum(PurchRFQCaseTable, StatusLow));
    statusSelection = PurchRFQStatusFilter::All;

    rec = element.args().record();
    switch (rec.TableId)
    {
        case tableNum(ProjTable):
            projectRange = SysQuery::findOrCreateRange(qbds, fieldNum(PurchRFQCaseTable, ProjId));
            break;
        case tableNum(InventTrans):
            if  (element.args().menuItemName() == menuitemDisplayStr(PurchRFQCaseLine))
            {
                rfqCaseIdRange = SysQuery::findOrCreateRange(qbds, fieldNum(PurchRFQCaseTable, rfqCaseId));
            }
            break;
    }
}

public void linkActive()
{
    QueryBuildRange                 projIdPurchRFQTable;
    QueryBuildDataSource            purchRFQTableDS;

    ProjTable                       projTableLink;
    ProjInvoiceTable                projInvoiceTableLink;
    ProjWIPTable                    projWIPTable;
    InventTransOrigin               inventTransOrigin;
    InventTrans                     inventTrans;

    // Initialize QueryBuildDataSource as table PurchRFQCaseTable.
    purchRFQTableDS = purchRFQCaseTable_ds.query().dataSourceTable(tableNum(PurchRFQCaseTable));

    switch (element.args().dataset())
    {
        case tableNum(ProjTable)        :
            projTableLink = element.args().record();
            projectRange.value(projTableLink.ProjId+'*');
            break;
        case tableNum(ProjInvoiceTable):
            projIdPurchRFQTable  = purchRFQTableDS.findRange(fieldNum(PurchRFQCaseTable, ProjId));
            while (projIdPurchRFQTable)
            {
                purchRFQTableDS.clearRange(fieldNum(PurchRFQCaseTable,ProjId));
                projIdPurchRFQTable = purchRFQTableDS.findRange(fieldNum(PurchRFQCaseTable, ProjId));
            }
            projInvoiceTableLink = element.args().record();
            while select ProjId from projTableLink
                where projTableLink.ProjInvoiceProjId   == projInvoiceTableLink.ProjInvoiceProjId
            {
                purchRFQTableDS.addRange(fieldNum(PurchRFQCaseTable,ProjId)).value(projTableLink.ProjId);
            }
            break;
        case tableNum(ProjWIPTable):
            projIdPurchRFQTable = purchRFQTableDS.findRange(fieldNum(PurchRFQCaseTable,ProjId));
            while (projIdPurchRFQTable)
            {
                purchRFQTableDS.clearRange(fieldNum(PurchRFQCaseTable,ProjId));
                projIdPurchRFQTable  = purchRFQTableDS.findRange(fieldNum(PurchRFQCaseTable,ProjId));
            }
            projWIPTable = element.args().record();
            while select ProjId from projTableLink
                where projTableLink.wipProject  == projWIPTable.ProjId
            {
                purchRFQTableDS.addRange(fieldNum(PurchRFQCaseTable,ProjId)).value(projTableLink.ProjId);
            }
            break;
        case tableNum(InventTrans):
            inventTrans = element.args().record();
            if  (element.args().menuItemName() == menuitemDisplayStr(PurchRFQCaseLine))
            {
                rfqCaseIdRange.value(queryValue(inventTrans.inventTransOrigin().ReferenceId));
            }
            else
            {
                rfqCaseIdRange.value('');
            }
            break;
        case tableNum(InventTransOrigin):
            inventTransOrigin = element.args().record();
            if  (element.args().menuItemName() == menuitemDisplayStr(PurchRFQCaseLine))
            {
                rfqCaseIdRange.value(queryValue(inventTransOrigin.ReferenceId));
            }
            else
            {
                rfqCaseIdRange.value('');
            }
            break;
    }

    super();
}

public void reread()
{
    super();

    element.setFieldsAccess();
}

public boolean validateWrite()
{
    boolean ret;

    ret = super();

    if (ret)
    {
        purchRFQTableMap2LineUpdatePrompt = PurchRFQTableMap2LineUpdatePrompt::construct();
        purchRFQTableMap2LineUpdatePrompt.purchRFQTableMap(purchRFQCaseTable);
        if (!purchRFQTableMap2LineUpdatePrompt.prompt())
            ret = false;
    }

    return ret;
}

void  reReadLines(boolean _refreshVendLink = false)
{
    purchRFQCaseLine_ds.executeQuery();

    if (_refreshVendLink)
    {
        purchRFQVendLink_ds.executeQuery();
    }
}

public int  active()
{
    int ret;

    element.setFieldsAccess();

    purchRFQCaseTable_ds.rereadReferenceDataSources();
    element.showHeaderLogisticsPostalEffectiveAddressMessage();

    ret = super();

    element.setMandatoryAgreementOrderFields(purchRFQCaseTable.rfqType == PurchRFQType::PurchAgreement);

    PurchRFQCaseTable currentRecord = this.cursor() as PurchRFQCaseTable;
    AmendmentStatus.visible(currentRecord.isAmended);

    return ret;
}

public void executeQuery()
{
    itemBuyerGroupRange.value(itemBuyerGroupSelection);

    statusRange.value(statusSelection != PurchRFQStatusFilter::All ? int2str(statusSelection) : SysQuery::valueUnlimited());

    logisticsPostalAddressHeader_ds.validTimeStateUpdate(ValidTimeStateUpdate::Correction);
    logisticsPostalAddressHeader_ds.query().validTimeStateDateTimeRange(DateTimeUtil::minValue(), DateTimeUtil::maxValue());

    super();
}

void  create(boolean  append = true)
{
    PurchRFQCaseTable   newPurchRFQCaseTable;

    if (PurchRFQCaseTable.RecId == 0)
    {
        PurchRFQCaseTable_ds.leaveRecord();
    }

    if (purchRFQCaseTableForm.create())
    {
        newPurchRFQCaseTable = PurchRFQCaseTable::find(purchRFQCaseTableForm.newRFQCaseId());

        if (newPurchRFQCaseTable)
        {
            super(append);
            purchRFQCaseTable.data(newPurchRFQCaseTable);
            element.setMandatoryAgreementOrderFields(purchRFQCaseTable.rfqType == PurchRFQType::PurchAgreement);

            purchRFQCaseTable_ds.write();
            // For the newly created rfq case ensure the lines ds is refreshed so it doesn't contain lines from a previous RFQ
            PurchRFQCaseLine_ds.research();
            if (listVendorsFromVendorSearch
                && listVendorsFromVendorSearch.elements() > 0)
            {
                element.addVendorsFromVendorSearch(listVendorsFromVendorSearch, false);
                listVendorsFromVendorSearch = null;
            }
            LineViewLines.setFocus();
        }
    }
    else
    {
        // if cancel button is clicked while creating the new request for quote from request for quote details form then
        // the request for quote details form should not be closed other wise it has to be closed.
        if (element.args().caller() &&
            element.args().parmEnum() == FormOpenMode::ForNew)
        {
            element.close();
        }
    }
}

void  write()
{
    DialogButton            copyAddress     = DialogButton::No;
    boolean                 convertCurrency = false;
    boolean                 revertCurrencyCodeChanges = false;
    TradeCurencyConversion  tradeCurencyConversion;

    if (purchRFQCaseTable.checkUpdate())
    {
        if (PurchRFQCaseLine::exist(purchRFQCaseTable.rfqCaseId) || MarkupTrans::exist(purchRFQCaseTable.TableId, purchRFQCaseTable.RecId))
        {
            if (purchRFQCaseTable.CurrencyCode != purchRFQCaseTable.orig().CurrencyCode)
            {
                [convertCurrency, tradeCurencyConversion] = PurchRFQCaseTable::promptConvertCurrencyCode();

                // If user has cancelled currency conversion
                // signal that currency code changes roll-back is required.
                if (!convertCurrency)
                    revertCurrencyCodeChanges = true;
            }
        }

        if (PurchRFQCaseLine::exist(purchRFQCaseTable.rfqCaseId))
        {
            if (purchRFQCaseTable.DeliveryPostalAddress != purchRFQCaseTable.orig().DeliveryPostalAddress)
                copyAddress = PurchRFQCaseTable::promptCopyDeliveryAddress();
        }

        ttsbegin;

        if (copyAddress != DialogButton::Cancel  &&  copyAddress != DialogButton::No)
            purchRFQCaseTable.copyAddressToLine(copyAddress, purchRFQCaseTable);

        if (revertCurrencyCodeChanges)
            purchRFQCaseTable.CurrencyCode = purchRFQCaseTable.orig().CurrencyCode;
        else if (convertCurrency)
            purchRFQCaseTable.convertCurrencyCode(purchRFQCaseTable.CurrencyCode, tradeCurencyConversion);

        super();

        if (purchRFQTableMap2LineUpdatePrompt)
        {
            purchRFQTableMap2LineUpdatePrompt.runOperation();
            purchRFQTableMap2LineUpdatePrompt = null;
        }

        ttscommit;
    }
    else
    {
        super();
    }

    purchRFQCaseTable_ds.reread();
    purchRFQCaseTable_ds.refresh();
}

void  delete()
{
    if (purchRFQCaseTable.checkDelete())
    {
        super();
        element.setButtonAccessHeader();

        if (!purchRFQCaseTable.RecId)
        {
            purchRFQCaseLine_ds.allowCreate(false);
        }
    }
}

/// <summary>
/// Performs a custom lookup on the <c>AgreementClassification</c> table.
/// </summary>
/// <param name="_formReferenceControl">
/// The form control on which the lookup will be performed.
/// </param>
/// <returns>
/// A record of the type <c>AgreementClassification</c> selected by the user.
/// </returns>
public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    AgreementClassificationLookup agreementClassificationLookup =
        AgreementClassificationLookup::newAgreementClassificationLookup(tablenum(PurchAgreementHeader));

    return agreementClassificationLookup.lookupAgreementClassification(_formReferenceControl);
}

public void modified()
{
    super();

    purchRFQCaseLine_ds.object(fieldNum(PurchRFQCaseLine, ExpiryDateTime)).allowEdit(purchRFQCaseTable.BidType != PurchRFQBidType::Sealed);

    PurchRFQCaseLine_ds.reread();
}

public boolean validate()
{
    boolean ret;

    ret = super();

    if (BrazilParameters::isEnabled())
    {
        ret = ret
            && element.canChangeDeliveryPostalAddress_BR()
            && CFOPValidation_BR::validateDeliveryAddress(
                InventSite::find(purchRFQCaseTable.InventSiteId).fiscalEstablishment(),
                purchRFQCaseTable.DeliveryPostalAddress);
    }
    return ret;
}

