{
    super();

    element.showHeaderLogisticsPostalEffectiveAddressMessage();
}

public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    Common ret;
    CompanyInfo companyInfo = CompanyInfo::find();

    ret = LogisticsLocationSelectionLookup::lookupAddressForm(_formReferenceControl,
        companyInfo,
        [LogisticsLocationRoleType::Delivery],
        false,
        LogisticsLocationSelectionLookup::transactionAssociatedAddresses(
                                              tableNum(PurchRFQCaseLine),
                                              fieldNum(PurchRFQCaseLine, DeliveryPostalAddress),
                                              fieldNum(PurchRFQCaseLine, rfqCaseId),
                                              purchRFQCaseTable.rfqCaseId,
                                              purchRFQCaseTable.DeliveryPostalAddress),
        purchRFQCaseTable);
    return ret;
}

