void initRange()
{
    purchRFQCaseLine_ds.query().dataSourceTable(tableNum(PurchRFQCaseLine))
        .addRange(fieldNum(PurchRFQCaseLine, ToBeDeleted))
        .value(SysQuery::value(NoYes::No));
}

public void reread()
{
    InventDimId invDimId = purchRFQCaseLine.InventDimId;
    super();
    if (purchRFQCaseLine.InventDimId != invDimId)
        this.research();
}

public void executeQuery()
{
    logisticsPostalAddressLine_ds.validTimeStateUpdate(ValidTimeStateUpdate::Correction);
    logisticsPostalAddressLine_ds.query().validTimeStateDateTimeRange(DateTimeUtil::minValue(), DateTimeUtil::maxValue());

    super();
    element.setButtonAccessLine();
}

public void itemIdModified()
{
    purchRFQCaseLine.itemIdChanged();
    inventDim.data(InventDim::find(purchRFQCaseLine.InventDimId));
    InventMovement::bufferSetTransQtyUnit(purchRFQCaseLine);

    if (purchRFQCaseLine.ProjId)
    {
        element.projInventJournalTransMapForm().fieldModifiedItemIdPost();
    }

    element.updateDesign(InventDimFormDesignUpdate::FieldChange);

    if (formCtrl)
    {
        formCtrl.fieldModified(fieldNum(PurchRFQCaseLine, ItemId));
    }

    element.findProductNumber();
}

        public int  active()
        {
            int     ret;

            element.setFieldsAccess();

            ret = super();
            purchRFQCaseLine_ds.rereadReferenceDataSources();
            element.showLineLogisticsPostalEffectiveAddressMessage();

            if (formCtrl)
            {
                // Enables CW qty field if item is CW item; otherwise enables PurchQty field
                formCtrl.dataSourceActivePost();

                // In case of amendment need to restrict Qty field access after above call which sets enablement based on CW item or not
                if (isRFQAmendmentEnabled)
                {
                    // Lock quantity fields if RFQ is Sent or published, and not under amendment
                    boolean lockFields = ( (purchRFQCaseTableForm.isRFQSent(purchRFQCaseTable) || purchRFQPublishTable_PSN.RecId)
                                            && purchRFQCaseTable.isAmended == NoYes::No );

                    if (lockFields)
                    {
                        PurchRFQCaseLine_DS.object(fieldNum(PurchRFQCaseLine, PurchQty)).allowEdit(false);
						PurchRFQCaseLine_DS.object(fieldNum(PurchRFQCaseLine, PdsCWQty)).allowEdit(false);
                    }
                }
            }

            return ret;
        }

void  linkActive()
{
    super();

    purchRFQCaseLine_ds.executeQuery();
}

public boolean validateWrite()
{
    boolean ret;

    purchRFQCaseLine.InventDimId   = InventDim::findOrCreate(inventDim).InventDimId;

    ret = super();

    return ret;
}

void  create(boolean append = true)
{
    if (purchRFQCaseTable.checkUpdate())
    {
        if (!purchRFQCaseLine.RecId)
        {
            element.setEditItemTypeAllowed(true);
        }

        purchRFQCaseTableForm.purchRFQCaseLine_PreSuperCreate(purchRFQCaseLine);

        super(append);
        purchRFQCaseTableForm.purchRFQCaseLine_Create(purchRFQCaseLine);
        element.setItemType(purchRFQCaseLine.LineItemType);

        if (BrazilParameters::isEnabled())
        {
            purchRFQCaseLine_ItemId.setFocus();
        }
        else
        {
            purchRFQCaseLine_LineItemType.setFocus();
        }
    }
}

void  delete()
{
    if (element.deleteLineAllowed())
    {
        super();

        purchRFQCaseTable_ds.reread();
        purchRFQCaseTable_ds.refresh();
        element.setButtonAccessLine();
    }
}

void  write()
{
    if (purchRFQCaseTable.checkUpdate())
    {
        purchRFQCaseLine.InventDimId = InventDim::findOrCreate(inventDim).InventDimId;

        InventMovement::bufferSetRemainQty(purchRFQCaseLine);

        purchRFQCaseTableForm.purchRFQCaseLine_PreSuperWrite(purchRFQCaseLine);

        super();

        if (purchRFQCaseLine.InventDimId != inventDim.InventDimId)
        {
            inventDim.data(InventDim::find(purchRFQCaseLine.InventDimId));
            inventDim_ds.setCurrent();
        }

        purchRFQCaseTable_ds.reread();
        purchRFQCaseTable_ds.refresh();
    }
    else
    {
        purchRFQCaseLine_ds.reread();
        purchRFQCaseTable_ds.reread();
    }

}

void initValue()
{
    if (purchRFQCaseTable)
    {
        purchRFQCaseLine.initFromPurchRFQCaseTable(purchRFQCaseTable);
        purchRFQCaseLine.LineItemType = PurchRFQLineItemType::Item;
    }

    super();
}

