        {
            element.modifyInventDimField(fieldNum(InventDim, InventDimension6));
        }
        else
        {
            purchRFQCaseLine.PurchRFQLineMap::modifyInventDim(inventDim, fieldNum(InventDim, InventDimension6));
        }
    }
}

public boolean validate()
{
    boolean ret = super();

    if (ret
        && purchRFQCaseLine.ItemId
        && purchRFQCaseTableCustomDimFlight)
    {
        ret = purchRFQCaseLine.PurchRFQLineMap::validateModifyInventDim(inventDim, fieldNum(InventDim, InventDimension7));
    }

    return ret;
}

