    {
        if (InventDim::isFieldIdProductDimension(fieldNum(InventDim, InventDimension7)))
        {
            element.modifyInventDimField(fieldNum(InventDim, InventDimension7));
        }
        else
        {
            purchRFQCaseLine.PurchRFQLineMap::modifyInventDim(inventDim, fieldNum(InventDim, InventDimension7));
        }
    }
}

