public int active()
{
    int ret;

    ret = super();

    if (purchRFQVendLink.statusHigh() > PurchRFQStatus::Created)
    {
        purchRFQVendLink_ds.allowDelete(false);
        if (purchRFQVendLink.statusHigh() > PurchRFQStatus::Sent)
        {
            purchRFQVendLink_ds.allowEdit(false);
        }
        else
        {
            purchRFQVendLink_ds.allowEdit(true);
            purchRFQVendLink_ds.object(fieldNum(PurchRFQVendLink, VendAccount)).allowEdit(false);
        }
    }
    else
    {
        purchRFQVendLink_ds.allowDelete(true);
        purchRFQVendLink_ds.allowEdit(true);
        purchRFQVendLink_ds.object(fieldNum(PurchRFQVendLink, VendAccount)).allowEdit(true);
    }

    return ret;
}

public void init()
{
    super();

    this.cacheAddMethod(tableMethodStr(PurchRFQVendLink, statusLow));
    this.cacheAddMethod(tableMethodStr(PurchRFQVendLink, statusHigh));
}

public void delete()
{
    super();

    purchRFQCaseTable_ds.reread();
    purchRFQCaseTable_ds.refresh();
    purchRFQCaseTable_ds.reReadLines();
    element.updateLinks();
}

public void write()
{
    super();

    purchRFQCaseTable_ds.reread();
    purchRFQCaseTable_ds.refresh();
    purchRFQCaseTable_ds.reReadLines();

    purchRFQVendLink_ds.reread();
    PurchRFQVendLink_ds.refresh();

    element.updateLinks();
}

public void linkActive()
{
    PurchRFQCaseLine    purchRFQCaseLineLocal;

    super();

    select firstonly RecId from purchRFQCaseLineLocal
        where purchRFQCaseLineLocal.rfqCaseId == purchRFQCaseTable.rfqCaseId &&
              purchRFQCaseLineLocal.StatusHigh != PurchRFQStatus::Accepted;

    //if all items on an RFQ Case have been accepted from any of the vendors then
    //it should not allow new vendors to be added to the RFQ Case
    if (purchRFQCaseTable.existLine())
    {
        purchRFQVendLink_ds.allowCreate(purchRFQCaseLineLocal.RecId != 0);
    }
    else
    {
        purchRFQVendLink_ds.allowCreate(true);
    }
}

public void create(boolean _append = false)
{
    super(_append);

    purchRFQVendLink.rfqCaseId = purchRFQCaseTable.rfqCaseId;
}

