    return true;
}

public void init()
{
    QueryBuildDataSource    qbds;

    super();

    qbds = this.query().dataSourceName('PurchRFQTable');

    qbds.addLink(fieldNum(PurchRFQVendLink, rfqCaseId),fieldNum(PurchRFQTable, rfqCaseId));
    qbds.addLink(fieldNum(PurchRFQVendLink, VendAccount),fieldNum(PurchRFQTable, VendAccount));
}

public void modified()
{
    super();
    if (BrazilParameters::isEnabled())
    {
        pUrchRFQTable_ds.rereadReferenceDataSources();
    }
}

public void executeQuery()
{
    element.updateLinks();

    // Avoid following call when form has been called first time from the list page
    if (!skipSecondExecuteQueryForLPCall)
        purchRFQCaseTable_ds.executeQuery();
    else
        skipSecondExecuteQueryForLPCall = false;
}

void initRange()
{
    QueryBuildDataSource qbds = this.query().dataSourceName(identifierStr(PurchRFQVendLinkAlternative));

    vendAccountRange = SysQuery::findOrCreateRange(qbds, fieldNum(PurchRFQVendLink, VendAccount));
    vendAccountRange.status(RangeStatus::Locked);
}

