        public void clicked()
        {
			PurchRFQCaseTable purchRFQCaseTablelocal = PurchRFQCaseTable::find(purchRFQCaseTable.RFQCaseId);

			if (purchRFQCaseTablelocal && purchRFQCaseTablelocal.type().mayRFQBeSent())
            {
                super();
            }
            else
			{
                PurchRFQCaseTableForm::publishRFQ(purchRFQCaseTablelocal, element);
			}
        }

