/// <summary>
/// Calls the print management setup form.
/// </summary>
void clicked()
{
    PrintMgmtSetupContext setupContext = new PrintMgmtSetupContext();

    if (purchRFQVendLink_ds.cursor() == null)
        return;

    super();

    setupContext.addHierarchyContext(PrintMgmtHierarchyType::Purch, PrintMgmtNodeType::PurchRFQVendLink);
    setupContext.parmCaller(element);
    setupContext.parmReferencedTableBuffer(purchRFQVendLink_ds.cursor());
    setupContext.parmCountryRegionId(purchRFQVendLink.vendTable().postalAddress().CountryRegionId);
    PrintMgmt::launchSetup(setupContext);
}

