/// <summary>
/// Launches the <c>BOMExpandPurchRFQ</c> form to expand the BOM of the current line in the lines grid.
/// </summary>
/// <exception cref="Exception::Error">
/// If the item does not have a BOM, the error message "Item cannot have a bill of materials" will be displayed.
/// </exception>
void  clicked()
{
    Args            args = new Args();
    FormRun         bomExpandPurchRFQ;

    if (!purchRFQCaseLine.inventTable().inventItemType().canHaveBOM())
        throw error("@SYS2069"); // Item cannot have a bill of materials.

    args.name(formStr(BOMExpandPurchRFQ));
    args.caller(this);
    args.record(purchRFQCaseLine);

    // Menu item info will be used by security framework, when setting access rights to form datasources
    args.menuItemType(MenuItemType::Display);
    args.menuItemName(menuitemDisplayStr(PurchRFQBOMExpand));

    bomExpandPurchRFQ = classfactory.formRunClass(args);

    bomExpandPurchRFQ.run();
    bomExpandPurchRFQ.wait();

    purchRFQCaseLine_ds.executeQuery();
}

void clicked()
{
    if (!purchRFQCaseLine.inventTable().inventItemType().canHaveBOM())
        throw error("@SYS2069"); // Item cannot have a bill of materials.

    super();
}

void clicked()
{
    super();
    purchRFQCaseLine_ds.research(true);
}

/// <summary>
/// Enable or disable buttons in the lines grid, based on type of line item, and type of RFQ.
/// </summary>
void clicked()
{
    boolean inventButtonsEnabled = (purchRFQCaseLine.LineItemType == PurchRFQLineItemType::Item &&
                                    purchRFQCaseLine.RecId) ? true : false ;

    buttonLineTransact.enabled(inventButtonsEnabled && purchRFQCaseTable.rfqType == PurchRFQType::Purch);
    buttonLineOnHand.enabled(inventButtonsEnabled);
    buttonLineLot.enabled(inventButtonsEnabled && purchRFQCaseTable.rfqType == PurchRFQType::Purch);
    inventDimTracking.enabled(inventButtonsEnabled && purchRFQCaseTable.rfqType == PurchRFQType::Purch);

    super();
}

