public boolean modified()
{
    boolean ret = super();

    PurchRFQVendViewQuestion.visible(VendorQuestion_IsVendorQuestionEnabled.checked());
    VendorQuestion_VendorQuestionCutoffDateTime.allowEdit(VendorQuestion_IsVendorQuestionEnabled.checked());

    if (!VendorQuestion_IsVendorQuestionEnabled.checked())
    {
        purchRFQCaseTable.VendorQuestionCutoffDateTime = utcDateTimeNull();
    }

    purchRFQCaseTable_ds.refresh();

    return ret;
}

void clicked()
{
    if (PurchRFQAddVendors::autoPopulateVendors(PurchRFQCaseTable))
    {
        purchRFQVendLink_ds.research();
        purchRFQVendLink_ds.refresh();
    }
    else
    {
        warning("@SYS4009995");
    }
}

