{
    if (purchRFQVendLink.VendAccount)
        ContactPerson::lookupVendContactPerson(purchRFQVendLink_ContactPersonId,
                                               purchRFQVendLink.VendAccount,
                                               this,
                                               purchRFQVendLink.ContactPersonId);
}

