[SysObsolete('This method is obsolete, please use method this.validateSealedReplies() instead', false, 12\09\2023)]
private void checkAllBidsAreSealed(PurchRFQCaseId _caseId)
{
    PurchRFQTable   purchRFQTableCheck;

    select firstonly RecId from purchRFQTableCheck
        where purchRFQTableCheck.RFQCaseId == _caseId
            && purchRFQTableCheck.BidType == PurchRFQBidType::Sealed;

    if(purchRFQTableCheck.RecId)
    {
        if (isNotSealedBid)
        {
            PurchRFQSealedReplyTable purchRFQSealedReplyTable;
            PurchRFQSealedReplyLine purchRFQSealedReplyLine;
            PurchRFQReplyLine purchRFQReplyLine;

            select firstonly RecId from purchRFQSealedReplyTable
            exists join  purchRFQTableCheck
                where purchRFQTableCheck.RFQCaseId == _caseId
                    && purchRFQTableCheck.RecId == purchRFQSealedReplyTable.PurchRFQReplyTable;

            if (purchRFQSealedReplyTable.RecId)
            {
                warning("@SCM:PurchRFQCompareSealedAndUnsealedBids");
            }
            else
            {
                select firstonly RecId from purchRFQSealedReplyLine
                exists join purchRFQReplyLine
                    where purchRFQReplyLine.RecId == purchRFQSealedReplyLine.PurchRFQReplyLine
                exists join  purchRFQTableCheck
                    where purchRFQTableCheck.RFQCaseId == _caseId
                        && purchRFQTableCheck.RFQId == purchRFQReplyLine.RFQId;

                if (purchRFQSealedReplyLine.RecId)
                {
                    warning("@SCM:PurchRFQCompareSealedAndUnsealedBids");
                }
            }
        }
        else
        {
            info("@SCM:PurchRFQCompareSealedBids");
        }
    }

}

private void validateSealedReplies(purchRFQCaseTable _rfqCaseTable)
{
    if (_rfqCaseTable.RecId && _rfqCaseTable.BidType == PurchRFQBidType::Sealed)
    {
        if (isNotSealedBid)
        {
            PurchRFQSealedReplyTable purchRFQSealedReplyTable;
            PurchRFQSealedReplyLine purchRFQSealedReplyLine;

            PurchRFQReplyTable purchRFQReplyTableLocal;
            PurchRFQTable purchRFQTableLocal;

            select firstonly RecId from purchRFQSealedReplyTable
                exists join purchRFQReplyTableLocal
                    where purchRFQReplyTableLocal.RecId == purchRFQSealedReplyTable.PurchRFQReplyTable
                exists join purchRFQTableLocal
                    where purchRFQTableLocal.RFQId == purchRFQReplyTableLocal.RFQId
                       && purchRFQTableLocal.RFQCaseId == _rfqCaseTable.RFQCaseId;

            if (!purchRFQSealedReplyTable.RecId)
            {
                PurchRFQReplyLine purchRFQReplyLineLocal;

                select firstonly RecId from purchRFQSealedReplyLine
                exists join purchRFQReplyLineLocal
                    where purchRFQReplyLineLocal.RecId == purchRFQSealedReplyLine.PurchRFQReplyLine
                exists join purchRFQTableLocal
                    where purchRFQTableLocal.RFQCaseId == _rfqCaseTable.RFQCaseId
                       && purchRFQTableLocal.RFQId == purchRFQReplyLineLocal.RFQId;
            }

            if (purchRFQSealedReplyTable.RecId || purchRFQSealedReplyLine.RecId)
            {
                warning("@SCM:PurchRFQCompareSealedAndUnsealedBids");
            }
        }
        else
        {
            info("@SCM:PurchRFQCompareSealedBids");
        }
    }

}

/// <summary>
/// Retrieves RFQ replies for the <c>PurchRFQCaseTable</c> record and load it into
/// the <c>PurchRFQTmpCompareRFQReply</c> tables.
/// </summary>
void buildTMPRecords()
{
    List                replyLineList;
    List                replyTableList;
    ListEnumerator      tableEnumeretor;
    ListEnumerator      lineEnumerator;
    container           rfqReplyList;
    CurrencyCode        companyCurrency;

    rfqReplyList = purchRFQCompare.getRFQReplies();

    replyTableList = List::create(conPeek(rfqReplyList,1));
    replyLineList  = List::create(conPeek(rfqReplyList,2));

    companyCurrency = conPeek(rfqReplyList,3);

    tableEnumeretor = replyTableList.getEnumerator();
    while (tableEnumeretor.moveNext())
    {
        purchRFQTmpCompareRFQReply.data(tableEnumeretor.current());
        if (purchRFQTmpCompareRFQReply.CurrencyCode != companyCurrency)
        {
            nonCompanyCurrency = true;
        }
        purchRFQTmpCompareRFQReply.insert();
    }

    lineEnumerator = replyLineList.getEnumerator();
    while (lineEnumerator.moveNext())
    {
        purchRFQTmpCompareRFQReply.data(lineEnumerator.current());
        purchRFQTmpCompareRFQReply.insert();
    }
}

