/// <summary>
/// Narrows the results of the <c>PurchRFQTmpCompareRFQReply</c> query adding
/// filter conditions based on the user selection
/// </summary>
public void filterPurchRFQTmpCompareRFQReply()
{
    NoYes localIncludeRejected     = ctrlIncludeRejected.value();
    NoYes localIncludeAccepted     = ctrlIncludeAccepted.value();
    NoYes localIncludeNotAnswered  = ctrlIncludeNotAnswered.value();
    NoYes localIncludeDeclined     = ctrlIncludeDeclined.value();

    includeReceived =NoYes::Yes;
    this.setComboBoxShowAccess();
    this.setCheckBoxAccess();

    // show all records that has the rejected status
    if (((localIncludeRejected != includeRejected) && purchRFQTmpCompareRFQReply.existRecord(PurchRFQStatus::Rejected)) || linesStatusUpdated)
    {
        purchRFQTmpCompareRFQReply.setShowRecord(PurchRFQStatus::Rejected, ctrlIncludeRejected.value());
        includeRejected = localIncludeRejected;
    }

    // show all records that have the accepted status
    if (((localIncludeAccepted != includeAccepted) && purchRFQTmpCompareRFQReply.existRecord(PurchRFQStatus::Accepted)) || linesStatusUpdated)
    {
        purchRFQTmpCompareRFQReply.setShowRecord(PurchRFQStatus::Accepted, ctrlIncludeAccepted.value());
        includeAccepted = localIncludeAccepted;
    }

    // show all records that have the sent status
    if (((localIncludeNotAnswered != includeNotAnswered) && purchRFQTmpCompareRFQReply.existRecord(PurchRFQStatus::Sent)) || linesStatusUpdated)
    {
        purchRFQTmpCompareRFQReply.setShowRecord(PurchRFQStatus::Sent, ctrlIncludeNotAnswered.value());
        includeNotAnswered = localIncludeNotAnswered;
    }

    // show all records that have the declined status
    if (((localIncludeDeclined != includeDeclined) && purchRFQTmpCompareRFQReply.existRecord(PurchRFQStatus::Declined)) || linesStatusUpdated)
    {
        purchRFQTmpCompareRFQReply.setShowRecord(PurchRFQStatus::Declined,ctrlIncludeDeclined.value());
        includeDeclined = localIncludeDeclined;
    }

    // by default show all records that have the received status
    if (includeReceived || linesStatusUpdated)
    {
        purchRFQTmpCompareRFQReply.setShowRecord(PurchRFQStatus::Received, NoYes::Yes);
        includeReceived = NoYes::No;
    }

    purchRFQTmpCompareRFQReply.filterShowedRecordByAmount(ComboBoxShow.selection());

    qbr.value(enum2str(NoYes::Yes));
    linesStatusUpdated = NoYes::No;
}

/// <summary>
/// Initializes the form before it is painted.
/// </summary>
/// <exception cref="Exception::Error">
/// If the form was not called using either the <c>PurchRFQCaseTable</c> or <c>PurchRFQTable</c> tables as record buffer,
/// an exception "Form was called incorrectly" will be thrown.
/// </exception>
public void init()
{
    PurchRFQCaseTable           purchRFQCaseTable;
    PurchRFQTable               localPurchRFQTable;
    PurchRFQCaseId              caseId;

    super();
    this.registerSystemDefinedButtonEvents();

    if (element.args().caller() && element.args().record().TableId == tableNum(PurchRFQCaseTable))
    {
        purchRFQCaseTable = element.args().record();
        caseId = purchRFQCaseTable.rfqCaseId;
    }
    else if (element.args().caller() && element.args().record().TableId == tableNum(PurchRFQTable))
    {
        localPurchRFQTable = element.args().record();
        caseId = localPurchRFQTable.rfqCaseId;
        purchRFQCaseTable = PurchRFQCaseTable::find(caseId);
    }
    else
    {
        throw error("@SYS22996"); // Form was called incorrectly.
    }

    isNotSealedBid      = !purchRFQCaseTable.IsSealed;
    doAlternatesExist   = purchRFQCaseTable.doAlternatesExist();

    purchRFQCompare = PurchRFQCompare::newCaseId(caseId);

    element.buildTMPRecords();

    element.updateDesign(InventDimFormDesignUpdate::Init);

    if (PurchRFQCompareValidateSealedRepliesFlight::instance().isEnabled())
    {
        element.validateSealedReplies(purchRFQCaseTable);
    }
    else
    {
        element.checkAllBidsAreSealed(caseId);
    }

    includeReceived     = NoYes::Yes;
    linesStatusUpdated  = NoYes::No;

    if (!PurchRFQParameters::canMarkAndAcceptMultipleLines())
    {
        this.setActionPaneButtonAccess(false);
    }
    {
        PurchRFQTmpCompareRFQReply_Status.visible(true);
    }

    this.setComboBoxShowAccess();
  }

