}

void openMenuItem(str _Name, MenuItemType _type = MenuItemType::Display, xArgs _args = purchRFQTmpCompareRFQReply_ds.purchRFQTableRecordArgs())
{
    if (PurchRFQParameters::canMarkAndAcceptMultipleLines() && this.hasProcessedLine() && !multiSelectInfoHasDisplayed)
    {
        info("@SCM:MarkAndProcessMultipleLinesInfo");
        multiSelectInfoHasDisplayed = true;
    }
    else
    {
        new MenuFunction(_Name, _type).run(_args);
    }
}

private void registerSystemDefinedButtonEvents()
{
    #SysSystemDefinedButtons

    FormCommandButtonControl    attachButton;

    attachButton = this.control(this.controlId(#SystemDefinedAttachButton)) as FormCommandButtonControl;
    attachButton.registerOverrideMethod(methodStr(FormButtonControl, clicked), formmethodstr(PurchRFQCompare, attachButtonClicked), this);
}

public void setActionPaneButtonAccess(boolean _gridIsNotEmpty)
{
    PurchRFQTable       rfqTable            = purchRFQTmpCompareRFQReply.findPurchRFQTable();
    PurchRFQCaseTable   purchRFQCaseTable   = purchRFQTmpCompareRFQReply.findPurchRFQCaseTable();
    boolean             lineTypeHeader      = (purchRFQTmpCompareRFQReply.LineType == PurchRFQCompareLineType::Header)? true :false;

    boolean isCanceledRFQ = rfqTable.StatusLow == PurchRFQStatus::Canceled && rfqTable.StatusHigh == PurchRFQStatus::Canceled;
    boolean gridIsNotEmptyAndRFQIsNotCanceled   = _gridIsNotEmpty && !isCanceledRFQ;

    boolean isRFQCaseAcceptRejectAllowed    = gridIsNotEmptyAndRFQIsNotCanceled && purchRFQCaseTable.isAcceptRejectAllowed();

    returnMenuItemButton.enabled(gridIsNotEmptyAndRFQIsNotCanceled && lineTypeHeader &&
                                  (rfqTable.StatusLow == PurchRFQStatus::Received || rfqTable.StatusHigh == PurchRFQStatus::Received));
    acceptMenuItemButton.enabled(isRFQCaseAcceptRejectAllowed);
    rejectMenuItemButton.enabled(isRFQCaseAcceptRejectAllowed);
    totalsMenuItemButton.enabled(gridIsNotEmptyAndRFQIsNotCanceled && lineTypeHeader);
    manageMiscChargesMenuItemButton.enabled(gridIsNotEmptyAndRFQIsNotCanceled && lineTypeHeader);
    rfqJournalMenuItemButton.enabled(gridIsNotEmptyAndRFQIsNotCanceled);
    printMgmtButton.enabled(gridIsNotEmptyAndRFQIsNotCanceled);
    rfqMenuItemButton.enabled(gridIsNotEmptyAndRFQIsNotCanceled);
    poMenuItemButton.enabled(gridIsNotEmptyAndRFQIsNotCanceled && rfqTable.PurchId);
    tradeAgreementsButtonGroup.enabled(gridIsNotEmptyAndRFQIsNotCanceled);
}

void setCheckBoxAccess()
{
    if (ComboBoxShow.selection()!=PurchRFQReplySearchFilter::All)
    {
        ctrlIncludeDeclined.allowEdit(false);
        ctrlIncludeNotAnswered.allowEdit(false);
    }
    else
    {
        ctrlIncludeDeclined.allowEdit(true);
        ctrlIncludeNotAnswered.allowEdit(true);
    }
}

void setComboBoxShowAccess()
{
    if (ctrlIncludeDeclined.value() || ctrlIncludeNotAnswered.value())
    {
        ComboBoxShow.selection(PurchRFQReplySearchFilter::All);
        ComboBoxShow.allowEdit(false);
    }
    else
    {
        ComboBoxShow.allowEdit(true);
    }
}

void updateDesign(InventDimFormDesignUpdate mode)
{
    InventMovement  movement            = InventMovement::construct(PurchRFQLine::findRecId(purchRFQTmpCompareRFQReply.PurchRFQLine));

    switch (mode)
    {
        case InventDimFormDesignUpdate::Init        :
            if (!inventDimFormSetup)
                inventDimFormSetup  = InventDimCtrl_Frm_Mov_PurchRFQ::newFromFormMovement(element, movement);

        case InventDimFormDesignUpdate::Active      :
            inventDimFormSetup.formActiveSetup(movement.inventDimGroupSetup(),movement);
            inventDimFormSetup.formSetControls(true);
            break;

        case InventDimFormDesignUpdate::FieldChange :
            inventDimFormSetup.formActiveSetup(movement.inventDimGroupSetup(),movement);
            inventDimFormSetup.formSetControls(false);
            break;

        default : throw error(strFmt("@SYS54195",funcName()));
    }

    PurchRFQTmpCompareRFQReply_IsAlternate.visible(doAlternatesExist);

    // Show information message about alternate lines.
    if (doAlternatesExist)
    {
        warning("@EPR4460254");
    }
}

private boolean hasMarkedLine()
{
    PurchRFQTmpCompareRFQReply lines;
    lines.setTmpData(purchRFQTmpCompareRFQReply);

    select firstonly RecId from lines
        where lines.RFQMark == NoYes::Yes 
        && lines.ShowRecord == NoYes::Yes
        && lines.LineType == PurchRFQCompareLineType::Line
        && lines.Status < PurchRFQStatus::Rejected;

    return lines.RecId;
}

private boolean hasProcessedLine()
{
    PurchRFQTmpCompareRFQReply lines;
    lines.setTmpData(purchRFQTmpCompareRFQReply);

    select firstonly RecId from lines
        where lines.RFQMark == NoYes::Yes
        && lines.ShowRecord == NoYes::Yes
        && lines.LineType == PurchRFQCompareLineType::Line
        && lines.Status > PurchRFQStatus::Received;

    return lines.RecId;
}

