public void updateStatus(PurchRFQId _rfqId = "")
{
    PurchRFQTmpCompareRFQReply  lines;
    PurchRFQLine                rfqLines;
    PurchRFQTable               rfqTable;

    lines.setTmpData(purchRFQTmpCompareRFQReply);

    if (_rfqId)
    {
        update_recordSet lines
        setting Status = rfqLines.Status, StatusHigh = rfqTable.StatusHigh, StatusLow = rfqTable.StatusLow
        join rfqLines
            where rfqLines.RecId == lines.PurchRFQLine && rfqLines.RFQId == _rfqId
                join rfqTable
                    where rfqTable.RFQId == _rfqId;
    }
    else
    {
        update_recordSet lines
        setting Status = rfqLines.Status, StatusHigh = rfqTable.StatusHigh, StatusLow = rfqTable.StatusLow
        join rfqLines
            where rfqLines.RecId == lines.PurchRFQLine && rfqLines.RFQId == lines.RFQId
                join rfqTable
                    where rfqTable.RFQId == lines.RFQId;
    }

    linesStatusUpdated = NoYes::Yes;
    this.executeQuery();
}

