public int active()
{
    int     ret;
    boolean allowEdit;

    ret = super();

    allowEdit = purchRFQTmpCompareRFQReply.LineType == PurchRFQCompareLineType::Header &&
                purchRFQTmpCompareRFQReply.StatusHigh < PurchRFQStatus::Rejected;

    if (!PurchRFQParameters::canMarkAndAcceptMultipleLines())
    {
        element.setActionPaneButtonAccess(purchRFQTmpCompareRFQReply.RecId);
    }
    rfqRank.allowEdit(allowEdit);

    return ret;
}

public void init()
{
    QueryBuildDataSource qbds;

    super();

    qbds = this.query().dataSourceTable(tableNum(PurchRFQTmpCompareRFQReply));
    qbds.addOrderByField(fieldNum(PurchRFQTmpCompareRFQReply,RFQId), SortOrder::Ascending);
    qbds.addOrderByField(fieldNum(PurchRFQTmpCompareRFQReply,RFQCaseLineLineNumber), SortOrder::Ascending);

    qbds.clearDynalinks();
    qbds.clearRanges();

    qbr = qbds.addRange(fieldNum(PurchRFQTmpCompareRFQReply, ShowRecord));
}

public void executeQuery()
{
    element.filterPurchRFQTmpCompareRFQReply();

    super();

    if (PurchRFQParameters::canMarkAndAcceptMultipleLines())
    {
        element.setActionPaneButtonAccess(element.hasMarkedLine());
    }
}

