    }
}

public void modified()
{
    int     position = purchRFQTmpCompareRFQReply_ds.getPosition();
    boolean headerRFQMardkBeforeSuper, headerRFQMardkAfterSuper;
    boolean   rfqMarkDelta = purchRFQTmpCompareRFQReply.orig().RFQMark != purchRFQTmpCompareRFQReply.RFQMark;

    if (rfqMarkDelta)
    {
        if (PurchRFQParameters::canMarkAndAcceptMultipleLines())
        {
            multiSelectInfoHasDisplayed = false;
        }
        headerRFQMardkBeforeSuper = purchRFQTmpCompareRFQReply.isHeaderRFQMarked(purchRFQTmpCompareRFQReply.RFQId);

        super();
        purchRFQTmpCompareRFQReply.update();

        headerRFQMardkAfterSuper = purchRFQTmpCompareRFQReply.isHeaderRFQMarked(purchRFQTmpCompareRFQReply.RFQId);

        if (purchRFQTmpCompareRFQReply.LineType == PurchRFQCompareLineType::Line && headerRFQMardkBeforeSuper == headerRFQMardkAfterSuper)
        {
            purchRFQTmpCompareRFQReply_ds.reread();
            purchRFQTmpCompareRFQReply_ds.refresh();
        }
        else
        {
            purchRFQTmpCompareRFQReply_ds.research();
        }

        purchRFQTmpCompareRFQReply_ds.setPosition(position);
    }
}

