/// <summary>
/// Validates if the form can close, when the OK button is clicked.
/// </summary>
/// <returns>
/// True if form can close; otherwise false.
/// </returns>
boolean  canClose()
{
    boolean ret = super();

    if (ret && this.closedOk() && !purchRFQFormLetter.checkCloseDialog())
    {
        purchRFQParmUpdate_ds.executeQuery();
        return false;
    }

    return ret;
}

void  chooseTable()
{
    purchRFQParmTable_ds.executeQuery();
}

void  chooseTrans()
{
    purchRFQParmLine_ds.executeQuery();
}

void closeOk()
{
    QueryRun    qr;
    PurchRFQParmTable   tempPurchRFQParmTable;
    boolean isOk = true;

    if (purchRFQParmUpdate)
        purchRFQParmUpdate_ds.write();

    if (purchRFQParmTable.RecId)
        purchRFQParmTable_ds.write();
    else if (PurchSendRFQEditLinesZeroQtyWarningFlight::instance().isEnabled())
    {
        throw warning("@SYS26185");
    }

    if (purchRFQParmLine)
        purchRFQParmLine_ds.write();

    purchRFQFormLetter.initParameters(purchRFQParmUpdate,
                                      printout,
                                      printFormLetter,
                                      usePrintManagement,
                                      printReplySheet,
                                      printPriceAmount);

    if (isConfigurationkeyEnabled(configurationKeyNum(LedgerAdv2BudgetCtrl)))
    {
        qr = purchRFQParmTable_DS.queryRun();
        qr.reset();
        while (qr.next())
        {
            tempPurchRFQParmTable = qr.get(tableNum(PurchRFQParmTable));
            PurchRFQTable purchRFQTable = PurchRFQTable::find(tempPurchRFQParmTable.rfqId);
            if (purchRFQTable.rfqType == PurchRFQType::Purch
                || purchRFQTable.rfqType == PurchRFQType::PurchAgreement)
            {
                if (!purchRFQFormLetter.validatePOTransdate(tempPurchRFQParmTable.poTransDate))
                {
                    isOk = false;
                    purchRFQParmTable_ds.positionToRecord(tempPurchRFQParmTable);
                    break;
                }
            }
        }
    }

    if (isOk && InventDistinctProductValidator::checkProductNotStopped(purchRFQParmLine.ItemId, InventItemOrderSetupType::Purch, InventDim::find(purchRFQParmLine.InventDimId), false))
    {
        super();

        if (purchRFQEditLinesForm is PurchRFQEditLinesForm_Send
            && purchRFQFormLetter is PurchRFQFormLetter_Send)
        {
            PurchRFQFormLetter_Send purchRFQFormLetter_Send = purchRFQFormLetter as PurchRFQFormLetter_Send;
            purchRFQFormLetter_Send.parmCanSend(Send.value());

            // If publish check box is checked, then publish the RFQ case
            if (isConfigurationkeyEnabled(configurationKeyNum(VendorCollaboration_PSN)) && Publish.value())
            {
                PurchRFQCasePublish_PSN::startPublish(purchRFQCaseTable, element);
            }
        }
    }
}

