void  init()
{
    ParmId  parmId;

    super();

    if (!element.args().caller())
         throw error("@SYS22539");

    purchRFQFormLetter      = element.args().caller();
    parmId                  = purchRFQFormLetter.parmId();
    purchRFQEditLinesForm   = PurchRFQEditLinesForm::newPurchRFQEditLinesForm(purchRFQFormLetter.purchRFQUpdateType());

    purchRFQEditLinesForm.parmPurchRFQFormLetter(purchRFQFormLetter);
    purchRFQParmUpdate_ds.query().dataSourceTable(tableNum(PurchRFQParmUpdate)).addRange(fieldNum(PurchRFQParmUpdate, ParmId)).value(queryValue(parmId));
    purchRFQParmTable_ds.query().dataSourceTable(tableNum(PurchRFQParmTable)).addRange(fieldNum(PurchRFQParmTable, ParmId)).value(queryValue(parmId));
    element.updateDesign(InventDimFormDesignUpdate::Init);
    this.batch(purchRFQFormLetter.canGoBatch());

    if (isConfigurationkeyEnabled(configurationKeyNum(LedgerAdv2BudgetCtrl)))
    {
        if (purchRFQEditLinesForm.poTransDate())
        {
            purchRFQParmTable_ds.object(fieldNum(PurchRFQParmTable,poTransDate)).allowEdit(true);
            purchRFQParmTable_ds.object(fieldNum(PurchRFQParmTable,poTransDate)).visible(true);
        }
        else
        {
            purchRFQParmTable_ds.object(fieldNum(PurchRFQParmTable,poTransDate)).visible(false);
        }
    }

    if (isConfigurationkeyEnabled(configurationKeyNum(VendorCollaboration_PSN))
        && purchRFQEditLinesForm is PurchRFQEditLinesForm_Send
        && purchRFQFormLetter is PurchRFQFormLetter_Send)
    {
        SendPublishGroup.visible(true);
        purchRFQCaseTable = purchRFQFormLetter.parmSourceTable();

        Publish.enabled(!PurchRFQPublishTable_PSN::isRFQCasePublised(purchRFQCaseTable.RecId));
    }

    purchRFQParmUpdate.SpecQty      = PurchRFQUpdate::All;
}

void initQuery()
{
    if (!purchRFQFormLetter.chooseLinesQuery())
    {
        return;
    }

    Query query = purchRFQFormLetter.chooseLinesQuery().query();

    if (query)
    {
        const int maxControls = 10;
        query.showInForm(LateSelectionGroup, maxControls);           
    }

    purchRFQFormLetter.chooseLinesQuery().query(query);

    purchRFQEditLinesParameters.enabled(false);
    purchRFQEditLinesPrintOptions.enabled(false);
    purchRFQEditLinesRecalculationOptions.enabled(false);
}

void initSpecQtySelection()
{
    if (purchRFQParmLine_ds.numberOfRowsLoaded() > 0)
    {
        // There are some Marked lines already, so initialize the Selection to be Marked
        purchRFQParmUpdate.SpecQty = PurchRFQUpdate::Marked;
    }
    else
    {
        // No Marked lines, so initialize the Selection to be All and reselect
        purchRFQParmUpdate.SpecQty = PurchRFQUpdate::All;
        purchRFQParmUpdate_ds.object(fieldNum(PurchRFQParmUpdate, SpecQty)).modified();
    }
}

