/// <summary>
/// Paints the form, and sets default form field values and properties.
/// </summary>
void  run()
{
    super();

    this.design().caption(purchRFQEditLinesForm.caption());

    if (purchRFQParmUpdate.ParmId)
    {
        if (purchRFQParmUpdate.LateSelection)
        {
            if (purchRFQParmUpdate.LateSelectionQuery)
                purchRFQFormLetter.chooseLinesQuery(new QueryRun(purchRFQParmUpdate.LateSelectionQuery));

            element.initQuery();
        }

        chooseButton.visible(purchRFQEditLinesForm.chooseButton());

        purchRFQParmUpdate_ds.object(fieldNum(PurchRFQParmUpdate, LateSelection)).visible(purchRFQEditLinesForm.lateSelection());
        purchRFQParmUpdate_ds.object(fieldNum(PurchRFQParmUpdate, SpecQty)).visible(purchRFQEditLinesForm.specQty());
        purchRFQParmUpdate_ds.object(fieldNum(PurchRFQParmUpdate, ReasonId)).visible(purchRFQEditLinesForm.reasonCode());
        purchRFQParmUpdate_ds.object(fieldNum(PurchRFQParmUpdate, ReasonComment)).visible(purchRFQEditLinesForm.reasonCode());

        purchRFQEditLinesRecalculationOptions.visible(purchRFQEditLinesForm.recalculation());

        printout            =   purchRFQFormLetter.printout();
        printFormLetter     =   purchRFQFormLetter.printFormLetter();
        usePrintManagement  =   purchRFQFormLetter.usePrintManagement();
        printReplySheet     =   purchRFQFormLetter.printReplySheet();
        printPriceAmount    =   purchRFQFormLetter.printPriceAmount();

        this.updatePrintOut(purchRFQFormLetter.printerSettingsFormletter(PrintSetupOriginalCopy::Original));

        purchRFQParmTable_ds.object(fieldNum(PurchRFQParmTable, rfqCaseId)).visible(purchRFQEditLinesForm.rfqCaseId());
        purchRFQParmTable_ds.object(fieldNum(PurchRFQParmTable, rfqId)).visible(!purchRFQEditLinesForm.rfqCaseId());
        purchRFQParmUpdate_ds.object(fieldNum(PurchRFQParmUpdate, LineStatusFilter)).visible(purchRFQEditLinesForm.lineStatusFilter());

        // bulk send mode
        gridLine.visible(purchRFQEditLinesForm.showLinesGrid());
        window_Handle.visible(purchRFQEditLinesForm.showLinesGrid());
        deleteButton.visible(purchRFQEditLinesForm.showLinesGrid());
        purchRFQEditLinesParameters.visible(purchRFQEditLinesForm.showLinesGrid());
    }
}

