/// <summary>
/// Sets the parameters section, from the <c>PurchRFQEditLinesParameters</c> form.
/// </summary>
/// <param name="_specQty">
/// An enum, to specify if records of type sent, received, marked or all are to be processed.
/// </param>
/// <param name="_lineStatusFilter">
/// An enum, to specificy if records with a status of created, sent, received, rejected, accepted, or all records are to be processed.
/// </param>
/// <param name="_reasonId">
/// The reason ID if the current action.
/// </param>
/// <param name="_reasonComment">
/// A comment for the current reason.
/// </param>
/// <param name="_lateSelection">
/// A boolean to indicate if late records also have to be selected.
/// </param>
public void setExternalParameters(PurchRFQUpdate _specQty,PurchRFQStatusFilter _lineStatusFilter,PurchRFQReasonId _reasonId,
                                  ReasonComment  _reasonComment,LateSelection _lateSelection)
{
    boolean  specQtyUpdate              = false;
    boolean  lineStatusFilterUpdated    = false;

    purchRFQParmUpdate.ReasonId         = _reasonId;
    purchRFQParmUpdate.ReasonComment    = _reasonComment;

    purchRFQParmUpdate.LateSelection    = _lateSelection;
    lateSelectionGroup.visible(purchRFQParmUpdate.LateSelection);

    purchRFQParmUpdate_DS.write();

    if  (purchRFQParmUpdate.SpecQty != _specQty)
    {
        purchRFQParmUpdate.SpecQty      = _specQty;
        specQtyUpdate = true;
    }

    if (purchRFQParmUpdate.LineStatusFilter != _lineStatusFilter)
    {
        purchRFQParmUpdate.LineStatusFilter = _lineStatusFilter;
        lineStatusFilterUpdated = true;
    }

    if (specQtyUpdate || lineStatusFilterUpdated )
    {
        if (lineStatusFilterUpdated)
        {
            purchRFQFormLetter.reSelect(purchRFQParmUpdate.SpecQty, purchRFQParmUpdate.LineStatusFilter);
        }
        else
        {
            purchRFQFormLetter.reSelect(purchRFQParmUpdate.SpecQty);
        }

        this.chooseTable();
    }
}

