public int task(int _taskId)
{
    #task
    int ret;

    if (_taskId == #taskEsc)
    {
        _taskId = #taskCtrlQ;
    }

    ret = super(_taskId);
    return ret;
}

void updateDesign(InventDimFormDesignUpdate mode)
{
    switch (mode)
    {
        case InventDimFormDesignUpdate::Init :
            if (!inventDimFormSetup)
            {
                inventDimFormSetup  = InventDimCtrl_Frm_PurchRFQ::newFromForm(element);
            }

        case InventDimFormDesignUpdate::Active :
            inventDimFormSetup.formActiveSetup(InventDimGroupSetup::newItemId(purchRFQParmLine.ItemId));
            inventDimFormSetup.formSetControls(true);
            break;

        default : throw error(strFmt("@SYS54195",funcName()));
    }
}

void updatePrintOut(container _printerSettings)
{
    if (printout == Printout::Current &&
            FormLetter::isClientOutput(_printerSettings))
    {
        printout = Printout::After;
    }
}

public void setButtonEnabled()
{
    if (isConfigurationkeyEnabled(configurationKeyNum(VendorCollaboration_PSN)))
    {
        boolean enable = Send.value() || Publish.value();

        Ok.enabled(enable);
        ButtonBatch.enabled(enable);
    }
}

public void init()
{
    super();
    purchRFQParmTable_ds.cacheAddMethod(tableMethodStr(PurchRFQParmTable, checkIfLines));
}

public int active()
{
    int ret;

    ret = super();

    if (ret && PurchRFQTable::find(purchRFQParmTable.rfqId).rfqType == PurchRFQType::PurchReq)
    {
        purchRFQParmTable_ds.object(fieldNum(PurchRFQParmTable,poTransDate)).allowEdit(false);
    }
    else
    {
        purchRFQParmTable_ds.object(fieldNum(PurchRFQParmTable,poTransDate)).allowEdit(true);
    }

    purchRFQParmTable_ds.object(fieldNum(PurchRFQParmTable, PublishToSites)).allowEdit(purchRFQParmTable.PublishToSites);

    if  (!parametersCalled)
    {
        purchRFQParmUpdate.SpecQty      = PurchRFQUpdate::All;
        element.chooseTrans();
    }

    return ret;
}

public void init()
{
    super();

    this.query().dataSourceTable(tableNum(purchRFQParmLine)).addOrderByField(fieldNum(purchRFQParmLine,RFQCaseLineLineNumber), SortOrder::Ascending);
}

public void delete()
{
    PurchRFQId  rfqId;
    ParmId      parmId;

    super();

    rfqId = purchRFQParmLine.rfqId;
    parmId = purchRFQParmLine.ParmId;

    if ( rfqId && parmId)
    {
        PurchRFQParmSubTable::deleteIfNoParmLines(rfqId,parmId);
        purchRFQParmSubTable_ds.research();
    }
}

public void init()
{
    super();

    purchRFQParmUpdate.LineStatusFilter = PurchRFQStatusFilter::All;
}

public void executeQuery()
{
    PurchRFQTable  purchRFQTable;
    ReasonTableRef reasonTableRef;

    super();

    if (purchRFQParmTable.rfqId)
    {
        purchRFQTable = PurchRFQTable::find(purchRFQParmTable.rfqId);

        if ( purchRFQTable.RecId != 0 && purchRFQTable.ReasonRef)
        {
            reasonTableRef = ReasonTableRef::find(purchRFQTable.ReasonRef);
        }
    }

    purchRFQParmUpdate.ReasonId      = reasonTableRef.Reason;
    purchRFQParmUpdate.ReasonComment = ReasonTableRef.ReasonComment;
}

public void modified()
{
    super();

    purchRFQFormLetter.purchRFQParmUpdate(purchRFQParmUpdate);

    if (purchRFQFormLetter.lateSelectionRemoveLines())
    {
        purchRFQParmTable_ds.executeQuery();

        lateSelectionGroup.visible(purchRFQParmUpdate.LateSelection);
    }
}

public void modified()
{
    super();

    if (reselectNeeded)  //don't reselect very first time. data should be OK at this point.
    {
        purchRFQFormLetter.reSelect(purchRFQParmUpdate.SpecQty, purchRFQParmUpdate.LineStatusFilter);
    }
    reselectNeeded = true;
    element.chooseTable();
}

public void modified()
{
    super();

    purchRFQFormLetter.reSelect(purchRFQParmUpdate.SpecQty);
    element.chooseTable();
}

void clicked()
{
    purchRFQParmLine_ds.delete();
}

/// <summary>
/// Calls the <c>PurchRFQFormLetter</c> functions, to launch the <c>SysQueryForm</c> dialog.
/// </summary>
void clicked()
{
    super();

    if (!purchRFQFormLetter.lateSelectionRemoveLines())
        return;

    purchRFQFormLetter.showQueryForm(true);
    purchRFQFormLetter.initLinesQuery();

    if (purchRFQParmUpdate.LateSelection)
    {
        purchRFQParmUpdate.LateSelectionQuery = purchRFQFormLetter.chooseLinesQuery().query().pack();
        element.initQuery();
    }
    else
    {
        purchRFQParmUpdate.LateSelection = false;
    }
    element.chooseTable();

    if (! purchRFQFormLetter.lateSelectionRemoveLines())
        return;

    purchRFQFormLetter.initParameters(purchRFQParmUpdate,
                                      printout,
                                      printFormLetter,
                                      usePrintManagement,
                                      printReplySheet,
                                      printPriceAmount);
}

public void gotFocus()
{
    str newHelpText;

    super();

    newHelpText = (purchRFQParmLine.RecId) ? '' : "@SYS26185";
    window_Handle.helpText(newHelpText);
}

