[Form]
public class PurchRFQPublishClosedCase_PSN extends FormRun implements SysIFilterConsumerForm, SysIFilterEventHandler
{
    private const int RelativeMonthFrom = -12;


}

/// <summary>
/// Register the data source to which the filter should be applied.
/// </summary>
public void registerDatasourceOnQueryingEvent()
{
    PurchRFQCaseTable_DS.OnQueryExecuting += eventhandler(this.parmFilterFormQueryEventHandler().applyFilter);
}

/// <summary>
/// Event handler to react on a filter change.
/// </summary>
public void onFilterChanged()
{
    PurchRFQCaseTable_DS.executeQuery();
}

    public void init()
    {
		super();

        element.hideSystemDefinedControls();

        var queryPurchRFQCaseTable = PurchRFQCaseTable_ds.queryBuildDataSource();
        var queryFilterPurchRfqCaseTable = PurchRFQCaseTable_ds.query();

        queryFilterPurchRfqCaseTable.addQueryFilter(queryPurchRFQCaseTable, fieldstr(PurchRFQCaseTable, AllowBidByInvitationOnly));
        queryFilterPurchRfqCaseTable.addQueryFilter(queryPurchRFQCaseTable, fieldstr(PurchRFQCaseTable, SolicitationType));
        queryFilterPurchRfqCaseTable.addQueryFilter(queryPurchRFQCaseTable, fieldstr(PurchRFQCaseTable, ExpiryDateTime));
    }

/// <summary>
/// Hides the system defined attachment and office control on the form.
/// </summary>
private void hideSystemDefinedControls()
{
    #SysSystemDefinedButtons

    FormCommandButtonControl attachButton = this.control(this.controlId(#SystemDefinedAttachButton)) as FormCommandButtonControl;
    if (attachButton)
    {
        attachButton.visible(false);
    }

    FormMenuButtonControl officeButton = this.control(this.controlId(#SystemDefinedOfficeButton)) as FormMenuButtonControl;
    if (officeButton)
    {
        officeButton.visible(false);
    }
}

    /// <summary>
    /// Applies the results view assigned to the RFQ case to filter the controls on the detail view fasttabs.
    /// </summary>
    /// <param name = "_rfqCloseVisibilityType">The visibility type for the closed RFQ case.</param>
    private void applyResultsViewType(PurchRFQCloseVisibilityType_PSN _rfqCloseVisibilityType)
    {
		// Controls in the results summary tab
        TotalAmount.visible(_rfqCloseVisibilityType.IsNetAmountVisible);
        TotalScore.visible(_rfqCloseVisibilityType.IsTotalScoreVisible);
        ReplyStatus.visible(_rfqCloseVisibilityType.IsPartialCompleteReplyVisible);
        ReplyComment.visible(_rfqCloseVisibilityType.IsCommentsOnReplyStatusVisible);
        Awarded.visible(_rfqCloseVisibilityType.IsAwardedVisible);

		// Controls in the results lines tab
        LineAmount.visible(_rfqCloseVisibilityType.IsNetAmountVisible);
        LineComment.visible(_rfqCloseVisibilityType.IsReplyLineCommentsVisible);
        WorkingDays.visible(_rfqCloseVisibilityType.IsWorkingDaysVisible);
        LeadTime.visible(_rfqCloseVisibilityType.IsLeadTimeVisible);
        PurchQty.visible(_rfqCloseVisibilityType.IsQuantityVisible);

        PurchPrice.visible(_rfqCloseVisibilityType.IsUnitPriceVisible);
        MultiLineDiscount.visible(_rfqCloseVisibilityType.IsMultilineDiscountVisible);
        MultiLineDiscountPercentage.visible(_rfqCloseVisibilityType.IsMultilineDiscountPercentVisible);
        LineDiscount.visible(_rfqCloseVisibilityType.IsDiscountVisible);
        LineDiscountPercentage.visible(_rfqCloseVisibilityType.IsDiscountPercentVisible);

        IsAwarded.visible(_rfqCloseVisibilityType.IsAwardedVisible);
        EffectiveDate.visible(_rfqCloseVisibilityType.IsEffectiveDateVisible);
        ExpirationDateTime.visible(_rfqCloseVisibilityType.IsExpirationDateVisible);
        PurchMarkup.visible(_rfqCloseVisibilityType.IsChargesOnPurchaseVisible);

        // Controls in the line details tab
        PurchRFQReplyDetailsView_PSN_PurchPrice.visible(_rfqCloseVisibilityType.IsUnitPriceVisible);
        PurchRFQReplyDetailsView_PSN_PurchQty.visible(_rfqCloseVisibilityType.IsQuantityVisible);
        PurchRFQReplyDetailsView_PSN_PurchMarkup.visible(_rfqCloseVisibilityType.IsChargesOnPurchaseVisible);
        PurchRFQReplyDetailsView_PSN_LineAmount.visible(_rfqCloseVisibilityType.IsNetAmountVisible);
        PurchRFQReplyDetailsView_PSN_LineDisc.visible(_rfqCloseVisibilityType.IsDiscountVisible);
        PurchRFQReplyDetailsView_PSN_LinePercent.visible(_rfqCloseVisibilityType.IsDiscountPercentVisible);
        PurchRFQReplyDetailsView_PSN_MultiLnDisc.visible(_rfqCloseVisibilityType.IsMultilineDiscountVisible);
        PurchRFQReplyDetailsView_PSN_MultiLnPercent.visible(_rfqCloseVisibilityType.IsMultilineDiscountPercentVisible);
        PurchaseDocumentId.visible(_rfqCloseVisibilityType.isConsumingDocumentIDVisible);

        RequestPurchPrice.visible(_rfqCloseVisibilityType.IsUnitPriceVisible);
        RequestPurchQty.visible(_rfqCloseVisibilityType.IsQuantityVisible);
        RequestPurchMarkup.visible(_rfqCloseVisibilityType.IsChargesOnPurchaseVisible);
        RequestLineAmount.visible(_rfqCloseVisibilityType.IsNetAmountVisible);
        RequestLineDisc.visible(_rfqCloseVisibilityType.IsDiscountVisible);
        RequestLinePercent.visible(_rfqCloseVisibilityType.IsDiscountPercentVisible);
        RequestMultiLnDisc.visible(_rfqCloseVisibilityType.IsMultilineDiscountVisible);
        RequestMultiLnPercent.visible(_rfqCloseVisibilityType.IsMultilineDiscountPercentVisible);
    }

    /// <summary>
    /// Determines the visibility of controls on the details view fast tabs based on the RFQ case visibility view type .
    /// </summary>
    private void showOrHideConfigurableControls()
    {
        if (purchRFQCaseTable.RFQCloseVisibilityType)
        {		 
            PurchRFQCloseVisibilityType_PSN rfqCloseVisibilityType = PurchRFQCloseVisibilityType_PSN::find(purchRFQCaseTable.RFQCloseVisibilityType);

            element.applyResultsViewType(rfqCloseVisibilityType);
        }
        else
		{
            element.setDefaultToConfigurableControls();
		}

    }

/// <summary>
