        public void init()
        {
            super();

            var purchRFQCaseTableDS = this.query().dataSourceTable(this.table());

			// Display only the closed published RFQ cases within last 12 months on the form.
            QueryBuildRange dateRange = SysQuery::findOrCreateRange(purchRFQCaseTableDS, fieldNum(PurchRFQCaseTable, ExpiryDateTime));
            dateRange.status(RangeStatus::Locked);
            dateRange.value(SysQueryRangeUtil::value(DateTimeUtil::addMonths(DateTimeUtil::utcNow(), RelativeMonthFrom)) + '..' + SysQueryRangeUtil::value(DateTimeUtil::utcNow()));
        }

        public int active()
        {
            int	ret = super();

            if (ret)
            {
                element.showOrHideConfigurableControls();
			}

            return ret;
        }

        /// <summary>
        /// Gets all reply summary for the selected RFQ case.
        /// </summary>
        public void executeQuery()
        {
            QueryBuildRange caseRange = SysQuery::findOrCreateRange(this.queryBuildDataSource(), fieldNum(PurchRFQAllReplySummaryView_PSN, RFQCaseId));

			caseRange.status(RangeStatus::Locked);
			caseRange.value(purchRFQCaseTable.RFQCaseId);

			super();
         }

        /// <summary>
        /// Gets all reply details for the selected RFQ case.
        /// </summary>
        public void executeQuery()
        {
            QueryBuildRange caseRange = SysQuery::findOrCreateRange(this.queryBuildDataSource(), fieldNum(PurchRFQAllReplyDetailView_PSN, RFQCaseId));

            caseRange.status(RangeStatus::Locked);
            caseRange.value(purchRFQCaseTable.RFQCaseId);

			super();
        }
