public void customizeMenuOptions(OfficeMenuOptions _menuOptions)
{
    List exportOptions = _menuOptions.customMenuItems();

    var exportOption = OfficeGeneratedExportMenuItem::construct(tableStr(PurchRequestForQuotationReplyHeaderEntity), ExportReplyHeaderAndLinesToExcelOptionId);
    exportOption.displayName(strfmt("@SYS76681", "@SCM:PurchRequestForQuotationReplyHeaderAndLines", '', curExt()));
    exportOptions.addEnd(exportOption);
}

public ExportToExcelDataEntityContext getDataEntityContext(OfficeGeneratedExportMenuItem _menuItem)
{
    ExportToExcelDataEntityContext context = null;

    switch (_menuItem.id())
    {
        case ExportReplyHeaderAndLinesToExcelOptionId:
            context = ExportToExcelDataEntityContext::constructDefault(tableStr(PurchRequestForQuotationReplyHeaderEntity));

            var headerEntityInfo = context.findEntityByName(tableStr(PurchRequestForQuotationReplyHeaderEntity));

            var headerFilterBuilder = new ExportToExcelFilterTreeBuilder(tablestr(PurchRequestForQuotationReplyHeaderEntity));
            var headerRFQIdFilter = headerFilterBuilder.areEqual(fieldStr(PurchRequestForQuotationReplyHeaderEntity, RFQNumber), PurchRFQTable.RFQId);
            headerEntityInfo.filter(headerFilterBuilder.and(headerFilterBuilder.companyFilter(), headerRFQIdFilter));

            var lineEntityInfo = PurchRequestForQuotationReplyLineV2EntityFlight::instance().isEnabled() ? 
                        context.addEntityDefault(tableStr(PurchRequestForQuotationReplyLineV2Entity)) :
                        context.addEntityDefault(tableStr(PurchRequestForQuotationReplyLineEntity));

            var lineFilterBuilder = new ExportToExcelFilterTreeBuilder(tablestr(PurchRequestForQuotationReplyLineEntity));
            var lineRFQIdFilter = lineFilterBuilder.areEqual(fieldStr(PurchRequestForQuotationReplyLineEntity, RFQNumber), PurchRFQTable.RFQId);
            lineEntityInfo.filter(lineFilterBuilder.and(lineFilterBuilder.companyFilter(), lineRFQIdFilter));

            var questionnaireEntityInfo = context.addEntityDefault(tableStr(PurchRequestForQuotationReplyQuestionnaireEntity));

            var questionnaireFilterBuilder = new ExportToExcelFilterTreeBuilder(tablestr(PurchRequestForQuotationReplyQuestionnaireEntity));
            var questionnaireRFQIdFilter = questionnaireFilterBuilder.areEqual(fieldStr(PurchRequestForQuotationReplyQuestionnaireEntity, RFQNumber), PurchRFQTable.RFQId);
            questionnaireEntityInfo.filter(questionnaireFilterBuilder.and(questionnaireFilterBuilder.companyFilter(), questionnaireRFQIdFilter));

            var scoringCriterionEntityInfo = context.addEntityDefault(tableStr(PurchRequestForQuotationReplyScoringCriterionEntity));

            var scoringCriterionFilterBuilder = new ExportToExcelFilterTreeBuilder(tablestr(PurchRequestForQuotationReplyScoringCriterionEntity));
            var scoringCriterionRFQIdFilter = scoringCriterionFilterBuilder.areEqual(fieldStr(PurchRequestForQuotationReplyScoringCriterionEntity, RFQNumber), PurchRFQTable.RFQId);
            scoringCriterionEntityInfo.filter(scoringCriterionFilterBuilder.and(scoringCriterionFilterBuilder.companyFilter(), scoringCriterionRFQIdFilter));

            break;
    }

    return context;
}

public boolean canChangeDeliveryPostalAddress_BR()
{
    if (BrazilParameters::isEnabled()
        && !purchRFQTable.InventSiteId)
    {
        return checkFailed("@SYS4000725");
    }
    return true;
}

boolean canProceed()
{
    if ((purchRFQTable.rfqType == PurchRFQType::PurchAgreement) && purchRFQTable.existCategoryBasedLine())
    {
        // Note, as agreement purchase order lines of type category are amount based,
        // the subsequent agreement lines will be created on an amount basis.
        // Click OK to continue or select Cancel.
        return (Box::okCancel("@SYS303011", DialogButton::Ok) == DialogButton::Ok);
    }

    return true;
}

public void deleteMarkedRecords()
{
    PurchRFQLine        purchRFQLineLocal;
    PurchRFQReplyLine   purchRFQReplyLineLocal;

    // Deletes all marked RFQ lines having status < received
    if (purchRFQLine_ds.validateDelete())
    {
        ttsbegin;
        while select forupdate RecId from purchRFQLineLocal
            join RecId from purchRFQReplyLineLocal
                where purchRFQLineLocal.rfqId == purchRFQTable.rfqId &&
                      purchRFQLineLocal.rfqId == purchRFQReplyLineLocal.rfqId &&
                      purchRFQLineLocal.RecId == purchRFQReplyLineLocal.rfqLineRecId &&
                      purchRFQLineLocal.Status < PurchRFQStatus::Received &&
                      purchRFQReplyLineLocal.rfqMark == NoYes::Yes
        {
            purchRFQLineLocal.delete();
        }
        ttscommit;
    }
    purchRFQLine_ds.executeQuery();
}

public Common docCursor()
{
    Common docCursor;

    docCursor = super();

    if (docCursor.TableId == tableNum(InventDim))
    {
        docCursor = purchRFQLine;
    }

    return docCursor;
}

/// <summary>
/// Rereads data from involved form data sources and refreshes displayed information.
/// </summary>
/// <remarks>
/// Should be called when displayed data sets where updated from outside the form.
/// </remarks>
void doRefresh()
{
    purchRFQTable_ds.reread();
    purchRFQTable_ds.refresh();

    purchRFQLine_ds.research();

    purchRFQReplyTable_ds.reread();
    purchRFQReplyTable_ds.refresh();

    element.setButtonAccessHeader();
    element.setButtonAccessLine();
    element.setMandatoryAgreementOrderFields(purchRFQTable.rfqType == PurchRFQType::PurchAgreement);

    PurchRFQSealedBidFlightingManager::initSealedBiddingSpecificControl(element);
}

public void enableProjSalesPrice()
{
    boolean mustDefaultCostPriceAsSalesPrice;

    if (purchRFQLine.ProjId)
    {
        if (!purchRFQLine.ItemId && ProjParameters::find().DefaultCostPriceAsSalesPrice)
        {
            mustDefaultCostPriceAsSalesPrice = true;
        }

        if (ProjInvoiceTable::isSalesCurrencyLocked(ProjTable::find(purchRFQLine.ProjId).ProjInvoiceProjId))
        {
            purchRFQLine_ds.object(fieldNum(PurchRFQLine, ProjSalesCurrencyId)).allowEdit(false);
            purchRFQLine_ds.object(fieldNum(PurchRFQLine, ProjSalesCurrencyId)).skip(true);
        }
        else
        {
            purchRFQLine_ds.object(fieldNum(PurchRFQLine, ProjSalesCurrencyId)).allowEdit(!mustDefaultCostPriceAsSalesPrice);
            purchRFQLine_ds.object(fieldNum(PurchRFQLine, ProjSalesCurrencyId)).skip(mustDefaultCostPriceAsSalesPrice);
        }

        purchRFQLine_ds.object(fieldNum(PurchRFQLine, ProjSalesPrice)).allowEdit(!mustDefaultCostPriceAsSalesPrice);
        purchRFQLine_ds.object(fieldNum(PurchRFQLine, ProjSalesPrice)).skip(mustDefaultCostPriceAsSalesPrice);
    }
    else
    {
        purchRFQLine_ds.object(fieldNum(PurchRFQLine, ProjSalesCurrencyId)).allowEdit(false);
        purchRFQLine_ds.object(fieldNum(PurchRFQLine, ProjSalesCurrencyId)).skip(true);

        purchRFQLine_ds.object(fieldNum(PurchRFQLine, ProjSalesPrice)).allowEdit(false);
        purchRFQLine_ds.object(fieldNum(PurchRFQLine, ProjSalesPrice)).skip(true);
    }
}

public void enableServiceFields_BR()
{
    InventTable inventTable;
    boolean     enable = false;

    select firstonly ItemType from inventTable where inventTable.ItemId == purchRFQLine.ItemId;

    if (inventTable.ItemType == ItemType::Service)
    {
        enable = true;
    }

    purchRFQLine_ds.object(fieldNum(PurchRFQLine, TaxServiceCode_BR)).enabled(enable);

    purchRFQLine_ds.refresh();
}

/// <summary>
/// Determines if an alternate line exists for the current line and vendor.
/// </summary>
/// <param name="_purchRFQLine">
/// The <c>PurchRFQLine</c> record.
/// </param>
/// <returns>
/// true if an alternate line exists for the current line and vendor; otherwise, false.
/// </returns>
public boolean existAlternateForVendor(PurchRFQLine _purchRFQLine)
{
    container     key = [_purchRFQLine.RFQCaseLineRefRecId,_purchRFQLine.RFQId];
    boolean       alternateExist;

    if (key)
    {
        if (!mapAlternateExist)
        {
            mapAlternateExist = new Map(Types::Container, Types::Enum);
        }

        if (mapAlternateExist.exists(key))
        {
            alternateExist = mapAlternateExist.lookup(key);
        }
        else
        {
            alternateExist = _purchRFQLine.existAlternateForVendor();
            mapAlternateExist.insert(key, alternateExist);
        }
    }

    return alternateExist;
}

/// <summary>
/// Determines if the questionnaire exist for the current RFQ.
/// </summary>
/// <returns>
/// true if a questionnaire exists; otherwise, false.
/// </returns>
public boolean existQuestionnaire()
{
    return PurchRFQTableKMCollection::findByRFQAndCollection(purchRFQTable.rfqId, purchRFQTableKMCollection.kmCollectionId).RecId != 0;
}

/// <summary>
/// Determines if response exists for the questionnaire.
/// </summary>
/// <returns>
/// true if a response exists; otherwise, false.
/// </returns>
public boolean existQuestionnaireResponse()
{
    return PurchRFQTableQuestionnaireResponse::findByRFQKMCollection(purchRFQTableKMCollection.RecId).RecId != 0;
}

public Integer getRFQLinesMarkForDelete()
{
    PurchRFQLine        purchRFQLineLocal;
    PurchRFQReplyLine   purchRFQReplyLineLocal;

    // Get the number of records that are marked for delete having status < Received
    select count(RecId) from purchRFQReplyLineLocal
        join RecId from purchRFQLineLocal
            where purchRFQLineLocal.rfqId == purchRFQTable.rfqId &&
                  purchRFQReplyLineLocal.rfqLineRecId == purchRFQLineLocal.RecId &&
                  purchRFQReplyLineLocal.rfqId == purchRFQLineLocal.rfqId &&
                  purchRFQLineLocal.Status < PurchRFQStatus::Received &&
                  purchRFQReplyLineLocal.rfqMark == NoYes::Yes;

    return int642int(purchRFQReplyLineLocal.RecId);
}

public void init()
{
    PurchRFQCaseTable   caseTable;
    PurchRFQLine        rfqLine;
    PurchRFQTable       rfqTable;
    PurchRFQVendLink    rfqVendLink;

    // <GIN>
    #ISOCountryRegionCodes
    // </GIN>

    if (#PdsCatchWeightEnabled)
    {
        formCtrl = PdsCWFormCtrl_PurchRFQReplyTable::newFromForm(element);
        formCtrl.initPre();
    }

    super();

    isProductSearchLookupEnabled = EcoResProductSearchLookup::isProductSearchLookupEnabled();

    if (formCtrl)
    {
        formCtrl.initPost();
    }

    element.updateDesign(InventDimFormDesignUpdate::Init);
    gridLine.visibleColsValue(13+conLen(inventDimFormSetup.dimFieldsActive(inventDimFormSetup.parmDimParmVisibleGrid())));

    switch (element.args().dataset())
    {
        case tableNum(PurchRFQCaseTable):
            caseTable = element.args().record();
            rfqCaseIdSelection = caseTable.rfqCaseId;
            element.design().controlName(formControlStr(PurchRFQReplyTable, NavigationList)).visible(true);
            break;

        case tableNum(PurchRFQVendLink):
            rfqVendLink = element.args().record();
            rfqCaseIdSelection = rfqVendLink.rfqCaseId;
            rfqVendIdSelection = rfqVendLink.VendAccount;
            break;

        case tableNum(PurchRFQLine):
            rfqLine = element.args().record();
            rfqIdSelection = rfqLine.rfqId;
            break;

        case tableNum(PurchRFQTable):
            rfqTable = element.args().record();
            rfqIdSelection = rfqTable.rfqId;
            tabHeader.tabValue(3);  // Go to the General tab
            break;

        case tableNum(AgreementLine):
            rfqIdSelection = PurchRFQReplyLinePurchAgreementLine::findByPurchAgreementLine(element.args().record().RecId).PurchRFQReplyLineRFQId;
            break;
        case tableNum(PurchRequestForQuotationReplyHeaderStaging):
            PurchRequestForQuotationReplyHeaderStaging header = element.args().record() as PurchRequestForQuotationReplyHeaderStaging;
            rfqIdSelection = header.RFQNumber;
            break;
        case tableNum(PurchRequestForQuotationReplyLineStaging):
            PurchRequestForQuotationReplyLineStaging line = element.args().record() as PurchRequestForQuotationReplyLineStaging;
            rfqIdSelection = line.RFQNumber;
            break;
        case tableNum(PurchRequestForQuotationReplyQuestionnaireStaging):
            PurchRequestForQuotationReplyQuestionnaireStaging questionnaire = element.args().record() as PurchRequestForQuotationReplyQuestionnaireStaging;
            rfqIdSelection = questionnaire.RFQNumber;
            break;
        case tableNum(PurchRequestForQuotationReplyScoringCriterionStaging):
            PurchRequestForQuotationReplyScoringCriterionStaging scoringCriterion = element.args().record() as PurchRequestForQuotationReplyScoringCriterionStaging;
            rfqIdSelection = scoringCriterion.RFQNumber;
            break;
    }

    // Needed to check validity of address
    logisticsPostalAddressHeader_ds.addFieldToSelectionList(fieldNum(LogisticsPostalAddress, ValidFrom));
    logisticsPostalAddressHeader_ds.addFieldToSelectionList(fieldNum(LogisticsPostalAddress, ValidTo));
    logisticsPostalAddressHeader_ds.addFieldToSelectionList(fieldNum(LogisticsPostalAddress, CountryRegionId));
    logisticsPostalAddressLine_ds.addFieldToSelectionList(fieldNum(LogisticsPostalAddress, ValidFrom));
    logisticsPostalAddressLine_ds.addFieldToSelectionList(fieldNum(LogisticsPostalAddress, ValidTo));
    logisticsPostalAddressLine_ds.addFieldToSelectionList(fieldNum(LogisticsPostalAddress, CountryRegionId));

    // <GIN>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN])
        && (!TaxParameters::find().SalesTax_IN
        || !TaxParameters::find().vat_in
        || !TaxParameters::find().ServiceTax_IN
        || !TaxParameters::find().Excise_IN
        || !TaxParameters::find().Customs_IN))
    {
        element.setVisible_IN();
    }
    // </GIN>
    // <GBR>
    if (BrazilParameters::isEnabled())
    {
        salesPurchOperationTypeController_BR = new SalesPurchOperationTypeController_BR(this.name());
    }
    // </GBR>

    if (TaxSolutionScopeIntegrationUtil::isCompanyEnabled())
    {
        TaxDocumentLauncher.visible(true);
        TransTaxInformation.visible(true);
        Line_SalesTax_TaxItemGroup.visible(false);
        Line_SalesTax_TaxGroup.visible(false);
        ProjectSalesTax_ProjTaxGroupId.visible(false);
        ProjectSalesTax_ProjTaxItemGroupId.visible(false);
    }

    if (Tax::isTaxIntegrationEnabledForBusinessProcess(TaxIntegrationBusinessProcess::Purchase))
    {
        PurchRFQLine_ds.object(fieldNum(PurchRFQLine, OverrideSalesTax)).visible(true);
        PurchRFQTable_ds.object(fieldNum(PurchRFQTable, OverrideSalesTax)).visible(true);
        TaxVATNumTable::enableLookupVATNum(SalesTax_VATNum);
    }
    else
    {
        PurchRFQLine_ds.object(fieldNum(PurchRFQLine, OverrideSalesTax)).visible(false);
        PurchRFQTable_ds.object(fieldNum(PurchRFQTable, OverrideSalesTax)).visible(false);
        TaxVATNumTable::disableLookupVATNum(SalesTax_VATNum);
    }
}

