/// <summary>
/// Updates product dimensions assuming item has already been set on the line.
/// </summary>
/// <param name="_inventDim">
/// An instance of <c>InventDim</c> that provides the new product dimensions.
/// </param>
public void updateProductDimensions(InventDim _inventDim)
{
    Set productDimensionFields = inventDim.copyProductDimensionsAndGetModifiedDimensionFields(_inventDim, purchRFQLine.ItemId);
    if (productDimensionFields)
    {
        this.modifyInventDimSet(_inventDim, productDimensionFields);
    }
}

/// <summary>
/// Updates the inventory dimension field.
/// </summary>
/// <param name = "_fieldId">Field Id that is being updated.</param>
public void modifyInventDimField(FieldId _fieldId)
{
    if (purchRFQLine.PurchRFQLineMap::modifyInventDimField(inventDim, _fieldId))
    {
        InventDim.data(purchRFQLine.inventDim());
        InventDim_DS.setCurrent();

        if (TaxIntegrationUtils::isTaxInformationEnabled())
        {
            InventDim inventDimLoc = purchRFQLine.InventDim();
            LogisticsLocationRecId logisticsLocationRecId
                = TaxLogisticsLocationControllerHelper_IN::getDefaultLogisticsLocation(inventDimLoc.InventLocationId, inventDimLoc.InventSiteId);

            TransTaxInformationHelper::newHelper().updateCompanyLocationFromTransTable(purchRFQLine, logisticsLocationRecId);
        }
    }
}

/// <summary>
/// Updates <c>InventDim</c> data based on the given set of modified dimension fields.
/// </summary>
/// <param name = "_inventDim">The new inventory dimension record.</param>
/// <param name = "_dimFields">A set containing the ID's of the modified <c>InventDim</c> fields.</param>
public void modifyInventDimSet(InventDim _inventDim, Set _dimFields)
{
    if (purchRFQLine.PurchRFQLineMap::modifyInventDimSet(_inventDim, _dimFields))
    {
        InventDim.data(purchRFQLine.inventDim());
    }
}

Object inventDimSetupObject()
{
    return inventDimFormSetup;
}

public LogisticsLocationRecId parmPostalAddressLocation(LogisticsLocationRecId _postalAddressLocation = 0)
{
    if (!_postalAddressLocation)
    {
        // Set location by active datasource
        if (element.dataSource().name() == purchRFQLine_ds.name())
        {
            _postalAddressLocation = purchRFQLine.deliveryLocation();
        }
        else
        {
            _postalAddressLocation = purchRFQTable.deliveryLocation();
        }
    }

    postalAddressLocation = _postalAddressLocation;
    return postalAddressLocation;
}

public ProjInventJournalTransMapForm projInventJournalTransMapForm()
{
    if (!projInventJournalTransMapForm)
    {
        projInventJournalTransMapForm = ProjInventJournalTransMapForm::construct(purchRFQLine);
    }
    else
    {
        projInventJournalTransMapForm.parmProjInventJournalTransMap(purchRFQLine);
    }

    return projInventJournalTransMapForm;
}

