void setAddressEditable()
{
    boolean editable = purchRFQTable.StatusLow == PurchRFQStatus::Sent || purchRFQTable.StatusLow == PurchRFQStatus::Received;

    purchRFQTable_ds.object(fieldNum(PurchRFQTable, DeliveryPostalAddress)).allowEdit(editable);
    purchRFQLine_ds.object(fieldNum(PurchRFQLine, DeliveryPostalAddress)).allowEdit(editable);

    DirPartyPostalAddressNewHeader.enabled(editable);
    DirPartyPostalAddressNewLine.enabled(editable);
}

void setButtonAccessHeader()
{
    boolean gridIsNotEmpty                  = purchRFQTable.RecId;
    boolean isNotSealed                     = purchRFQCaseTable.IsSealed == NoYes::No;
    boolean isRFQCaseAcceptRejectEnabled    = gridIsNotEmpty && purchRFQCaseTable.isAcceptRejectAllowed();
    boolean isRFQReplyReceived              = (purchRFQTable.StatusLow == PurchRFQStatus::Received) || (purchRFQTable.StatusHigh == PurchRFQStatus::Received);

    purchRFQReplyEncryptionAuditLog.visible(purchRFQCaseTable.BidType == PurchRFQBidType::Sealed);

    // Header button enablement
    returnMenuItemButton.enabled(gridIsNotEmpty && isRFQReplyReceived);

    acceptMenuItemButton.enabled(isRFQCaseAcceptRejectEnabled && isRFQReplyReceived);

    rejectMenuItemButton.enabled(isRFQCaseAcceptRejectEnabled && isRFQReplyReceived);

    // Buttons for linked documents
    agreements.enabled(gridIsNotEmpty && purchRFQTable.AgreementId);
    poMenuItemButton.enabled(gridIsNotEmpty && purchRFQTable.PurchId);

    // Questionnaire
    kmKnowledgeCollectorAnswerReportForm.enabled(this.existQuestionnaireResponse());

    // Edit buttons
    element.enableDisableEditButtons();

    PurchRFQSealedBidFlightingManager::initSealedBiddingSpecificControl(element);
}

void setButtonAccessLine()
{
    boolean isLineStocked = purchRFQLine.isStocked();
    boolean isRFQSealed = purchRFQCaseTable.IsSealed;
    boolean isSentAndActive = purchRFQLine.Status == PurchRFQStatus::Sent && !purchRFQCaseTable.isRFQExpiredForCurrentUser();

    buttonPurchTable.enabled(purchRFQLine.PurchId ? true : false);
    buttonAgreement.enabled(purchRfqLine.AgreementId ? true : false);

    buttonInventTrans.enabled(isLineStocked 
        && (purchRFQTable.rfqType == PurchRFQType::Purch || purchRFQTable.rfqType == PurchRFQType::PurchReq));
    ButtonInventOnhand.enabled(isLineStocked);
    ButtonInventDimTracking.enabled(isLineStocked);
    ButtonInventLot.enabled(isLineStocked);

    newLine.enabled(!isRFQSealed 
        && (purchRFQTable.rfqType == PurchRFQType::Purch || purchRFQTable.rfqType == PurchRFQType::PurchAgreement));
    deleteLine.enabled(!isRFQSealed && purchRFQLine.isAddedByProcurementAgent());
    menuButtonSetupLine.visible(!isRFQSealed);
    menuButtonInquiriesLine.visible(!isRFQSealed);
    menuButtonInvent.visible(!isRFQSealed);

}

/// <summary>
/// Set access levels to datasources and fields.
/// </summary>
void setFieldsAccess()
{
    purchRFQLine_ds.object(fieldNum(PurchRFQLine, CategoryId)).enabled(purchRFQCaseTable.IsSealed != NoYes::Yes);

    if (purchRFQCaseTable.isRFQCanceled())
    {
        this.setFieldsAccessForCanceledRFQ();
    }
}

private void setFieldsAccessForCanceledRFQ()
{
    purchRFQTable_ds.allowCreate(false);
    purchRFQTable_ds.allowEdit(false);
    purchRFQTable_ds.allowDelete(false);

    inventDim_ds.allowCreate(false);
    inventDim_ds.allowEdit(false);
    inventDim_ds.allowDelete(false);

    purchRFQLine_ds.allowCreate(false);
    purchRFQLine_ds.allowCreate(false);
    purchRFQLine_ds.allowDelete(false);

    purchRFQCaseTable_ds.allowCreate(false);
    purchRFQCaseTable_ds.allowEdit(false);
    purchRFQCaseTable_ds.allowDelete(false);
}

void setHeaderEditable()
{
    purchRFQTable_ds.allowEdit(purchRFQTable.StatusHigh < PurchRFQStatus::Rejected);
}

void setItemType(PurchRFQLineItemType _itemType, boolean _isActivate = true)
{
    InventDim           invDim;

    if (purchRFQLine.Status == PurchRFQStatus::Created ||
        purchRFQLine.Status == PurchRFQStatus::Sent ||
        purchRFQLine.Status == PurchRFQStatus::Received)
    {
        if (_itemType == PurchRFQLineItemType::Item)
        {
            purchRFQLine_ds.object(fieldNum(PurchRFQLine, LineType)).allowEdit(!_isActivate);
            purchRFQLine_ds.object(fieldNum(PurchRFQLine, LineType)).skip(_isActivate);
            purchRFQLine_ds.object(fieldNum(PurchRFQLine, ItemId)).allowEdit(!_isActivate);
            purchRFQLine_ds.object(fieldNum(PurchRFQLine, ItemId)).skip(_isActivate);
            purchRFQLine_ds.object(fieldNum(PurchRFQLine, CategoryId)).allowEdit(false);
            purchRFQLine_ds.object(fieldNum(PurchRFQLine, CategoryId)).skip(true);
            purchRFQLine_ds.object(fieldNum(PurchRFQLine, ItemName)).allowEdit(false);
            purchRFQLine_ds.object(fieldNum(PurchRFQLine, ItemName)).skip(true);
            purchRFQLine_ds.object(fieldNum(PurchRFQLine, ItemId)).mandatory(true);
            purchRFQLine_ds.object(fieldNum(PurchRFQLine, CategoryId)).mandatory(false);
            purchRFQLine_ds.object(fieldNum(PurchRFQLine, ItemName)).mandatory(false);
        }
        else
        {
            purchRFQLine_ds.object(fieldNum(PurchRFQLine, LineType)).allowEdit(true);
            purchRFQLine_ds.object(fieldNum(PurchRFQLine, LineType)).skip(false);
            purchRFQLine_ds.object(fieldNum(PurchRFQLine, CategoryId)).allowEdit(true);
            purchRFQLine_ds.object(fieldNum(PurchRFQLine, CategoryId)).skip(false);
            purchRFQLine_ds.object(fieldNum(PurchRFQLine, ItemId)).allowEdit(false);
            purchRFQLine_ds.object(fieldNum(PurchRFQLine, ItemId)).skip(true);
            purchRFQLine_ds.object(fieldNum(PurchRFQLine, ItemName)).allowEdit(true);
            purchRFQLine_ds.object(fieldNum(PurchRFQLine, ItemName)).skip(false);
            purchRFQLine_ds.object(fieldNum(PurchRFQLine, CategoryId)).mandatory(true);
            purchRFQLine_ds.object(fieldNum(PurchRFQLine, ItemId)).mandatory(false);
            purchRFQLine_ds.object(fieldNum(PurchRFQLine, ItemName)).mandatory(true);
            purchRFQLine.ItemId = '';
        }

        invDim = purchRFQLine.inventDim();
        inventDim.InventSiteId      = invDim.InventSiteId;
        inventDim.InventLocationId  = invDim.InventLocationId;
        purchRFQLine_ds.refresh();
    }
    else
    {
        purchRFQLine_ds.object(fieldNum(PurchRFQLine, LineType)).allowEdit(false);
        purchRFQLine_ds.object(fieldNum(PurchRFQLine, LineType)).skip(true);
        purchRFQLine_ds.object(fieldNum(PurchRFQLine, CategoryId)).allowEdit(false);
        purchRFQLine_ds.object(fieldNum(PurchRFQLine, CategoryId)).skip(true);
        purchRFQLine_ds.object(fieldNum(PurchRFQLine, ItemId)).allowEdit(false);
        purchRFQLine_ds.object(fieldNum(PurchRFQLine, ItemId)).skip(true);
        purchRFQLine_ds.object(fieldNum(PurchRFQLine, ItemName)).allowEdit(false);
        purchRFQLine_ds.object(fieldNum(PurchRFQLine, ItemName)).skip(true);
        purchRFQLine_ds.object(fieldNum(PurchRFQLine, ItemId)).mandatory(false);
        purchRFQLine_ds.object(fieldNum(PurchRFQLine, ItemName)).mandatory(false);
    }

    // <GBR>
    if (BrazilParameters::isEnabled())
    {
        purchRFQLine_ds.object(fieldNum(PurchRFQLine, LineType)).allowEdit(false);
    }
    // </GBR>
}

void  setMandatoryAgreementOrderFields(boolean _mandatory)
{
    rfqTable_BPO.visible(_mandatory);
    rfqReplyTable_BPO.visible(_mandatory);
}

private void setVisible_IN()
{
    TaxParameters       taxParametersLoc = TaxParameters::find();
    boolean             marked           = TaxParameters::checkTaxParameters_IN();
    priceBasis_IN.visible(marked);
}

[Hookable(false), Wrappable(false)]
internal void showHeaderLogisticsPostalEffectiveAddressMessage()
{
    // Keep the message appearing on the order details pages (not on the list) only with lowest status either Sent or Received
    if (element.viewOptionHelper().getViewOption() != FormViewOption::Grid
        && (purchRFQTable.StatusLow == PurchRFQStatus::Sent || purchRFQTable.StatusLow == PurchRFQStatus::Received)
        && (logisticsPostalAddressHeader.RecId && !logisticsPostalAddressHeader.isEffective()))
    {
        warning("@ProcurementAndSourcing:HeaderEffectiveAddressMessage");
    }
}

[Hookable(false), Wrappable(false)]
internal void showLineLogisticsPostalEffectiveAddressMessage()
{
    // Keep the message appearing on the order details pages (not on the list) only with status either Sent or Received
    if (element.viewOptionHelper().getViewOption() == FormViewOption::Details
        && (purchRFQLine.RecId && (purchRFQLine.Status == PurchRFQStatus::Sent || purchRFQLine.Status == PurchRFQStatus::Received))
        && (logisticsPostalAddressLine.RecId && !logisticsPostalAddressLine.isEffective()))
    {
        warning("@ProcurementAndSourcing:LineEffectiveAddressMessage");
    }
}

void updateDesign(InventDimFormDesignUpdate mode)
{
    InventMovement      movement = InventMovement::construct(purchRFQLine);

    switch (mode)
    {
        case InventDimFormDesignUpdate::Init        :
            if (!inventDimFormSetup)
                inventDimFormSetup  = InventDimCtrl_Frm_Mov_PurchRFQ::newFromFormMovement(element, movement);

        case InventDimFormDesignUpdate::Active      :
            inventDimFormSetup.formActiveSetup(movement.inventDimGroupSetup(),movement);
            inventDimFormSetup.formSetControls(true);
            break;

        case InventDimFormDesignUpdate::FieldChange :
            inventDimFormSetup.formActiveSetup(movement.inventDimGroupSetup(),movement);
            inventDimFormSetup.formSetControls(false);
            break;

        default : throw error(strFmt("@SYS54195",funcName()));
    }

    purchRFQLine_IsAlternate.visible(purchRFQCaseTable.AllowAlternates == NoYes::Yes);
    HeaderQuestionnaire.visible(purchRFQCaseTable.IsSealed != NoYes::Yes);

    // Static text showing at top of first tab when RFQ is sealed
    RFQSealedMessageLineView.visible(purchRFQCaseTable.IsSealed);
    RFQSealedMessageHeaderView.visible(purchRFQCaseTable.IsSealed);

    // Sets the configuration for the system defined Edit button and custom Edit buttons.
    element.setEditConfiguration();
    element.enableDisableButtonsForMaintainingReplyLine();
}

/// <summary>
/// Enables or disables Add and Remove buttons for maintaining reply line.
/// </summary>
private void enableDisableButtonsForMaintainingReplyLine()
{
    boolean isAddedByProcurementAgent = purchRFQline.isAddedByProcurementAgent();

    NewLine.enabled(!purchRFQReplyTable.isDeclined());
    DeleteLine.enabled(!purchRFQReplyTable.isDeclined() && isAddedByProcurementAgent);

    element.enableDisableReplyLineDataSource(isAddedByProcurementAgent);
}

/// <summary>
/// Enables or disables RFQ reply line data source.
/// </summary>
/// <param name = "_isEditable">
/// The flag for data source.
/// </param>
private void enableDisableReplyLineDataSource(boolean _isEditable)
{
    PurchRFQReplyline_ds.allowEdit(_isEditable);
    PurchRFQReplyline_ds.allowCreate(_isEditable);
    PurchRFQReplyline_ds.allowDelete(_isEditable);
}

public int task(int _taskId)
{
    #Task
    int taskResult;

    switch (_taskId)
    {
        case #taskAttachments :
            taskResult = super(_taskId);
            break;

        case #taskEditRecord :
        case #taskDoneEditRecord :
            // Hide system defined edit button otherwise two edit buttons will be displayed.
            taskResult = super(_taskId);
            element.hideSystemDefinedEditButton();
            break;

        default :
            taskResult = super(_taskId);
    }

    return taskResult;
}

private void setEditConfiguration()
{
    element.hideSystemDefinedEditButton();
    element.enableDisableEditButtons();
}

/// <summary>
/// Hide the system defined edit button.
/// </summary>
private void hideSystemDefinedEditButton()
{
    #SysSystemDefinedButtons

    FormCommandButtonControl sytemDefinedEditButton = this.control(this.controlId(#SystemDefinedViewEditButton)) as FormCommandButtonControl;

    if (sytemDefinedEditButton)
    {
        sytemDefinedEditButton.visible(false);
    }
}

private void enableDisableEditButtons()
{
    EditRFQ.enabled(purchRFQTable.RecId && element.viewEditModeHelper().isInViewMode());
    EditRFQReply.enabled(purchRFQTable.RecId
        && (purchRFQReplyTable.ReplyProgressStatus == PurchRFQReplyProgressStatus::ProcurementAgentEditing
            || purchRFQReplyTable.isEditByProcurementAgentAllowed())
        && purchRFQTable.StatusHigh != PurchRFQStatus::Declined
        && purchRFQTable_ds.hasAnyLineToEdit()
        && purchRFQCaseTable.IsSealed == NoYes::No);
}

public boolean validateWrite()
{
    boolean ret;

    ret = super();

    if (ret)
    {
        purchRFQTable2LineUpdatePrompt = PurchRFQTableMap2LineUpdatePrompt::construct();
        purchRFQTable2LineUpdatePrompt.purchRFQTableMap(purchRFQReplyTable); //workaround to avoid a buffer clearance by the next call
        purchRFQTable2LineUpdatePrompt.purchRFQTableMap(purchRFQTable);
        if (!purchRFQTable2LineUpdatePrompt.prompt())
            ret = false;
    }

    return ret;
}

public void delete()
{
    super();

    purchRFQTable_ds.reread();
    purchRFQLine_ds.research();

    element.setButtonAccessHeader();
    element.setButtonAccessLine();
    element.setMandatoryAgreementOrderFields(purchRFQTable.rfqType == PurchRFQType::PurchAgreement);
}

void  write()
{
    DialogButton        copyAddress     = DialogButton::No;
    PurchRFQCaseTable   caseTable;

    if (purchRFQTable.checkUpdate())
    {
        if (PurchRFQLine::exist(purchRFQTable.rfqId))
        {
            // <GBR>
            if (BrazilParameters::isEnabled())
            {
                if (purchRFQTable2LineUpdatePrompt.fieldsToUpdate()
                    && purchRFQTable.InventSiteId != purchRFQTable.orig().InventSiteId)
                {
                    copyAddress = DialogButton::Yes;
                }
            }
            else
            {
                // </GBR>
                if (purchRFQTable.DeliveryPostalAddress != purchRFQTable.orig().DeliveryPostalAddress)
                    copyAddress = PurchRFQCaseTable::promptCopyDeliveryAddress();
                // <GBR>
            }
            // </GBR>
        }

        ttsbegin;

        if (copyAddress != DialogButton::Cancel  &&  copyAddress != DialogButton::No)
            purchRFQTable.copyAddressToLine(copyAddress, purchRFQTable);

        super();

        if (purchRFQTable2LineUpdatePrompt)
        {
            purchRFQTable2LineUpdatePrompt.runOperation();
            purchRFQTable2LineUpdatePrompt = null;
        }

        //update RFQ Case status if RFQTable high status is higher.
        caseTable = PurchRFQCaseTable::find(purchRFQTable.rfqCaseId, true);
        if (purchRFQTable.StatusHigh > caseTable.StatusHigh)
        {
            caseTable.updateStatus();
        }

        ttscommit;
    }
    purchRFQTable_ds.reread();
    purchRFQTable_ds.refresh();
    element.setButtonAccessHeader();
    element.setMandatoryAgreementOrderFields(purchRFQTable.rfqType == PurchRFQType::PurchAgreement);
}

//Posting routine needs a boolean parameter to be in this method. See the similar method in PurchRFQCaseTable form.
void  reReadLines(boolean _refreshVendLink = false)
{
    element.setButtonAccessHeader();
    element.setHeaderEditable();
    element.setAddressEditable();
    purchRFQLine_ds.executeQuery();
    element.setMandatoryAgreementOrderFields(purchRFQTable.rfqType == PurchRFQType::PurchAgreement);
}

public int active()
{
    int ret;

    element.setButtonAccessHeader();
    element.setHeaderEditable();
    element.setAddressEditable();
    element.setMandatoryAgreementOrderFields(purchRFQTable.rfqType == PurchRFQType::PurchAgreement);
    element.showHeaderLogisticsPostalEffectiveAddressMessage();

    ret = super();

    return ret;
}

public void executeQuery()
{
    QueryBuildDataSource qbds;
    qbds = this.query().dataSourceTable(tableNum(PurchRFQTable));
    qbds.addSortField(fieldNum(PurchRFQTable, rfqId), SortOrder::Descending);

    if (purchRFQTable_ds.query().dataSourceTable(tableNum(PurchRFQTable)).findRange(fieldNum(PurchRFQTable,rfqCaseId)))
    {
        rangeRFQCaseId.value(rfqCaseIdSelection ? queryValue(rfqCaseIdSelection) : SysQuery::valueUnlimited());
    }

    if (purchRFQTable_ds.query().dataSourceTable(tableNum(PurchRFQTable)).findRange(fieldNum(PurchRFQTable, rfqId)))
    {
        rangeRFQId.value(rfqIdSelection ? queryValue(rfqIdSelection) : SysQuery::valueUnlimited());
    }

    if (purchRFQTable_ds.query().dataSourceTable(tableNum(PurchRFQTable)).findRange(fieldNum(PurchRFQTable, VendAccount)))
    {
        rangeRFQVendId.value(rfqVendIdSelection ? queryValue(rfqVendIdSelection) : SysQuery::valueUnlimited());
    }

    logisticsPostalAddressHeader_ds.validTimeStateUpdate(ValidTimeStateUpdate::Correction);
    logisticsPostalAddressHeader_ds.query().validTimeStateDateTimeRange(DateTimeUtil::minValue(), DateTimeUtil::maxValue());

    super();

    element.setMandatoryAgreementOrderFields(purchRFQTable.rfqType == PurchRFQType::PurchAgreement);
}

public void init()
{
    QueryBuildDataSource    qbds;

    super();

    // Accessing query build data source...
    qbds = this.query().dataSourceTable(tableNum(PurchRFQTable));

    qbds.clearDynalinks();

    rangeRFQCaseId      = qbds.addRange(fieldNum(PurchRFQTable, rfqCaseId));
    rangeRFQId          = qbds.addRange(fieldNum(PurchRFQTable, rfqId));
    rangeRFQVendId      = qbds.addRange(fieldNum(PurchRFQTable, VendAccount));

    // Enable caching of the document handling and note it display fields
    purchRFQTable_ds.cacheAddMethod(tableMethodStr(PurchRFQTable, showDocHandIcon));
    // <GBR>
    purchRFQTable_ds.object(fieldNum(PurchRFQTable, InclTax)).allowEdit(!BrazilParameters::isEnabled());
    // </GBR>
}

public TitleFields parentTitleFields(Common _record)
{
    // Avoid showing title fields from another form that is opening this form
    return "";
}

/// <summary>
/// Gets or sets Rank value on the <c>PurchRFQReplyTable</c> record.
/// </summary>
/// <param name="_set">
/// Indicates whether to update.
/// </param>
/// <param name="_rank">
/// The RFQ reply rank value.
/// </param>
/// <returns>
/// rank value
/// </returns>
[SysClientCacheDataMethodAttribute(true)]
public edit PurchRFQRank requestForQuoteRank(boolean _set, PurchRFQRank _rank)
{
    PurchRFQReplyTable replyTable = PurchRFQReplyTable::find(purchRFQTable.RFQId, _set);

    if (_set)
    {
        ttsbegin;
        replyTable.Rank = _rank;
        replyTable.update();
        ttscommit;
    }

    return replyTable.Rank;
}

/// <summary>
/// Checks if there is any line to edit for the procurement agent.
/// </summary>
[SysClientCacheDataMethodAttribute(true)]
display boolean hasAnyLineToEdit()
{
    PurchRFQLine purchRFQlineLocal;

    select firstonly RecId from purchRFQlineLocal
        where purchRFQlineLocal.RFQId                == purchRFQTable.RFQId
            && purchRFQlineLocal.Status              <= PurchRFQStatus::Received
            && purchRFQlineLocal.RFQCaseLineRefRecId > 0;

    return purchRFQlineLocal.RecId;
}

public boolean validate()
{
    boolean ret;

    ret = super();

    // <GBR>
    ret = ret && element.canChangeDeliveryPostalAddress_BR();

    if (BrazilParameters::isEnabled())
    {
        ret = ret
            && CFOPValidation_BR::validateDeliveryAddress(
                InventSite::find(purchRFQTable.InventSiteId).fiscalEstablishment(),
                purchRFQTable.DeliveryPostalAddress);
    }
    // </GBR>
    return ret;
}

