    }
    // </GBR>
}

public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    Common ret;
    CompanyInfo companyInfo = CompanyInfo::find();
    Common targetBuffer = null;

    if (element.canChangeDeliveryPostalAddress_BR())
    {
        targetBuffer = purchRFQTable;
    }

    ret = LogisticsLocationSelectionLookup::lookupAddressForm(_formReferenceControl,
        companyInfo,
        [LogisticsLocationRoleType::Delivery],
        false,
        LogisticsLocationSelectionLookup::transactionAssociatedAddresses(
                                              tableNum(PurchRFQLine),
                                              fieldNum(PurchRFQLine, DeliveryPostalAddress),
                                              fieldNum(PurchRFQLine, rfqId),
                                              purchRFQTable.rfqId,
                                              purchRFQTable.DeliveryPostalAddress),
        targetBuffer);
    return ret;
}

